css:
@-moz-document domain('zmunjali.wordpress.com') {
    /*[[themecolor]]*/
  
    #masthead{
        margin-bottom: 5px;
    }
    #masthead .site-branding > a, #masthead .site-branding > h2.site-description {
        display: none;
        opacity: 0;
    }
	#content, #primary {
		width: 100%;
	}
  	@media (min-width: 910px){
        #content {
            width: 880px !important;
        }
  	}
    #main > article .entry-content p{
        margin: 0;
        font-size: /*[[fontsize]]*/;
    }
	.entry-header, .entry-content, .entry-footer, .entry-summary, .page-header, .page-content {
    padding: 0 55px !important;
}

    body #secondary{
        width: 100%;
        text-align: center;
        margin-left:0;
    }
}
