@-moz-document url-prefix("https://dou.ua/") {
.b-comments #commentsList {
    /*Relative because vertical lines for all levels will be positioned against it*/
    position: relative;
    padding-left: 19px;
}

.b-comments #commentsList .b-comment.level-0, .b-comments #commentsList .b-comment.level-1, .b-comments #commentsList .b-comment.level-2, .b-comments #commentsList .b-comment.level-3,
.b-comments #commentsList .b-comment.level-4, .b-comments #commentsList .b-comment.level-5, .b-comments #commentsList .b-comment.level-6, .b-comments #commentsList .b-comment.level-7,
.b-comments #commentsList .b-comment.level-8, .b-comments #commentsList .b-comment.level-9, .b-comments #commentsList .b-comment.level-10, .b-comments #commentsList .b-comment.level-11
{
    /*Static because see above*/
    position: static;
}

.b-comments #commentsList .b-comment .scroll-wrapper {
    /*Fix J and K*/
    top: auto;
    transform: translateY(-55px);
}

.b-comments #commentsList .b-comment .comment,
.b-comments #commentsList .b-comment .scroll-wrapper:target+.comment {
    /*All this to mask lines that are going throug all comments*/
    background: #fff;
    padding-left: 0;
    margin-left: 32px;
    margin-bottom: 0;
    padding-bottom: 20px;
    z-index: 12;
}

.b-comments #commentsList .b-comment #floatForm {
    /*Masks lines when editing comment*/
    background: #fff;
    z-index: 12;
    position: relative;
}

.b-comments #commentsList .thread-comments {
    /*Mask lines below "expand thread" link*/
    background: #fff;
    z-index: 12;
    margin-left: 82px;
    padding-left: 0;
}

.b-comments #commentsList .b-comment .scroll-wrapper:target+.comment {
    /*"focused" comment when user uses j and k needs special attention*/
    margin-right: 0;
    outline: none !important;
    background: #fafafa;
    padding-left: 5px;
    padding-right: 5px;
}

.b-comments #commentsList .b-comment .scroll-wrapper:target+.comment:before {
    content: '';
    display: block;
    position: absolute;
    height: 5px;
    width: 100%;
    background: #fff;
    top: -5px;
    left: 0;
}

.b-comments #commentsList .b-comment .scroll-wrapper:target+.comment:after {
    content: '';
    display: block;
    position: absolute;
    height: 5px;
    width: 100%;
    background: #fff;
    top: 100%;
    left: 0;
}

.b-comments #commentsList .b-comment .scroll-wrapper:target+.comment  .b-post-author .avatar img {
    /*"focused" comment when user uses j and k needs special attention*/
    transform: translateX(-5px);
}

.b-comments #commentsList .b-comment .g-new-comment .comment-link {
    /*"focused" comment when user uses j and k needs special attention*/
    transform: translateX(5px);
}

.b-comments #commentsList .b-comment .comment .b-post-author .avatar img {
    /*Avaras has opacity 0.3 by default, need to set 1 to mask lines*/
    opacity: 1;
}

.b-comments #commentsList .b-comment.level-0:before, .b-comments #commentsList .b-comment.level-1:before, .b-comments #commentsList .b-comment.level-2:before,
.b-comments #commentsList .b-comment.level-3:before, .b-comments #commentsList .b-comment.level-4:before, .b-comments #commentsList .b-comment.level-5:before,
.b-comments #commentsList .b-comment.level-6:before, .b-comments #commentsList .b-comment.level-7:before, .b-comments #commentsList .b-comment.level-8:before,
.b-comments #commentsList .b-comment.level-9:before, .b-comments #commentsList .b-comment.level-10:before, .b-comments #commentsList .b-comment.level-11:before
{
    /*Vertical lines*/
    content: '';
    display: block;
    width: 1px;
    position: absolute;
    height: calc(100% - 20px);
    top: 0;
    /*border-left: 2px solid;*/
    border-left: /*[[level0Style]]*/;
    /*border-color: #0CF500;*/
    border-color: /*[[level0Color]]*/;
    transform: translateX(1.2em);
}

.b-comments #commentsList .b-comment.level-0:before {
    /*Level 0 is a bit different*/
 	transform: none;
}

.b-comments #commentsList .b-comment.level-0 ~ .b-comment.level-0:before,
.b-comments #commentsList .b-comment.level-1 ~ .b-comment.level-1:before
.b-comments #commentsList .b-comment.level-2 ~ .b-comment.level-2:before
.b-comments #commentsList .b-comment.level-3 ~ .b-comment.level-3:before
.b-comments #commentsList .b-comment.level-4 ~ .b-comment.level-4:before
.b-comments #commentsList .b-comment.level-5 ~ .b-comment.level-5:before
.b-comments #commentsList .b-comment.level-6 ~ .b-comment.level-6:before
.b-comments #commentsList .b-comment.level-7 ~ .b-comment.level-7:before
.b-comments #commentsList .b-comment.level-8 ~ .b-comment.level-8:before
.b-comments #commentsList .b-comment.level-9 ~ .b-comment.level-9:before
.b-comments #commentsList .b-comment.level-10 ~ .b-comment.level-10:before {
    /*We need only one line per comment level*/
    content: none;
    display: none;
}

/*Below margins and colors for each level*/

.b-comments #commentsList .b-comment.level-1 {
	margin-left: 3em;
}

.b-comments #commentsList .b-comment.level-1:before {
    /*border-left: 2px solid;*/
    border-left: /*[[level1Style]]*/;
    /*border-color: #0BE000;*/
