css:
@-moz-document regexp("https?:\\/\\/www\\.google(\\.[a-z]{2,3})?\\.[a-z]{2,3}\\/search.*") {
/* make searchform align center */
.srp #searchform {
    display: flex;
    justify-content: center
}
#sej-container {
    display: flex;
    justify-content: center;
    margin: 0 113px;
}
#gb {
    order: 1;
}
#hdtb-msb {
    justify-content: center;
    width: 98%
}
#hdtb-msb .hdtb-mitem:first-child.hdtb-msel {
    margin: 0
}
.ab_tnav_wrp {
    margin: 0
}
#extabar {
    text-align: center
}
#resultStats {
    position: unset
}
/* search result align center */
.big .mw {
    max-width: 1280px;
    margin: auto
}
#rcnt > .col {
    width: 100% !important;
}
#rhscol {
    display:none
}
/*multi-column*/

#rso {
    -moz-columns: /*[[columns]]*/;
    /*-moz-column-fill: balance;*/
}
DIV#cnt #center_col {
    width: 100%;
    margin: auto
}
#cnt #center_col,
.mw #center_col {}
.rc {
    overflow: hidden;
}
/* wrap result cells */
g-section-with-header {
    overflow:hidden
}
.f {
    /*wrap result URL*/
    white-space: normal !important;
    height: auto !important
}
#brs {
    justify-content: center;
    display: flex
}
#cnt #center_col, #cnt #foot {
    margin: auto
}
}
