css:
@-moz-document domain("discordapp.com") {
div.private-channels .status.status-idle:after {
    content:  "idle";
    opacity: 0.5;
    color: #FFFFFF;
    position: absolute;
    left: 21px;
    top: -1px;
}


div.private-channels .status.status-online:after {
    content: "online";
    opacity: 0.5;
    color: #00FFB8;
    position: absolute;
    left: 21px;
    top: -1px;
}


div.private-channels .status.status-offline:after {
    content: "offline";
    opacity: 0.5;
    color: #777777;
    position: absolute;
    left: 21px;
    top: -1px;
}


div.private-channels a:hover .status.status-idle:after,
div.private-channels a:hover .status.status-online:after,
div.private-channels a:hover .status.status-offline:after {
    opacity: 1.0;
}

div.member-inner {
    position: relative;
}

div.channel-members div.member-status.member-status-idle div.member-inner:after {
    content: "idle";
    opacity: 0.5;
    color: #ffffff;
    position: absolute;
    font-size: 85%;
    left: 0px;
    top: 9px;
}
div.channel-members div.member-status.member-status-online div.member-inner:after {
    content: "online";
    opacity: 0.5;
    color: #00FFB8;
    position: absolute;
    font-size: 85%;
    left: 0px;
    top: 9px;
}
div.channel-members div.member-status.member-status-offline div.member-inner:after {
    content: "offline";
    opacity: 0.5;
    color: #777777;
    position: absolute;
    font-size: 85%;
    left: 0px;
    top: 9px;
}

div.member.member-status.member-status-idle:hover div.member-inner:after,
div.member.member-status.member-status-online:hover div.member-inner:after,
div.member.member-status.member-status-offline:hover div.member-inner:after
{
    opacity: 1.0;
}
}
