css:
@-moz-document domain("www.frandroid.com") {
/**
    Partout
*/

/* Titre. */
h2.has-text-gradient-blue-inverted {
	display: none;
}

/* Pub. */
div.is-margin-bottom-4, div.is-margin-bottom-6 {
	display: none;
}

/* Les "entre-article". */
div.has-background-gradient-blue {
	display: none;
}

div.sidebar {
	display: none;
}

div.article-content.with-sidebar {
	max-width: max-content;
}
}

@-moz-document url-prefix("https://www.frandroid.com/actualites") {
/**
    Juste sur la page actualité.
*/

/* Le bazar du haut. */
div.is-margin-bottom-3 {
	display: none;
}
}

@-moz-document regexp("https:\\/\\/www.frandroid.com\\/[^actualites].+") {
/**
    Toutes les pages sauf la page d'accueil et la page d'actualité.
*/

/* Demande d'email pour newsletter. */
div.newsletter-form.newsletter-form--columns {
    display: none;
}

/* Suggestions d'article. */
div.article-footer__related-posts {
    display: none;
}

div.breadcrumb ul {
	max-width: 100%;
}
}
