css:
@-moz-document domain("cryptopia.co.nz") {
/*
 * General
 */

body /* website */
, #sidebar /* sidebar */
{
    /* Set global colors */
    background-color: /*[[bg]]*/;
    color: /*[[fg_general]]*/;
}

*
{
    /* Remove all borders */
    border: none !important;
}

.btn-default:focus
, .btn-default:hover
, .btn:focus
, .btn:hover
{
    background-color: /*[[bg_btn]]*/;
    color: /*[[fg_general]]*/;
}

.btn.active
, .btn-default.active
, .btn.active:focus
, .btn.active:hover
, .btn-default.active:focus
, .btn-default.active:hover
{
    background-color: /*[[bg_btn_hover]]*/;
    color: /*[[fg_general]]*/;
}

.form-control /* all form controls */
{
    background-color: /*[[bg_frm_ctrl]]*/;
    color: /*[[fg_form_ctrl]]*/;
}

.form-control[disabled] /* disabled form controls */
{
    background-color: /*[[bg_frm_ctrl_dis]]*/;
}

/*
 * Navigation
 */

#cryptopia-nav /* navigation */
{
    background-color: /*[[bg_nav]]*/;
}

#cryptopia-nav /* navigation */
, #cryptopia-nav a /* navigation links */
{
    color: /*[[fg_nav]]*/;
}

#cryptopia-nav ul
{
    /* disable top border */
    box-shadow: none;
}

#cryptopia-nav ul>li:hover
, #cryptopia-nav ul>li:hover a
{
    background-color: /*[[bg_nav_link]]*/;
}

#cryptopia-nav ul>li>a:hover
{
    background-color: /*[[bg_nav_link_hover]]*/ !important;
}

#cryptopia-nav ul>li>ul>li:hover
, #cryptopia-nav ul>li>ul>li:hover a
, #cryptopia-nav ul>li>ul>li a
, #cryptopia-nav ul>li>ul>li a:hover
{
    background-color: /*[[bg_nav_link_hover]]*/ !important;
}

#cryptopia-nav a[href='/Home']:hover
, li.nohover a
, li.hohover:hover a
, li.nohover a:hover
, li.nohover:hover a:hover
, .main-logo:hover
{
    background-color: /*[[bg_nav]]*/ !important;
    box-shadow: none !important;
    border: none !important;
}

#cryptopia-nav *
{
    box-shadow: none !important;
    border: none !important;
}

/*
 * Trading chart
 */

#chart-trade /* candles */
{
    background-color: /*[[bg_chart]]*/;
}

.highcharts-scrollbar /* svg scrollbar */
{
    display: none;
}

/*
 * Order panels
 */

.row .panel, /* panels */
.row .panel-primary /* panels */
{
    background-color: /*[[bg_panel]]*/;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .1), 0 -2px 1px rgba(77, 182, 172, 1);
}

.panel-primary>.panel-heading /* panel headings */
{
    background-color: /*[[bg_panel_heading]]*/ !important;
    color: /*[[fg_general]]*/ !important;
    box-shadow: none !important;
}

#sellorders>tbody>tr:hover
, #buyorders>tbody>tr:hover
, #markethistory>tbody>tr:hover
, #useropenorders>tbody>tr:hover
, #userorderhistory>tbody>tr:hover
{
    color: /*[[fg_order]]*/;
}

a#userBalanceBuy /* panel value buy link */
, a#userBalanceSell /* panel value sell link */
{
    color: /*[[fg_balance]]*/;
}

.input-group-addon /* order input */
{
    background-color: /*[[bg_btn]]*/;
    color: /*[[fg_btn_addon]]*/;
}

.panel-body input.form-control /* order input buy amount */
{
    background-color: /*[[bg_panel_input]]*/;
    color: /*[[fg_form_ctrl]]*/;
}

.dataTables_scrollBody td>a /* exchange pairs in overview */
, #top-currency td a /* exchange pairs top markets */
{
    color: /*[[fg_exchange_pair]]*/;
}

/*
 * Sidebar
 */

#markets-search /* sidebar search */
, #balance-search
, #openorders-search
, .btn-default /* buttons */
{
    background-color: /*[[bg_sidebar_search]]*/;
    color: /*[[fg_general]]*/;
}

table#sideMenuOpenOrders a /* exchange pair */
{
    color: /*[[fg_exchange_pair]]*/;
}

table#sideMenuOpenOrders tr:hover td /* order hover */
{
    background-color: /*[[bg_sidebar_order_hv]]*/;
}

.table-striped>tbody>tr:nth-child(2n+1) /* other rows */
{
    background-color: /*[[bg_table_stripe]]*/;
}

tr.info /* selected row in sidebar */
, tr.info * /* selected row content */
, .table>thead>tr>td.info
, .table>tbody>tr>td.info
, .table>tfoot>tr>td.info
, .table>thead>tr>th.info
, .table>tbody>tr>th.info
, .table>tfoot>tr>th.info
, .table>thead>tr.info>td
, .table>tbody>tr.info>td
, .table>tfoot>tr.info>td
, .table>thead>tr.info>th
, .table>tbody>tr.info>th
, .table>tfoot>tr.info>th
{
    background-color: /*[[bg_sidebar_selected]]*/;
}

.text-success {
    color: /*[[fg_trend_up]]*/;
}

.text-danger {
	color: /*[[fg_trend_down]]*/;
}

/*
 * Account
 */

.profile-sidebar
{
    background-color: /*[[bg_profile_sidebar]]*/ !important;
}

.profile-usertitle-name
{
    color: /*[[fg_username]]*/ !important;
}

form#form0 a
{
    color: /*[[fg_form0]]*/ !important;
}

/*
 * Chat
 */

.chat-header
{
    background-color: /*[[bg_chat_header]]*/;
    color: /*[[fg_chat]]*/;
}

table.chat-messages tr>td:hover
{
    background-color: /*[[bg_chat_msg_hover]]*/;
}

textarea.chat-message
{
    background-color: /*[[bg_chat_msg]]*/;
    color: /*[[fg_chat]]*/;
}

textarea.chat-message:focus
, textarea.chat-message:active
{
    box-shadow: 0 0px 3px rgb(96, 125, 139);
}

/*
 * Footer
 */

#footer /* footer */
{
    background-color: /*[[bg_footer]]*/;
    color: /*[[fg_footer]]*/;
}

#footer h4
{
    color: /*[[fg_footer]]*/;
}

#footer a
{
    color: /*[[fg_footer_link]]*/;
}
}
