/* 画像を幅一杯表示 */
.media-gallery {
	height: auto !important;
	display: flex;
	justify-content: center;
	align-items: center;
	flex-wrap: wrap;
}
@media (min-width: 961px) {
	:not(.detailed-status) > .media-gallery {
		width: calc(100% + 64px);
		margin-top: 16px;
		position: relative;
		left: -64px;
	}
}
.media-gallery__item {
	flex-grow: 1;
	position: static;
	border: 1px solid rgba(128, 128, 128, .5);
}
.media-gallery__item-thumbnail img {
	max-height: calc(100vh / 3);
	object-fit: contain;
}
.media-gallery__item-gifv-thumbnail {
	object-fit: contain;
}
.media-gallery__gifv {
	height: 110px;
}
.detailed-status .media-gallery__gifv {
	height: auto;
}

/* 通知欄の表示を簡略化 */
.notification .display-name,
.notification .status__action-bar,
.notification .status__relative-time {
	display: none;
}
.notification__favourite-icon-wrapper + span {
	font-size: small;
}
.notification .status__content p {
	color: rgba(255, 255, 255, .75);
}

/* userIDを常に表示する */
.display-name__html {
	display: block;
	float: left;
	margin-right: .5rem;
}
.display-name__account {
	display: block;
	float: left;
}
/* clearfix */
.display-name:after {
	content: "";
	clear: both;
	display: block;
}

/* カラム幅を変更 */
@media (min-width: 631px) {
	.column {
		flex-grow: 1;
		width: 0;
	}
	.drawer,
	.column:last-of-type {
		flex-grow: 1;
		width: 0;
		max-width: 300px;
	}
/*[[drawer]]*/
