@-moz-document regexp("https*://www.google..*") {

    ::-webkit-scrollbar-button {
        display: none;
        height: 13px;
        border-radius: 0px;
        background-color: #AAA;
    }

    ::-webkit-scrollbar-button:hover {
        background-color: #AAA;
    }

    ::-webkit-scrollbar-thumb {
        background-color: #161616;
        border-radius: 100px;
    }

    ::-webkit-scrollbar-thumb:hover {
        background-color: #161616;
    }

    ::-webkit-scrollbar-track {
        background-color: #4f4f4f;
        border-radius: 25px;
    }

    ::-webkit-scrollbar-track:hover {
        background-color: #4f4f4f;
        border-radius: 25px;
    }

    ::-webkit-scrollbar {
        width: 8px;
    }

    img,
    .rg_ic,
    .rg_i {
        background-color: rgba(0, 0, 0, 1) !important;
    }

    .ctr-p,
    .mdm {
        background-color: /*[[maincolor]]*/;
        color: /*[[maintextcolor]]*/;
    }

    .fbar,
    .sfbgg,
    #hdtb-msb,
    #hdtbSum,
    .xpdopen,
    .nlkcvc *,
    .dHOsHb *,
    #topabar,
    #center_col,
    .So9e7d *,
    .minidiv .sfbg,
    #logo,
    .y8Jpof:hover,
    .rg_bx,
    .iKjWAf,
    #logo *,
    .cv2VAd,
    .abup,
    .appcenter,
    #botabar,
    .garHBe,
    .K20DDe, .Uo6gve, .a1vOw, .BY2RHc
     {
        background-color: /*[[secondcolor]]*/ !important;
    }

    #center_col {
        padding-top: 10px;
        margin-top: 7px;
        border-radius: 9px;
    }

    #fbar,
    .b2hzT,
    .xpdopen,
    .kp-blk,
    .nlkcvc *,
    .dHOsHb *,
    #hdtb,
    .So9e7d *
    .cv2VAd *,
    .cv2VAd,
    .klbar {
        border: none !important;
    }

    .gb_P,
    .b0KoTc,
    #gb#gb a.gb_b,
    #SIvCob,
    .st,
    .r-iSKToTu2W3ok,
    .XiMyEd,
    .ellip,
    .ILfuVd,
    .xcQxib,
    .bNg8Rb+span,
    #swml-loc,
    #swml-src,
    .ab_button,
    .med,
    .dPAwzb,
    .nJGrxf,
    .gb_ca,
    .gb_ca *,
    .abup,
    .rhsl4,
    .izHQgf,
    .cwUqwd,
    .EaHP9c
    .EaHP9c *,
    .SPZz6b * {
        color: /*[[secondtextcolor]]*/ !important;
    }

    #lga {
        background-image: /*[[logo]]*/;
        background-size: 23%;
        background-repeat: no-repeat;
        background-position: center 80%;
    }

    #hplogo {
        display: none;
    }

    a:visited {
        color: #0093AF !important;
    }

    a,
    .wUrVib,
    .qa__svg-icon,
    .gsmt,
    .fl,
    .w8qArf,
    .w8qArf .fl,
    .w8qArf *,
    .Q8LRLc,
    .LrzXr,
    .kno-fv,
    .RJn8N,
    .xXEKkb,
    .ellip,
    .mVDMnf
    .gb_P,
    .b0KoTc,
    #gb#gb a.gb_b,
    #SIvCob,
    .st,
    .r-iSKToTu2W3ok,
    .XiMyEd,
    .ellip,
    .ILfuVd,
    .xcQxib,
    .bNg8Rb+span,
    #swml-loc,
    #swml-src,
    .ab_button,
    .med,
    .dPAwzb,
    .nJGrxf,
    .gb_ca,
    .gb_ca *,
    .abup,
    .rhsl4,
    .izHQgf,
    .cwUqwd,
    .EaHP9c
    .EaHP9c * {
        color: /*[[secondtextcolor]]*/ !important;
    }

}

