body,
  section,
  p,
  blockquote,
  div,
  span, 
  li , 
  h1,
  h2,
  h3
  {
      font-family: 'Open Sans', sans-serif !important;
      background: #333 !important;
      color: #ccc !important;
      border: none !important;
