css:
@-moz-document url-prefix("https://www.nicovideo.jp/watch/") {
/* * ver */
html {

/* ** page default */

	--cajDefaultTextColor: #000;
	--cajDefaultLinkColor: #777;
	--cajDefaultTextColorPale: #99a3ad;
	--cajDefaultBackgroundColor: #fff;
	--cajDefaultBorderColor: #e5e8ea;
	--cajDefaultFontFamily: Avenir,Open Sans,Helvetica Neue,Helvetica,Arial,Verdana,Roboto,ヒラギノ角ゴ Pro,Hiragino Kaku Gothic Pro,Meiryo UI,メイリオ,Meiryo,MS\ Ｐゴシック,MS PGothic,sans-serif;

/* ** size, position, timing */

	--cajPlayerleftFadeinDelay: 0s;
	--cajPlayerleftFadeinSpeed: 0.5s;
	--cajPlayerleftFadeoutDelay: 0s;
	--cajPlayerleftFadeoutSpeed: 0.8s;

	--cajPlayerleftMarqueeFadeinDelay: 0.1s;

	--cajHeaderItemsWidthNormal: 927px;

/* ** font */

	--cajBodyFontFamily: "/*[[FontMain]]*/", var(--cajDefaultFontFamily);
	--cajBodyMonospaceFontFamily: "/*[[FontMain]]*/", monospace;
	--cajBodyLinkFontFamily: var(--cajBodyFontFamily);
	--cajBodyButtonFontFamily: var(--cajBodyFontFamily);
	--cajPlayerrightHeaderFontFamily: "/*[[FontMain]]*/", "TTEdit半角ゴシック", "k8x12", "TTEdit2/3ゴシック", var(--cajBodyFontFamily);
	--cajPlayerrightCommentFontFamily: "/*[[FontMain]]*/", "Meiryo UI", "MeiryoKe_UIGothic", var(--cajBodyFontFamily);
	--cajPlayerrightDataFontFamily: var(--cajBodyMonospaceFontFamily);

/* ** color */

/*[[ColorPreset]]*/

}





/* * layout */





/* ** general */

body.ja-jp.is-middle,
body.ja-jp.is-large,
body.ja-jp.is-autoResize {
	background-color: var(--cajBodyBackgroundColor);
}
#js-app {
	background-color: transparent;
	color: var(--cajBodyTextColor);
	font-family: var(--cajBodyFontFamily);
}
/* *** link */
#js-app .HeaderContainer a[href]:not(.TagItem-name),
#js-app .BottomContainer-main .Card.PlaylistContainer a[href] {
	color: var(--cajBodyLinkColor);
	font-family: var(--cajBodyLinkFontFamily);
}
#js-app .BottomContainer a[href]:not([href^="javascript:"]) {
	color: var(--cajBottomLinkColor);
	font-family: var(--cajBodyLinkFontFamily);
}
/* *** video thumbnail delay-load area */
#js-app .PlaylistCardList .RouterLink .InView,
#js-app .ContentTree .RouterLink .InView,
#js-app .RouterLink.UadVideoItem-link .UadVideoItem-thumbnail {
	background-color: var(--cajThumbnailBackgroundColor);
}
#js-app .VideoListPanelContainer .RouterLink .Thumbnail {
	background-color: var(--cajPlayerleftBackgroundColor);
}
/* *** mild pictures for dark-theme */
#js-app .MainContainer .MiniBannerAdContainer-inner,
#js-app .HeaderContainer img,
#js-app .BottomContainer .BottomMainContainer .Card:not(.PlaylistContainer):not(.UadVideosContainer) img,
#js-app .BottomContainer .BottomSideContainer img,
#js-app .BottomContainer .BottomContainer-adContainer img,
#js-app .HeaderContainer iframe,
#js-app .BottomContainer .BottomSideContainer iframe,
#js-app .BottomContainer .BottomContainer-adContainer iframe,
#js-app .BottomContainer .BottomMainContainer .UadVideoItem-thumbnail,
#js-app .BottomContainer .BottomMainContainer #ichiba_mainpiaitem,
#js-app .BottomContainer .BottomMainContainer #ichibaMainFooter input,
#js-app .BottomContainer .BottomSideContainer .FeedbackLink {
	opacity: var(--cajPictureOpacity);
}





/* ** main container layout */

/* *** tops */
#js-app .WatchAppContainer-main {
	width: 100%;
	display: flex;
	flex-direction: column;
	margin-bottom: 0px;
}
#js-app .WatchAppContainer-main > .MainContainer {
	order: -1;
	margin: 18px auto 0px auto;
}
#js-app .WatchAppContainer-main > .HeaderContainer {
	order: 0;
	background-color: transparent;
	background-image: none;
}
/* **** player size */
body:not(.is-large) #js-app .WatchAppContainer-main > .MainContainer {
	width: 1024px;
}
body.is-large #js-app .WatchAppContainer-main > .MainContainer {
	width: 1238px;
}
@media screen and (min-height: 590px) and (min-width: 1286px) {
	body.is-autoResize #js-app .WatchAppContainer-main > .MainContainer {
		width: 1238px;
	}
}
@media screen and (min-height: 650px) and (min-width: 1392px) {
	body.is-autoResize #js-app .WatchAppContainer-main > .MainContainer {
		width: 1344px;
	}
}
@media screen and (min-height: 850px) and (min-width: 1736px) {
	body.is-autoResize #js-app .WatchAppContainer-main > .MainContainer {
		width: 1664px;
	}
}

/* *** botoms */
#js-app .WatchAppContainer-main > .BottomContainer {
	order: 0;
	background-color: var(--cajBottomBackgroundColor);
	background-image: none;
	border-top: var(--cajBottomBorderTop);
	width: auto !important;
	min-width: 1024px;
	left: 0px;
	right: 0px;
	margin-left: 0;
	margin-right: 0;
	margin-top: 20px;
	padding-top: 28px;
	padding-bottom: 26px;
}
#js-app .WatchAppContainer-main > .BottomContainer > * {
	margin-left: auto;
	margin-right: auto;
	width: 1024px;
}
#js-app .WatchAppContainer-main > .WatchAppContainer-flashPlayerNotSwitchableMessage {
	order: 1;
	text-align: center;

}





/* ** player left */

#js-app .PlayerContainer .ActionButton.is-disabled svg,
#js-app .PlayerContainer .ActionButton:disabled svg {
	fill: var(--cajPlayerleftDisableColor);
}
#js-app .PlayerContainer .ActionButton.is-disabled:hover svg,
#js-app .PlayerContainer .ActionButton:disabled:hover svg {
	fill: var(--cajPlayerleftDisableColor);
}



/* *** top: maquee */

#js-app .MarqueeContainer {
	background-color: var(--cajPlayerleftBackgroundColor);
}
#js-app .MarqueeContainer .Marquee {
	opacity: var(--cajPlayerleftMarqueeOpacity);
	transition-duration: var(--cajPlayerleftFadeoutSpeed);
	transition-delay: var(--cajPlayerleftFadeoutDelay);
}
#js-app .MarqueeContainer:hover .Marquee {
	opacity: 1;
	transition-duration: var(--cajPlayerleftFadeinSpeed);
	transition-delay: var(--cajPlayerleftMarqueeFadeinDelay);
}
/* **** text link */
#js-app .Marquee .Marquee-itemArea .AnimatorContainer .DefaultAnimator a {
	font-size: 13px;
	margin-left: 40px;
	color: var(--cajPlayerleftNormalColor);
}
/* **** close button */
#js-app .Marquee .Marquee-itemArea .AnimatorContainer .ActionButton.DefaultAnimator-excludeButton {
	position: absolute;
	left: 20px;
	height: 44px;
	width: 20px;
	margin: 0;
	padding: 0;
	border: 0px none;
	border-radius: 0px;
	background-color: transparent;
	box-shadow: none;
	color: var(--cajPlayerleftNormalColor);
	font-size: 16px;
}
#js-app .Marquee .Marquee-itemArea .AnimatorContainer .ActionButton.DefaultAnimator-excludeButton::after {
	background: none;
}
#js-app .Marquee .Marquee-itemArea .AnimatorContainer .ActionButton.DefaultAnimator-excludeButton:hover {
	background-color: var(--cajPlayerleftDisableColor);
}
/* **** pageing button: normal */
#js-app .Marquee .Marquee-buttonArea:last-child {
	position: absolute;
	left: 20px;
}
#js-app .Marquee .Marquee-buttonArea {
	background-color: transparent;
}
#js-app .Marquee.is-prevAvailable .Marquee-buttonArea .ActionButton.ArrowPrevButton svg,
#js-app .Marquee.is-nextAvailable .Marquee-buttonArea .ActionButton.ArrowNextButton svg {
	fill: var(--cajPlayerleftNormalColor);
}
#js-app .Marquee.is-prevAvailable .Marquee-buttonArea .ActionButton.ArrowPrevButton:hover,
#js-app .Marquee.is-nextAvailable .Marquee-buttonArea .ActionButton.ArrowNextButton:hover {
	background-color: var(--cajPlayerleftDisableColor);
}
/* ***** pageing button: disabled */
#js-app .Marquee:not(.is-prevAvailable) .Marquee-buttonArea .ActionButton.ArrowPrevButton,
#js-app .Marquee:not(.is-nextAvailable) .Marquee-buttonArea .ActionButton.ArrowNextButton {
	visibility: hidden;
}



/* *** middle: movie screen */

#js-app .PlayerContainer .VideoContainer:not(.is-fullscreen) {
	background-color: var(--cajPlayerleftBackgroundColor);
}



/* *** bottom 1: controller */

#js-app .ControllerBoxContainer:not(.is-fullscreen) {
	background-color: var(--cajPlayerleftBackgroundColor);
}

#js-app .PlayerContainer .SeekBarContainer,
#js-app .PlayerContainer .ControllerContainer {
	opacity: var(--cajPlayerleftControllerOpacity);
	transition-duration: var(--cajPlayerleftFadeoutSpeed);
	transition-delay: var(--cajPlayerleftFadeoutDelay);
}
#js-app .PlayerContainer:hover .SeekBarContainer,
#js-app .PlayerContainer:hover .ControllerContainer {
	opacity: 1;
	transition-duration: var(--cajPlayerleftFadeinSpeed);
	transition-delay: var(--cajPlayerleftFadeinDelay);
}
/* **** seekbar */
#js-app .SeekBar {
	background-color: var(--cajPlayerleftDisableColor);
}
#js-app .SeekBar .SeekBar-buffered,
#js-app .SeekBar .SeekBar-hover {
	background-color: var(--cajPlayerleftNormalColor);
}
#js-app .SeekBar-played {
	background-color: var(--cajPlayerleftHighlightColor);
}
#js-app .SeekBarHandle-handle {
	background-color: var(--cajPlayerleftHighlightColor);;
}
/* **** button */
#js-app .PlayerPlayTime {
	color: var(--cajPlayerleftNormalColor);
	text-shadow: none;
}
#js-app .ControllerContainer .ControllerButton svg {
	fill: var(--cajPlayerleftNormalColor);
}
#js-app .ControllerContainer .ActionButton.ControllerButton:hover:not([disabled]) svg {
	transition-duration: 0s;
	transition-delay: 0s;
	fill: var(--cajPlayerleftHighlightColor) !important;
}
#js-app .ControllerContainer .ControllerButton.UnMuteVideoButton svg,
#js-app .ControllerContainer .ControllerButton.PlayerRepeatOffButton svg,
#js-app .ControllerContainer .ControllerButton svg.CommentOnOffButton-iconShow,
#js-app .ControllerContainer .ControllerButton.DisableFullScreenButton svg,
#js-app .ControllerContainer .ControllerButton.PlayerOptionButton.is-active svg {
	fill: var(--cajPlayerleftActiveColor);
}
/* **** volumebar */
#js-app .VolumeBar-progress {
	background-color: var(--cajPlayerleftNormalColor);
}
#js-app .VolumeBar:hover .VolumeBar-progress {
	background-color: var(--cajPlayerleftHighlightColor);
}
#js-app .VolumeBar .XSlider {
	background-color: var(--cajPlayerleftDisableColor);
}
#js-app .VolumeBar .VolumeBar-progress {
	background-color: var(--cajPlayerleftNormalColor);
}
#js-app .VolumeBar:hover .VolumeBar-progress {
	background-color: var(--cajPlayerleftHighlightColor);
}
#js-app .VolumeBar,
#js-app .VolumeBar .XSlider,
#js-app .VolumeBar .ProgressBar,
#js-app .VolumeBar:hover,
#js-app .VolumeBar:hover .XSlider,
#js-app .VolumeBar:hover .ProgressBar {
	/* bottom: auto; */
	height: 12px;
	top: 6px;
}



/* *** bottom 2: comment post form */

/* **** command, comment */
#js-app .CommentPostContainer:not(.is-fullscreen) .CommentPostContainer-commandInput,
#js-app .CommentPostContainer:not(.is-fullscreen) .CommentPostContainer-commentInput {
	background-color: var(--cajPlayerleftTextareaBackgroundColor);
}
#js-app .CommentPostContainer:not(.is-fullscreen) .CommentPostContainer-commandInput {
	border-right: 1px solid transparent;
}
#js-app .CommentPostContainer:not(.is-fullscreen) .CommentCommandInput,
#js-app .CommentPostContainer:not(.is-fullscreen) .CommentInput-textarea {
	background-color: var(--cajPlayerleftTextareaBackgroundColor);
	color: var(--cajBodyTextColorPale);
}
#js-app .CommentPostContainer:not(.is-fullscreen) .CommentInput {
	height: 32px;
	padding: 0px;
	font-size: 14px;
	line-height: 14px;
}
#js-app .CommentPostContainer:not(.is-fullscreen) .CommentInput > .CommentInput-textarea {
	height: 32px;
	padding: 9px;
	font-size: 14px;
	line-height: 14px;
}
#js-app .CommentPostContainer:not(.is-fullscreen) .CommentCommandInput:focus,
#js-app .CommentPostContainer:not(.is-fullscreen) .CommentCommandInput:not([value=""]),
#js-app .CommentPostContainer:not(.is-fullscreen) .CommentInput-textarea:focus,
#js-app .CommentPostContainer:not(.is-fullscreen) .CommentInput-textarea:not(:empty) {
	background-color: var(--cajPlayerleftTextareaBackgroundColorActive);
	color: var(--cajPlayerleftTextareaTextColor);
	/* v  rendering noise (blink bug?) patch  v */
	opacity: 0.9999;
	/* ^                                      ^ */
}
/* **** submit button */
#js-app .CommentPostContainer:not(.is-fullscreen) .CommentPostButton {
	background-color: var(--cajPlayerleftPostbuttonBackgroundColror);
	color: var(--cajPlayerleftPostbuttonTextColror);
}
#js-app .CommentPostContainer .CommentPostButton:focus,
#js-app .CommentPostContainer .CommentPostButton:hover {
	background-color: var(--cajPlayerleftActiveColor);
	color: var(--cajPlayerleftNormalColor);
}
#js-app .is-fullscreen .CommentPostButton {
	background-color: rgba(128,128,128,0.7);
	color: #bbb;
}





/* ** player right */

#js-app .MainContainer-playerPanel {
	background-color: var(--cajPlayerrightBackgroundColor);
}
#js-app .NGSettingPanelContainer {
	color: var(--cajDefaultTextColor);
}

/* *** top 1: sendto */

#js-app .VideoMenuContainer-areaLeft,
#js-app .VideoMenuContainer-areaRight {
	background-color: transparent;
}
#js-app .VideoMenuContainer::after {
	border-bottom: 0px none;
}
/* **** button */
#js-app .VideoMenuContainer .VideoMenuContainer-areaLeft .VideoMenuContainer-button svg {
	fill: var(--cajPlayerrightButtonFillColor);
}
#js-app .VideoMenuContainer  .VideoMenuContainer-areaLeft .VideoMenuContainer-button:hover svg {
	fill: var(--cajPlayerrightButtonFillColorHover);
}
/* **** niconi koukoku ad */
#js-app .VideoMenuContainer-areaLeft .UadButtonBalloon {
	background-color: var(--cajPlayerrightButtonFillColorPale);
	border: none;
}
#js-app .VideoMenuContainer-areaLeft .UadButtonBalloon::before {
	border-right-color: var(--cajPlayerrightButtonFillColorPale);
}
#js-app .VideoMenuContainer-areaLeft .UadButtonBalloon::after {
	border-right-color: transparent;
}
#js-app .VideoMenuContainer-areaLeft .UadButtonBalloon a {
	color: var(--cajPlayerrightButtonTextColor);
}
#js-app .VideoMenuContainer-areaLeft .UadButtonBalloon pre.UadButtonBalloon-message {
	font-family: var(--cajBodyMonospaceFontFamily);
}
/* **** share button */
#js-app .VideoMenuContainer-areaRight .VideoMenuContainer-button svg path[fill="#fff"],
#js-app .VideoMenuContainer-areaRight .VideoMenuContainer-button svg {
	fill: var(--cajPlayerrightBackgroundColor);
}
#js-app .VideoMenuContainer-areaRight .VideoMenuContainer-button {
	opacity: var(--cajPlayerrightSharebuttonOpacity);
	filter: var(--cajPlayerrightSharebuttonFilter);
	transition: 0s;
}
#js-app .VideoMenuContainer-areaRight .VideoMenuContainer-button:hover {
	opacity: 0.9999;
	filter: none;
}



/* *** top 2: tab */

#js-app .PlayerPanelContainer {
	background-color: var(--cajPlayerrightBackgroundColor);
}
#js-app .PlayerPanelContainer-tab {
	padding-left: 44px;
	padding-bottom: 0px;
}
#js-app .PlayerPanelContainer-tab .PlayerPanelContainer-tabItem.current {
	display: none;
	width: 0px;
}
#js-app .PlayerPanelContainer-tab .PlayerPanelContainer-tabItem {
	font-size: 0px;
	border-bottom-width: 0px;
	background-color: var(--cajPlayerrightButtonFillColorPale);
	color: var(--cajPlayerrightButtonTextColor);
	border-radius: 34px 0px 0px 34px;
}
#js-app .PlayerPanelContainer-tab .PlayerPanelContainer-tabItem:first-child {
	border-radius: 17px 0px 0px 0px;
}
#js-app .PlayerPanelContainer-tab .PlayerPanelContainer-tabItem:hover {
	background-color: var(--cajPlayerrightButtonFillColorHover);
	color: var(--cajPlayerrightButtonTextColorHover);
}
#js-app .PlayerPanelContainer-tab .PlayerPanelContainer-tabItem::before {
	font-size: 13px;
	padding-right: 44px;
}
#js-app .PlayerPanelContainer-tab .PlayerPanelContainer-tabItem:last-child::before {
	content: "動画リスト ▼";
}
#js-app .PlayerPanelContainer-tab .PlayerPanelContainer-tabItem:first-child::before {
	content: "動画リスト ▲";
}





/* *** middle: chromium scrollbar */
#js-app .CommentPanelDataGrid .CommentPanelDataGrid-Body::-webkit-scrollbar,
#js-app .VirtualList-inner::-webkit-scrollbar,
#js-app .WatchRecommendation-inner::-webkit-scrollbar {
	background-color: var(--cajPlayerrightScrollbarColor);
}
#js-app .CommentPanelDataGrid .CommentPanelDataGrid-Body::-webkit-scrollbar-corner,
#js-app .VirtualList-inner::-webkit-scrollbar-corner,
#js-app .WatchRecommendation-inner::-webkit-scrollbar-corner {
	background-color: var(--cajPlayerrightScrollbarCornerColor);
}
#js-app .CommentPanelDataGrid .CommentPanelDataGrid-Body::-webkit-scrollbar-thumb,
#js-app .VirtualList-inner::-webkit-scrollbar-thumb,
#js-app .WatchRecommendation-inner::-webkit-scrollbar-thumb {
	background-color: var(--cajPlayerrightScrollbarThumbColor);
}



/* *** middle C1: comment controller */

#js-app .CommentPanelContainer {
	background-color: var(--cajPlayerrightBackgroundColor);
}
/* **** select box */
#js-app .CommentPanelMenuContainer-mainMenu select{
	background-color: var(--cajPlayerrightButtonFillColorPale);
	color: var(--cajPlayerrightButtonTextColor);
	font-family: var(--cajBodyButtonFontFamily);
	border: 0px none;
}
#js-app .CommentPanelMenuContainer-mainMenu select:hover {
	cursor: pointer;
	background-color: var(--cajPlayerrightButtonFillColorHover);
	color: var(--cajPlayerrightButtonTextColorHover) !important;
}
/* **** button */
#js-app .CommentPanelMenuContainer-mainMenu > div:first-child {
	text-align: right;
}
#js-app .CommentPanelMenuContainer-mainMenu .CommentPanelAutoScrollButton {
	position: absolute;
	right: 337px;
}
#js-app .CommentPanelMenuContainer-mainMenu button[data-title="NG設定"] {
	position: absolute;
	right: 341px;
	top: -31px;
}
#js-app .CommentPanelMenuContainer .ActionButton svg {
	fill: var(--cajPlayerrightButtonFillColor);
}
#js-app .CommentPanelMenuContainer .ActionButton:hover svg,
#js-app .CommentPanelMenuContainer-mainMenu .CommentPanelAutoScrollButton.is-active:hover svg,
#js-app .CommentPanelMenuContainer-mainMenu .CalendarButton.is-active:hover svg {
	fill: var(--cajPlayerrightButtonFillColorHover);
}
#js-app .CommentPanelMenuContainer-mainMenu .CommentPanelAutoScrollButton.is-active svg,
#js-app .CommentPanelMenuContainer-mainMenu .CalendarButton.is-active svg {
	fill: var(--cajPlayerrightButtonFillColorActive);
}
/* **** ng setting mode */
#js-app .NGSettingPanelContainer-headerText {
	text-align: center;
}
#js-app .NGSettingPanelContainer-close {
	right: auto;
	top: 7px;
	left: 9px;
	width: 26px;
	height: 26px;
	padding: 7px;
}
#js-app .NGSettingPanelContainer-contentTop{
	display: flex;
	padding: 0 0px 0 8px;
	justify-content: space-between;
	position: relative;
}
#js-app .NGSettingPanelContainer-enableSettingArea {
	text-align: left;
	order: -2;
	position: absolute;
	left: 8px;
}
#js-app .NGSettingPanelContainer-openViewerNGRegisterButtonCell {
	order: -1;
	margin: 0 0;
	padding-left: 0px;
	text-align: center;
	width: 100%;
}
#js-app .NGSettingPanelContainer-num {
	text-align: left;
	width: 30%;
	position: absolute;
	right: 0;
}
/* **** cover ad */
#js-app .CommentPanelBannerAd {
	display: none;
}



/* *** middle C2: comment list */

/* **** header and comment both */

#js-app .CommentPanelContainer [data-row-nicoru-rank="1"],
#js-app .CommentPanelContainer [data-row-nicoru-rank="2"],
#js-app .CommentPanelContainer [data-row-nicoru-rank="3"],
#js-app .CommentPanelContainer [data-row-nicoru-rank="4"] {
	background: var(--cajPlayerrightTableBackgroundColor);
}

/* *****　vertical glid line */
#js-app .CommentPanelDataGrid .CommentPanelDataGrid-HeaderCell,
#js-app .CommentPanelDataGrid .CommentPanelDataGrid-TableCell {
	border-right: var(--cajPlayerrightGridBorderColumn);
}
#js-app .CommentPanelDataGrid .CommentPanelDataGrid-HeaderCell:last-child,
#js-app .CommentPanelDataGrid .CommentPanelDataGrid-TableCell:last-child {
	border-right: 0px none;
}
/* ***** comment wa arimasen */
#js-app .CommentPanelDataGrid .CommentPanelDataGrid-Message {
	color: var(--cajPlayerrightTableTextColor);
	font-family: var(--cajPlayerrightCommentFontFamily);
}
/* ***** row first cell: comment text */
#js-app .CommentPanelDataGrid .CommentPanelDataGrid-HeaderCell[data-name="content"],
#js-app .CommentPanelDataGrid .CommentPanelDataGrid-TableCell[data-name="content"] {
	padding-left: 8px;
	font-family: var(--cajPlayerrightCommentFontFamily);
}
/* ***** row second cell: nicoru */
#js-app .CommentPanelDataGrid .CommentPanelDataGrid-TableCell[data-name="nicoru"] {
	padding-left: 0px;
}
#js-app .NicoruCell {
	padding: 0 0;
}
#js-app .NicoruIcon.is-gray circle {
	fill: transparent;
}
#js-app .NicoruCell-count[data-nicoru-count] {
	color: var(--cajPlayerrightTableTextColor);
}
#js-app .NicoruIcon.is-gray path,
#js-app .NicoruIcon.is-gray rect {
	fill: var(--cajPlayerrightTableTextColor);
}
#js-app .CommentPanelDataGrid-TableRow:hover .NicoruCell-count[data-nicoru-count] {
	color: var(--cajPlayerrightTableTextColorHover);
}
#js-app .CommentPanelDataGrid-TableRow:hover .NicoruIcon.is-gray path,
#js-app .CommentPanelDataGrid-TableRow:hover .NicoruIcon.is-gray rect {
	fill: var(--cajPlayerrightTableTextColorHover);
}
/* ***** row behind cell: video time, post time, data index */
#js-app .CommentPanelDataGrid .CommentPanelDataGrid-HeaderCell,
#js-app .CommentPanelDataGrid .CommentPanelDataGrid-TableCell {
	padding-left: 2px;
	padding-right: 2px;
	font-family: var(--cajPlayerrightDataFontFamily);
}
/* **** header row */
#js-app .CommentPanelDataGrid .CommentPanelDataGrid-HeaderCell {
	background-color: var(--cajPlayerrightHeaderBackgroundColor);
	color: var(--cajPlayerrightButtonTextColor);
	font-weight: normal;
	font-family: var(--cajPlayerrightHeaderFontFamily);
}
#js-app .CommentPanelDataGrid .CommentPanelDataGrid-HeaderCell:hover {
	background-color: var(--cajPlayerrightButtonFillColorHover);
	color: var(--cajPlayerrightButtonTextColorHover) !important;
}
/* **** comment row */
#js-app .CommentPanelContainer .CommentPanelDataGrid {
	color: var(--cajPlayerrightTableTextColor);
	background-color: var(--cajPlayerrightTableBackgroundColor);
	cursor: context-menu;
}
#js-app .CommentPanelContainer .CommentPanelDataGrid-TableRow:hover {
	background-color: var(--cajPlayerrightTableBackgroundColorHover);
	color: var(--cajPlayerrightTableTextColorHover);
}
/* ***** right click menu */
#js-app .ContextMenu-wrapper .CommentPanelContextMenuContainer {
	color: var(--cajDefaultTextColor);
	background-color: var(--cajDefaultBackgroundColor);
	border: 1px solid var(--cajDefaultBorderColor);
}



/* *** middle L2: movie list */

/* **** top switch */
#js-app .VideoListPanelContainer-playlistHeader {
	background-color: var(--cajPlayerrightHeaderBackgroundColor);
	border-bottom-width: 0px;
}
#js-app .VideoListPanelContainer-playlistHeader:hover {
	background-color: var(--cajPlayerrightButtonFillColorHover);
}
#js-app .VideoListPanelContainer-playlistHeader .PlaylistTitle-sub,
#js-app .VideoListPanelContainer-playlistHeader .PlaylistTitle-main,
