	margin-bottom: auto;
	font-size: 14px;
	color: var(--cajBottomTitleTextColor);
}
#js-app .BottomContainer .Card .Card-header,
#js-app .BottomContainer .Card .Card-main {
	padding-left: 0px;
	padding-right: 24px;
}
/* delete white-background */
#js-app .BottomContainer .Card {
	background-color: transparent;
	box-shadow: none;
}



/* *** content tree */

#js-app .BottomMainContainer .ContentTree-parentSummary .Link,
#js-app .BottomMainContainer .ContentTree-parentSummary .Link * {
	background-color: var(--cajDefaultBackgroundColor) !important;
	color: var(--cajDefaultLinkColor) !important;
}



/* *** niconi koukoku */

#js-app .BottomMainContainer .UadVideosContainer-item {
	height: 140px;
}
#js-app .BottomMainContainer .UadVideoItem-title {
	height: 46px;
}



/* *** niconico ichiba */

#js-app .BottomMainContainer .Ichiba .IchibaForWatch {
	background-color: transparent;
}
#js-app .BottomMainContainer .Ichiba #ichibaMain {
	font-family: var(--cajBodyFontFamily);
}
#js-app .BottomMainContainer .Ichiba .IchibaForWatch_Title_Body,
#js-app .BottomMainContainer .Ichiba .IchibaSuggest_Title {
	color: var(--cajBottomTitleTextColor);
}
#js-app .BottomMainContainer .Ichiba .IchibaForWatch_EditButton,
#js-app .BottomMainContainer .Ichiba .IchibaForWatch_SearchButton,
#js-app .BottomMainContainer .Ichiba .IchibaFooter_SearchButton {
	color: var(--cajBottomLinkColor);
	border-color: var(--cajBottomLinkColor);
}
#js-app .BottomMainContainer .Ichiba .IchibaMainItem_Thumbnail {
	border: 0px none;
}
#js-app .BottomMainContainer .Ichiba .IchibaSuggestItem {
	border: 0px none;
	box-shadow: none;
}
#js-app .BottomMainContainer .Ichiba .IchibaMainItem_Detail > div,
#js-app .BottomMainContainer .Ichiba .IchibaMainItem_Detail > div span,
#js-app .BottomMainContainer .Ichiba .IchibaSuggestItem_Detail > div,
#js-app .BottomMainContainer .Ichiba .IchibaSuggestItem_Detail > div span {
	color: var(--cajBottomTextColorPale);
}
#js-app .BottomMainContainer .Ichiba .IchibaMainItem_Detail .IchibaMainItem_Price_SaleRate_Number,
#js-app .BottomMainContainer .Ichiba .IchibaSuggestItem_Detail .IchibaSuggestItem_Price_SaleRate_Number {
	color: var(--cajBottomTextColor);
}
#js-app .BottomMainContainer .Ichiba .IchibaMainItem_Thumbnail img,
#js-app .BottomMainContainer .Ichiba .IchibaSuggestItem_Thumbnail img {
	border-radius: 4px;
}
#js-app .BottomMainContainer .Ichiba .IchibaMain_Container--blank .IchibaBalloon,
#js-app .BottomMainContainer .Ichiba .IchibaSuggestItem_AddButton {
	opacity: 0.8;
}


/* *** bottom right-sidebar */

#js-app .BottomSideContainer {
	color: var(--cajBottomTextColor);
}

/* **** square ad */
#js-app .BottomSideContainer .RectangleAdContainer .Advertisement {
	background-color: transparent;
}
/* **** nicolive osusume */
#js-app .BottomSideContainer .LiveTopicsContainer .LiveTopicslabel {
	display: none;
}
#js-app .BottomSideContainer .LiveTopicsContainer .LiveTopicsItem-right {
	vertical-align: middle;
}
#js-app .BottomSideContainer .LiveTopicsContainer .LiveTopicsItem-programTitle {
	margin-top: auto;
}
#js-app .BottomSideContainer .LiveTopicsContainer .LiveTopicsItem-community {
	font-weight: bold;
	color: var(--cajBottomLinkColor);
	line-height: 1em;
	height: 13px;
}
/* **** ad sticky-move stop */
#js-app .BottomSideContainer .sticky-outer-wrapper {
	height: auto !important;
}
#js-app .BottomSideContainer .sticky-outer-wrapper .sticky-inner-wrapper {
	position: static !important;
	transform: none !important;
	padding-top: 16px;
}





/* ** page bottom bar */
#js-app .WatchAppContainer-switchToFlash {
	margin-top: 0px;
	background-color: #272727;
}
#js-app .SwitchToFlashLead .SwitchToFlashLead-info .SwitchToFlashLead-new {
	background-color: Transparent;
	color: #000;
}
#js-app .FooterContainer {
	margin-top: 0px;
}
