css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tvtropes.org") {
/*[[theme]]*/

body, .main-full-width, .footer-bottom, .panel, .trope-cont, .main-left.ykttw .ykttw-overview .item .item-content, .query-page .post-list > li > .post-item .post-data-inner, #main-content {
  background-color: var(--bg)
}
#header-new, .main-left > .inner, .sidebar-right .sidebar-item, .footer-top, #loginModal #login-modal-tabs, .modal-content, #loginModal .row.panel, .gsc-control-cse, .new-trope-depictions .trope-body, .main-left.ykttw .ykttw-overview .wrapper, .acaptionright, #main-content #main-content-sidebar {
  background-color: var(--bg-2)
}
oddrow, #header-new #main-nav ul li:first-child a::before, #header-new #main-nav ul li a::after, .sidebar-right h3.sidebar-title, .secondary-nav, ul.section-link-row, .panel-default > .panel-heading, .panel-footer, .article-history .diffbox, .modal-header, .new-trope-depictions .header .inner, .article-page .page-content h1, div.floatboxright, div.legend, .page-content .btn, .main-left.ykttw .ykttw-overview .item .item-title-bar, .post-children-list:not(.no-children), .query-page .post-list .post-item .banner, .secondary-nav .dropdown-list > li > ul, .dropdown-menu {
  background-color: var(--bg-odd)
}
input[type="text"], input[type="password"], select, textarea, .form-control, .secondary-nav .watch-page, ul.main-body-links > li > a, div.folderlabel, div.folderlabelopen, #main-content-sidebar .sidebar-item p.sidebar-item-title::after, ul.subpage-links > li > a {
  background-color: var(--bg-input);
  color: var(--fg-input)
}
ul.main-body-links > li > a.curr-subpage {
  background-color: var(--bg-input) !important;
  color: var(--fg-input) !important
}
input[type="text"]:focus, input[type="password"]:focus, select:focus, textarea:focus, .form-control:focus, .secondary-nav .watch-page:hover, div.folderlabel:hover, div.folderlabelopen:hover, ul.subpage-links > li > a:hover {
  background-color: var(--bg-input-hover);
  color: var(--fg-input-hover)
}
ul.main-body-links > li > a.curr-subpage:hover, ul.main-body-links > li > a:hover {
  background-color: var(--bg-input-hover) !important;
  color: var(--fg-input-hover) !important
}
body, .navbar, input[type="text"], select, textarea, .form-control {
  background-image: none
}
#loginModal #login-modal-tabs li.cur-tab a::after, .fp-divider::before, .fp-divider::after, .page-content h3, .article-page .page-content h2 {
  background-color: transparent;
}

/* Foreground */
body, h1, h2, h3, h4, h5, .panel-default > .panel-heading, .page-controls > ul > li > a, .search-results .gs-result .gs-snippet, .search-results .gsc-results .gsc-cursor-box .gsc-cursor-page, .new-trope-depictions .share-count, .new-trope-depictions .footer a, .browse-page .page-filters > ul > li a, .btm-section .trope-img-title, .main-left.ykttw .ykttw-overview .item .desc, .query-page .post-list .post-item .body, .article-content, .comment-box, .launch-pad-draft, #main-content-sidebar .sidebar-item, #main-content-sidebar .sidebar-item a:not(.bubble), .action-bar a:link, .action-bar a:visited, .action-bar a:hover {
  color: var(--fg)
}
.secondary-nav .dropdown.more-links > ul li a, .secondary-nav .dropdown.more-links > ul li a i {
  color: var(--fg) !important
}
.brand, .section-content h1.page-title, .section-links h3, .search-results .gs-result .gs-visibleUrl, .new-trope-depictions .controller .title span, .new-trope-depictions .trope-body .author, .alt-titles h3 span, div.floatboxright, h1.page-title, .table > tbody > tr > th {
  color: var(--fg-2)
}
.muted, .fancy, .footer-bottom p, .sidebar-right ul.sidebar-collapsible > li ul li a, #loginModal #login-modal-tabs li a, #loginModal fieldset .body, .search-results .gsc-control-cse div, .new-trope-depictions .trope-body .date-of-approval, .forum-topics .section-content span, .btm-section .trope-img-title .group-name, .main-left.ykttw .ykttw-overview .item .item-footer .footer-info-list, .query-page .edited_label {
  color: var(--fg-muted)
}
a.createlink:link {
  color: #f33;
}
a[style*="color:purple"] {
  color: #88f !important
}
/* Links */
a, a:link, #header-new #main-nav ul li a, .share-box, .sidebar-right ul.sidebar-collapsible > li > a, a.urllink:link, ul.section-link-row li a, ul.footer-menu li a, #loginModal #login-modal-tabs li.cur-tab a, .search-results .gs-result .gs-title, .search-results .gs-result .gs-title *, .page-content .btn, .main-left.ykttw .ykttw-overview .item .item-content .view-trope, .query-page .post-list .post-item .footer .right-side .voting li a, #main-content[class*="HomePage"] #main-entry .article-content #featured-tropes .featured-trope a.more-link, #main-content #main-entry h1.entry-title a {
  color: var(--fg-link)
}
a:hover, a:visited:hover, #header-new #main-nav ul li a:hover, .share-box:hover, .sidebar-right ul.sidebar-collapsible > li > a:hover, a.urllink:hover, ul.section-link-row li a:hover, ul.footer-menu li a:hover, .sidebar-right ul.sidebar-collapsible > li a:hover, .sidebar-right ul.sidebar-collapsible > li.open > a, .search-results .gs-result .gs-title:hover, .search-results .gs-result .gs-title:hover *, .new-trope-depictions .footer a:hover {
  color: var(--fg-link-hover)
}
.secondary-nav li a:hover, .secondary-nav li a:hover i, p > a:hover {
  color: var(--fg-link-hover) !important;
}
a:visited {
  color: var(--fg-link-visited)
}

/* Borders */
noborder, .navbar, .sidebar-right .social-box ul li a, ul.main-body-links > li > a, ul.section-link-row, .footer-social ul li a, .page-controls, .article-history .diffbox, #header-new, .gsc-control-cse, .gsc-webResult.gsc-result, .gsc-webResult.gsc-result:hover, .search-results .gsc-results .gsc-cursor-box .gsc-cursor-page, .new-trope-depictions .trope-body, .new-trope-depictions .controller, .new-trope-depictions .controller .right-side, .new-trope-depictions .controller .left-side, .new-trope-depictions .share-count, .new-trope-depictions .header .inner, div.floatboxright, .page-content li, .trope-cont, .trope-img, .main-left .section:last-child, .alert-danger, .query-page .post-list > li > .post-item .post-data-inner, .post-children-list:not(.no-children), .query-page .post-list .post-item .header, .acaptionright, div.folderlabel, div.folderlabelopen, div.titlebar {
  border: none
}
custom-border {
  border: var(--border)
}
input, select, textarea, ul.main-body-links, .page-content li, hr, .page-content hr, .form-control, .page-controls > ul > li > a, .panel-default > .panel-heading, .panel, .page-heading-wrapper, .panel-footer, .sidebar-right ul.sidebar-collapsible > li ul, #loginModal #login-modal-tabs li a, #loginModal #login-modal-tabs, .modal-header, .search-results .gsc-above-wrapper-area, .page-content .btn, .alphalist a, .section.page-header, .page-header, .browse-page .page-filters > ul > li a, .main-left.ykttw .ykttw-overview .item .item-content .view-trope, .fancy span::before, .fancy span::after, .filter-sort .search-form .form-control, .table > tbody > tr > th, .table > tbody > tr > td, .query-page .post-list .post-item .footer .right-side .voting li a, .query-page .post-list .post-item .footer a, .query-page .post-list .post-item .footer .toggle-child, .secondary-nav .dropdown-list > li > ul > li, .video-gallery .thumbnail .video-info-row .video-title-line .index-title-row {
  border-color: var(--border-color)
}
.section-block hr {
  border-color: var(--border-color) !important;
}
#main-content #main-entry h1.entry-title a {
  border-color: var(--fg-link);
}
#loginModal #login-modal-tabs li.cur-tab a::before {
  color: var(--border-color)
}
.main-left.ykttw .ykttw-overview .item .desc::after, .post-children-list > li::after {
  background-color: var(--border-color)
}


/* Shadows */
.share-box, .share-box:hover, .main-left.ykttw .ykttw-overview .wrapper, .main-left.ykttw .ykttw-overview .item .item-content {
  box-shadow: none
}
.section-block::before {
  box-shadow: var(--border-color) 0 0 3px 1px;
}

/* Custom BG/FG */
success, success a, .diffadd, span.newmsg {
  background-color: var(--bg-success);
  color: var(--fg-success)
}
ins {
  background-color: rgba(0, 255, 0, 0.5);
  color: var(--fg-success);
}
info, info a {
  background-color: var(--bg-info);
  color: var(--fg-info)
}
warning, warning a, .banner_fridge, .banner_fridge strong, .diffdel {
  background-color: var(--bg-warning);
  color: var(--fg-warning)
}
error, error a, del, .alert-danger {
  background-color: var(--bg-error);
  color: var(--fg-error)
}
disabled, .spoiler, .spoiler a:link {
  background-color: var(--bg-disabled);
  color: var(--bg-disabled)
}
.spoiler:hover, .spoiler:hover a:link {
  color: var(--fg-disabled);
}
.sidebar-right .social-box ul li a:hover, .footer-social ul li a:hover, .search-results .gsc-results .gsc-cursor-box .gsc-cursor-current-page, .alphalist a:hover, .main-left.ykttw .ykttw-overview .item .item-content .view-trope:hover, .dropdown-menu > li > a:hover, .secondary-nav .dropdown-list > li > ul > li > a:hover {
  background-color: var(--fg-link);
  color: var(--bg);
}

/* Code */
pre {
  background-color: var(--syntax-bg);
  border: none
}
pre {
  color: var(--syntax-fg)
}

/* Filters */
img.img-default {
  filter: brightness(200%)
}

/* Ads */
.ad-content-top {
  min-height: 0px;
}
.sb-ad-unit, .sidebar-item.showcase {
  display: none;
}

}
