css:
/*
Primary: #21252D
Secondary: #2C3037
Tertiary: #434953

Border: #3C414C
Link: #4B97F8
Search: #F8AC4B

Primary Text: #EBEBEB
Secondary Text: #626262
Tertiary Text: #555555
*/

@-moz-document url-prefix("http://mangalife"), url-prefix("https://mangalife"), domain('mangalife.us')
{

div[style*="background-color"]:not(.navShowUser):not(.navShowSearch) {
    background-color: /*[[Primary]]*/ !important;
}

.quickSearchResults, .resultItem {
    background-color: /*[[Primary]]*/ !important;
}

.leftColumn .box-body {
    background-color: /*[[Secondary]]*/ !important;
}

a.list-group-item, button.list-group-item {
    color: /*[[Primary Text]]*/ !important;
}

.btn-default {
    background-color: /*[[Secondary]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
    border-color: /*[[Border]]*/ !important;
}

a.list-group-item:focus, a.list-group-item:hover, button.list-group-item:focus, button.list-group-item:hover {
    color: /*[[Primary Text]]*/ !important;
    background-color: /*[[Secondary]]*/ !important;
    text-decoration: underline !important;
}

.note {
    color: /*[[Secondary Text]]*/ !important;
}

a {
    color: /*[[Link]]*/ !important;
}

.tooltip {
    border: 1px solid /*[[Border]]*/ !important;
}

.tooltip-inner, .tooltip {
    background-color: /*[[Primary]]*/ !important;
}

blue {
    color: /*[[Link]]*/ !important;
}

.back-to-top {
    background-color: /*[[Secondary]]*/ !important;
}

.bigNavBar, .footer, .navbar-default, .navbar-default .navbar-nav>.active>a, .navbar-default .navbar-nav>.active>a:focus, .navbar-default .navbar-nav>.active>a:hover, .navbar-default .navbar-nav>.open>a, .navbar-default .navbar-nav>.open>a:focus, .navbar-default .navbar-nav>.open>a:hover, .navbar-default .navbar-toggle:focus, .navbar-default .navbar-toggle:hover {
    background-color: /*[[Primary]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
}

.box-header {
    background-color: /*[[Primary]]*/ !important;
    border: 1px solid /*[[Border]]*/ !important;
    border-bottom: none !important;
    color: /*[[Primary Text]]*/ !important;
}

.breadcrumb {
    background-color: /*[[Secondary]]*/ !important;
}

.containerNav, .modal-content {
    background-color: /*[[Secondary]]*/ !important;
    border: 1px solid /*[[Border]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
}

.textarea-wrapper,
.textarea {
    background-color: /*[[Secondary]]*/ !important;
    border-color: /*[[Border]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
}

.feedDescription {
    color: /*[[Secondary Text]]*/ !important;
}

.genreLastest {
    color: /*[[Link]]*/ !important;
}

.alert {
    box-shadow: none !important;
    background-color: /*[[Tertiary]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
}

.comment-container {
    background-color: /*[[Primary]]*/ !important;
}

.leftColumn .latestSeries .row, .perFeed .row {
    background-color: /*[[Primary]]*/ !important;
    border: 1px solid /*[[Border]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
}

.list-group-item, .leftColumn .latestGroup, .hotUpdate .box-body, .MostPopular {
    background-color: /*[[Secondary]]*/ !important;
    border: 1px solid /*[[Border]]*/ !important;
}

.mainWell {
    background-color: /*[[Secondary]]*/ !important;
    border: 1px solid /*[[Border]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
}

.modal-header {
    background-color: /*[[Primary]]*/ !important;
    border: 1px solid /*[[Border]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
}

.navShowSearch {
    background-color: /*[[Search]]*/ !important;
}

.navShowUser {
    background-color: /*[[Link]]*/ !important;
}

.navbar-addon {
    background-color: /*[[Secondary]]*/ !important;
    border-bottom: 1px solid /*[[Border]]*/ !important;
    border-top: 1px solid /*[[Border]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
}

.panel-collapse, .list-group, .panel-body, .panel-default, .panel-group, .panel-heading {
    background-color: /*[[Primary]]*/ !important;
    border-color: /*[[Border]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
}

.userParent {
    background-color: /*[[Secondary]]*/ !important;
}

.well {
    background-color: /*[[Secondary]]*/ !important;
    border: 1px solid /*[[Border]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
}

.quickSearchResults, .resultItem {
    background-color: /*[[Secondary]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
    border-color: /*[[Border]]*/ !important;
}

.quickSearchResults {
    border: 1px solid /*[[Border]]*/ !important;
}

.well.perSub {
    background-color: /*[[Primary]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
    border-color: /*[[Border]]*/ !important;
}

hr {
    border-color: /*[[Border]]*/ !important;
}

.input-group-addon {
     background-color: /*[[Secondary]]*/ !important;
     color: /*[[Primary Text]]*/ !important;
     border-color: /*[[Border]]*/ !important;
}

a {
    color: /*[[Link]]*/ !important;
}

a:hover {
    text-decoration: underline !important;
}

body, html {
    background-color: /*[[Primary]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
}

button {
    background-color: /*[[Border]]*/ !important;
    border-color: /*[[Border]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
}

button.close {
    color: /*[[Primary Text]]*/ !important;
}

img {
    background-color: /*[[Secondary]]*/ !important;
    border: 1px solid /*[[Border]]*/ !important;
}

input, textarea, select, option {
    background-color: /*[[Secondary]]*/ !important;
    border-color: /*[[Border]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
}

.chapter-list .list-group-item,
.chapter-list .list-group-item .chapterLabel
 {
    background-color: /*[[Primary]]*/ !important;
    border-color: /*[[Border]]*/ !important;
    color: /*[[Link]]*/ !important;
}

.SeriesTime {
    color: /*[[Secondary Text]]*/ !important;
}
}
