@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nytimes.com") {

/*[[theme]]*/

::-moz-selection {
  color: var(--bg) !important;
  background: var(--fg-link) !important;
}
::selection {
  color: var(--bg) !important;
  background: var(--fg-link) !important;
}

/* ads
#app > div[class^="css-"]:not([data-reactroot]) {
    display: none !important
}*/

body, div, li[data-testid="mini-nav-item"] a:hover, footer, article, section, header, ul, li, nav, div[style*="background:"], h1, h2, h3, h4, h5, h6 {
    background-color: var(--bg) !important }
.subnavigation ol, .subnavigation ol a, .subnavigation ol li, form, form > div {
    background-color: var(--bg-2) !important }

body, p, span, ul, li, div, header, input {
    color: var(--fg) !important }
time {
    color: var(--fg-muted) !important }
h1, h2, h3, h4, h5, h6, span.balancedHeadline, h1 > a, h1 > a:link {
    color: var(--fg-2) !important }

a, a:link {
    color: var(--fg-link) !important }
a:hover, a:visited:hover {
    color: var(--fg-link-hover) !important }
a:visited {
    color: var(--fg-link-visited) !important }

main, div, article, footer, nav, ul, nav::before, nav::after, div::before, div::after, section, section::before, section::after, li, hr, time, header, ol, input, button, h1, h2, h3, h4, h5, h6 {
    border-color: #fff3 !important }
hr, section::before, section::after, li::before {
    background-color: #fff3 !important }
.css-1nwy9te {
    border-left-color: var(--fg) !important }

a:hover, a.active, a.selected, .media.video.promo::before {
    background-color: #fff1 !important }

.layout, .subnavigation ol, .stream .story-menu-options {
    background-image: none !important }

button {
    background-color: var(--bg-input) !important; color: var(--fg-input) !important }

div[data-testid="masthead-desktop-logo"], h2.branding, a[href="/"], .sprite-icon:not(.weather-icon):not(.ticker-icon), header > a[href="/"] {
    background-color: transparent !important;
    filter: invert(1) !important }
header > div > a[href="/"] {
    filter: none !important }

.ReactModal__Content div {
    background-color: transparent !important }

}
