css:
@-moz-document domain(twitter.com){
    
body,[dir="ltr"].logged-in{
        background-color: #ffedf9;
}
    
.global-nav-inner {
    background: #ffeaf7;
    height: 46px;
    border: 2px solid #ffc4e5;
    border-radius: 10px;
}
    
.global-nav {
    border-bottom: 1px solid rgba(0, 0, 0, 0);
}
    
.bird-topbar-etched {
    color: #ffb9ea;
}
    
.global-nav .search-input {
    background-color: #fff2fae6;
    border-radius: 21px;
    border: 2px solid #FFB9EA;
    color: #FFB9EA;
}
    
.global-nav .search-input::placeholder {
    color: #ffa3d9;
}
    
.global-nav .search-input::-moz-placeholder {
    color: #ffc4e7;
}
    
.DashboardProfileCard {
    border: 2px solid #FFB9EA;
    background: #fff5fb;
    border-radius: 10px;
}
    
.Trends {
    background-color: #FFF5FB;
    border: 2px solid #FFB9EA;
    border-radius: 10px;
}
    
.module ul:first-child li:first-child .list-link, .module .flex-module:first-child {
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
}

.flex-module {
    background-color: #FFF5FB;
    border: 2px solid #FFB9EA;
}
    
.module ul:last-child li:last-child .list-link, .module ul:last-child li.last-item .list-link, .module .list-link.last, .module .flex-module:last-child {
    border-bottom-right-radius: 10px;
    border-bottom-left-radius: 10px;
}
    
.module .flex-module {
    background-color: #FFF5FB;
    border: 2px solid #FFC4E5;
}
    
.wtf-module .UserSmallListItem {
    border-bottom: 2px solid #FFC4E5;
    padding-bottom: 8px;
}
    
.top-timeline-tweetbox .timeline-tweet-box {
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
    border-bottom-left-radius: 10px;
    border-bottom-right-radius: 10px;
    border: 2px solid #FFC4E5;
}
    
.top-timeline-tweetbox .tweet-user {
    border-radius: 10px;
    position: relative;
}
    
.new-tweets-bar {
    background-color: #fff7fc;
    border-bottom: 2px solid #FFC4E5;
    border-top: 1px solid #FFC4E5;
    border-left: 2px solid #ffc4e5;
    border-right: 2px solid #ffc4e5;
    border-radius: 10px;
}
    
.tweet {
    border-bottom: 2px solid #FFC4E5;
    cursor: pointer;
    border-radius: 10px;
}
    
.stream-item:not(.no-header-background-module) {
    background: #FFF5FB;
        background-clip: border-box;
    border-left: 2px solid #FFC4E5;
    border-right: 2px solid #FFC4E5;
    border-radius: 10px;
}
    
.tweet .context .pretty-link s, .tweet .context .pretty-link b {
    color: #FFB4DE;
}
    
.with-icn {
    font-size: 12px;
    color: #FFB4DE;
}
    
.ProfileTweet-actionButton, .ProfileTweet-actionButtonUndo {
    color: #FFC4E5;
}
    
.ProfileCardStats-statLabel {
    color: #FFC4E5;
}
    
.fullname {
    word-break: break-all;
    color: #FFC4E5;
}
    
.flex-module-inner, .flex-module-footer {
    color: #FFC4E5;
}
    
.flex-module-header h3 {
    color: #ffc4ec;
}
    
#global-tweet-dialog .modal-tweet .tweet:hover, #global-tweet-dialog .modal-tweet .tweet {
    background: #ffe6f2;
}
    
.modal-body p {
    color: #ff89cb;
}
    
.Footer-item, .Footer-link {
    color: #FFC4E5;
}
    
body {
    color: #ffa7e8;
}
    
}
