}
#mp-left, #mp-right, #mp-middle, #mp-lower, caption[style*="background"], div[style*="background-color:"] {
  background-color: var(--bg) !important;
  color: var(--fg) !important;
}
#header, .navbar, .popover, .popover-title, .popover-content, .dropdown-menu, #toc, .toc, .suggestions-results, .suggestions-special, .uls-menu, .language-settings-dialog, .infobox, .navbox, .navbox th, .navbox-subgroup .navbox-group, table.wikitable > * > tr > th, .toccolours, .cnotice, table.ombox, .referencetooltip li, .mw-search-profile-tabs, .oo-ui-menuSelectWidget, .mw-searchresults-has-iw .iw-resultset, .navbox-abovebelow, .mwe-popups, .oo-ui-tagMultiselectWidget.oo-ui-widget-enabled.oo-ui-tagMultiselectWidget-outlined .oo-ui-tagMultiselectWidget-handle, .mw-rcfilters-ui-changesListWrapperWidget .mw-changeslist-legend, .navbox-subgroup .navbox-abovebelow, #content .gallerybox div.thumb, #filetoc, .mw_metadata td, .mw-mmv-info-box, .wikiEditor-ui-toolbar, .client-js #preftoc li.selected a,.client-js #preferences, .navbox-subgroup .navbox-title, .header-container.header-chrome, nav, .hatnote, .dablink, .rellink, .ext-related-articles-card-list .ext-related-articles-card, .content table.infobox, footer .last-modified-bar, .overlay-header-container, .overlay-footer-container, .search-overlay .results, .language-searcher .panel, .list-header, .overlay.search-overlay, #mw-mf-page-left, #mw-mf-page-left ul li {
  background-color: var(--bg-2)
}
.mbox-small, .mw-body-content *[style*="background:#eee"], #mp-topbanner, ul[style*="background"], tr[style*="background"], .vertical-navbox, .messagebox, span[style*="background"], tr[bgcolor], table[style*="background"], .quotebox, h2[style*="background-color: #cedff2"], h2[style*="background-color:#cedff2"], th[style*="background"] {
