  background-color: var(--bg-2) !important
}
oddrow, .navbox-odd, #pagehistory li.selected, .navbox-title, .fileinfo-paramfield, .mw_metadata th, #mwe-popups-settings, .oo-ui-popupWidget-popup, .page-list li {
