}
.mw-body-content th[style*="background"], .mw-body-content td[style*="background"], div.NavFrame div.NavHead, div[style*="background:"], .language-searcher .site-link-list li {
  color: var(--fg) !important;
  background-color: var(--bg-odd) !important
}
input, input[type="text"], input[type="password"], input[type="number"], select, textarea, .form-control, #simpleSearch, #simpleSearch input, #languagesettings-panels .menu-section.active, #languagesettings-panels .menu-section.active:hover, #languagesettings-settings-panel, .oo-ui-textInputWidget input, .mw-ui-input, #mw-createaccount-join, .oo-ui-tagMultiselectWidget.oo-ui-widget-disabled .oo-ui-tagMultiselectWidget-handle, nav ul li, .client-js .toc-mobile, .mw-echo-ui-notificationItemWidget-unread, .oo-ui-tagMultiselectWidget.oo-ui-widget-enabled .oo-ui-tagMultiselectWidget-handle {
  background-color: var(--bg-input);
  color: var(--fg-input)
}
input, input[type="text"]:focus, input[type="password"]:focus, input[type="number"]:focus, select:focus, textarea:focus, .form-control:focus, #simpleSearch:focus, .settings-menu-items .menu-section:hover, #mw-createaccount-join:hover, nav ul li:hover, .mw-echo-ui-notificationItemWidget-unread:hover, .oo-ui-tagMultiselectWidget.oo-ui-widget-enabled .oo-ui-tagMultiselectWidget-handle:focus-within, ol.references li:target {
  background-color: var(--bg-input-hover);
  color: var(--fg-input-hover)
}
.search-box .search {
  background-color: var(--bg-input-hover) !important; color: var(--fg-input-hover) !important }
.uls-language-settings-close-block, .mw-echo-ui-crossWikiNotificationItemWidget-group, div.thumbinner {
  background-color: transparent
}
body, .navbar, input[type="text"], select, textarea, .form-control, div#mw-panel div.portal div.body, #mw-page-base, div.vectorTabs span, div.vectorTabs li.selected, div.vectorTabs ul, div.vectorTabs ul li, div.vectorTabs, #simpleSearch, .wikiEditor-ui-toolbar, .client-js #preftoc li, .client-js #preftoc li.selected a, .client-js #preftoc, .client-js .ambox .ambox-learn-more::before {
  background-image: none
}

body, .mw-body, .tocnumber, div.vectorTabs li.selected a, div.vectorTabs li.selected a:visited, .suggestions a.mw-searchSuggest-link, .suggestions-result, .suggestions a.mw-searchSuggest-link, .suggestions-special .special-query, .display-settings-block:hover .settings-text, div#footer ul li, .referencetooltip li, table.wikitable, .infobox, .mwe-popups .mwe-popups-extract, .mw-rcfilters-ui-filterTagMultiselectWidget-wrapper-content-title, .mw-mmv-post-image, .mw-mmv-permission-box .mw-mmv-permission-text, #mwe-popups-settings main form label > span, .page-list li > a:hover, .toctogglelabel, .mw-echo-ui-notificationItemWidget-content-message-header, .oo-ui-buttonElement-frameless.oo-ui-widget-enabled > .oo-ui-buttonElement-button, .mw-echo-ui-notificationItemWidget-content-actions-timestamp, .client-js .ambox, #mw-mf-page-left ul li a, #mw-mf-page-left ul li a:visited {
  color: var(--fg)
}
span[style*="color:black"] {
  color: var(--fg) !important
}
.brand, h1, h2, h3, h4, h5, .input-settings-block, .languagesettings-menu h1, .mw-search-result-data, .mw-mmv-permission-box h3, .client-js #preferences legend, .ra-read-more h2, .list-header {
  color: var(--fg-2)
}
.muted, div#mw-panel div.portal h3, #pt-anonuserpage, .suggestions-special .special-label, .settings-text, .results-info, #contentSub, .mw-mmv-credit, .mw-mmv-image-links li, .mw-mmv-image-desc, #mwe-popups-settings main p, .htmlform-tip, .content figcaption, .hatnote, .dablink, .rellink, .ext-related-articles-card-list .ext-related-articles-card-extract, footer .last-modified-bar, .page-list li > a, .language-searcher .site-link-list a .title, .content .thumbcaption {
  color: var(--fg-muted)
}
*[style*="color:#555"] {
  color: var(--fg-muted) !important
}

a, a:link, div#mw-panel div.portal div.body ul li a, div.vectorTabs li a, .CategoryTreeToggle, nav ul.hlist li a, .ext-related-articles-card-list h3 a, .page-summary h3, .oo-ui-buttonElement-frameless.oo-ui-widget-enabled > .oo-ui-buttonElement-button:hover, .language-searcher .site-link-list a .autonym, .client-js .ambox .ambox-learn-more, #mw-mf-page-left ul.hlist li a {
  color: var(--fg-link)
}
.mw-body-content a, .mw-body-content a:link, .mw-body-content a *, .CategoryTreeToggle:hover {
  color: var(--fg-link) !important;
}
a:hover, a:visited:hover, div#mw-panel div.portal div.body ul li a:hover, div.vectorTabs li a:hover, nav ul.hlist li a:hover, .page-summary:hover h3 {
  color: var(--fg-link-hover)
}
.mw-body-content a:hover, .mw-body-content a:link:hover, .mw-body-content a:hover *, .wb-langlinks-link > a:hover {
  color: var(--fg-link-hover) !important;
}
a:visited, div#mw-panel div.portal div.body ul li a:visited {
  color: var(--fg-link-visited)
}
.mw-body-content a:visited, .mw-body-content a:link:visited, .mw-body-content a:visited * {
  color: var(--fg-link-visited) !important;
}
a.new, .mw-body-content a.new, div.vectorTabs li.new a, .searchdidyoumean, .mw-plusminus-neg {
  color: #f33 !important;
}
a.new:hover, .mw-body-content a.new:hover, div.vectorTabs li.new a:hover {
  color: #f55 !important;
}
.mw-plusminus-pos {
  color: #3f3;
}

noborder, .navbar, .mw-body, #toc, .toc, table.fmbox-system, .language-settings-buttons, .mw-body-content *[style*="border"], .infobox, div.thumbinner, html .thumbimage, .navbox, .catlinks, .toccolours, table.tmbox-notice, .cnotice, img.thumbborder, table.ombox, .mw-search-profile-tabs, .mw-rcfilters-ui-changesListWrapperWidget .mw-changeslist-legend, #filetoc, .mw-mmv-image-metadata, .mw-mmv-info-box {
  border: none !important
}
.wikiEditor-ui-toolbar, .wikiEditor-ui .wikiEditor-ui-top, .wikiEditor-ui .wikiEditor-ui-view, .client-js #preferences, .client-js #preferences fieldset, fieldset, nav ul li, .oo-ui-popupWidget-popup, .mw-echo-ui-notificationItemWidget:last-child {
  border: rgba(255, 255, 255, 0.1) }
table.ambox {
  border-top: none; border-right: none; border-bottom: none;
}
.referencetooltip li {
  border-top: none; border-left: none; border-right: none;
}
custom-border {
  border: var(--border)
}
input, input[type="number"], select, textarea, h1, h2, h3, h4, h5, #simpleSearch, hr, tr + tr > .navbox-group, table.wikitable > * > tr > th, table.wikitable > * > tr > td, .mw-ui-input, #pagehistory li, #pagehistory li.selected, .infobox.bordered td, .infobox.bordered th, tr + tr > .navbox-abovebelow, tr + tr > .navbox-list, #mwe-popups-settings, #mwe-popups-settings header, #page-actions, .content .mw-parser-output > h2, .content .section-heading, .client-js .toc-mobile, footer > .post-content > h2, .page-list li, .oo-ui-tagMultiselectWidget-handle, .page-actions-menu, .search-box .search, .overlay .panel, blockquote, .overlay.search-overlay .search-content, .mw-parser-output .quotebox, #mw-mf-page-left ul li {
  border-color: var(--border-color)
}
table, table td, table th {
  border-color: var(--border-color) !important
}
#simpleSearch:hover, .mw-ui-input:hover, .oo-ui-textInputWidget.oo-ui-widget-enabled input:focus, .oo-ui-textInputWidget.oo-ui-widget-enabled input:hover, .oo-ui-textInputWidget.oo-ui-widget-enabled input:focus:hover {
  border-color: var(--fg-link);
}
.language-searcher .site-link-list a {
  border-color: #fff1;
}
.referencetooltip li, li.gallerybox div.thumb, footer .last-modified-bar,.minerva-footer, .header-container {
  border-color: transparent
}
table.ambox-content, table.ambox-style, table.ambox {
  border-left-color: #f28500 !important
}
hr, #mw-mf-page-left ul li a:hover {
  background-color: var(--border-color)
}

.mw-ui-input, .oo-ui-textInputWidget.oo-ui-widget-enabled input:focus, .oo-ui-textInputWidget.oo-ui-widget-enabled input:hover, #mw-mf-page-left ul li a:hover {
  box-shadow: none
}
th[style*="box-shadow"], table.metadata table {
  box-shadow: none !important
}
.referencetooltip li, .toc, #toc, .mw-body-content table, .thumb, div#mw-panel, #mp-topbanner, #mp-otd-img, #mp-tfl-img, .dyk-img, #mp-tfa-img, .itn-img, #filetoc {
  box-shadow: 0 0 5px rgba(0, 0, 0, 0.5)
}

success, success a {
  background-color: var(--bg-success);
  color: var(--fg-success)
}
info, info a {
  background-color: var(--bg-info);
  color: var(--fg-info)
}
warning, warning a {
  background-color: var(--bg-warning);
  color: var(--fg-warning)
}
error, error a {
  background-color: var(--bg-error);
  color: var(--fg-error)
}
disabled, .table-na {
  background-color: var(--bg-disabled) !important;
  color: var(--fg-disabled) !important
}
.suggestions-result-current, a.button, .oo-ui-buttonElement-framed.oo-ui-widget-enabled > .oo-ui-buttonElement-button {
  background-color: var(--fg-link);
  color: var(--bg)
}
.mw-ui-button, .mw-ui-button:hover, .mw-ui-button:focus, input[type="submit"] {
  background-color: var(--fg-link);
  color: var(--bg);
  border: none;
  box-shadow: 0 0 3px rgba(0,0,0, 0.5);
}
.mw-ui-button:hover, .mw-ui-button:focus, input[type="submit"]:hover, .oo-ui-buttonElement-framed.oo-ui-widget-enabled > .oo-ui-buttonElement-button:hover {
  background-color: var(--fg-link-hover);
}

div.mw-warning-with-logexcerpt, table.ambox-content, table.ambox, .client-js .ambox {
  background-color: rgba(200, 50, 50, 0.5)
}
table.tmbox, table.tmbox td[style*="background-color"], table.compact-ambox, table.ambox-style, table.ambox-content, table.ambox, table.ambox div {
  background-color: rgba(100, 50, 0, 1) !important
}

pre, code, .mw-highlight {
  background-color: var(--syntax-bg);
  border: solid 0.5px rgba(255,255,255,0.2);
}
.mw-highlight .hll {
  background-color: var(--syntax-bg-2);
}
pre, code, .mw-highlight, .mw-highlight .o, .mw-highlight .go {
  color: var(--syntax-fg)
}
.mw-highlight .nl, .mw-highlight .cs {
  color: var(--syntax-fg-2)
}
.mw-highlight .nd, .mw-highlight .kc, .mw-highlight .kd, .mw-highlight .nv, .mw-highlight .nf, .mw-highlight .nc, .mw-highlight .kp { color: var(--syntax-function) }
.mw-highlight .nt, .mw-highlight .nn, .mw-highlight .kt, .mw-highlight .kr, .mw-highlight .bp, .mw-highlight .ow, .mw-highlight .dl, .mw-highlight .no, .mw-highlight .vi, .mw-highlight .kn { color: var(--syntax-builtin) }
.mw-highlight .si, .mw-highlight .k, .mw-highlight .na, .mw-highlight .gp, .mw-highlight .nb { color: var(--syntax-keyword) }
.mw-highlight .s, .mw-highlight .sx, .mw-highlight .s1, .mw-highlight .s2, .mw-highlight .sh, .mw-highlight .ss, .mw-highlight .sd { color: var(--syntax-string) }
.mw-highlight .cp, .mw-highlight .c1, .mw-highlight .cm, .mw-highlight .c { color: var(--syntax-comment) }
.mw-highlight .m, .mw-highlight .mh, .mw-highlight .mi { color: var(--syntax-number) }

ul {
  list-style-image: url("data:image/svg+xml,%3Csvg xmlns=%22http://www.w3.org/2000/svg%22 width=%225%22 height=%2213%22%3E %3Ccircle cx=%222.5%22 cy=%229.5%22 r=%222.5%22 fill=%22%23999999%22/%3E %3C/svg%3E");
}

.mwe-math-element, img[src*=".svg"][alt*="composition"], .oo-ui-icon-search, .oo-ui-icon-menu, .oo-ui-icon-article, .oo-ui-icon-tag, .oo-ui-icon-trash,.mw-ui-icon-popups-settings::before, .mw-ui-icon-popups-close::before, #pt-notifications-alert .mw-echo-notifications-badge::before, #pt-notifications-notice .mw-echo-notifications-badge::before, .oo-ui-iconElement-icon {
  filter: invert(1)
}
img[src*="Wiktionary-logo"], .mw-ui-icon-mf-arrow::before, .mw-ui-icon-minerva-edit-enabled::before, .mw-ui-icon-minerva-language-switcher::before, .mw-ui-icon-mf-watch::before {
  filter: saturate(200%) invert(50%) brightness(200%)
}

.mw-wiki-logo {
  filter: invert(100%)
}

#mw-mf-main-menu-button, img[alt="Wikipedia"] {
  filter: brightness(3) }

#mw-page-base {
  max-height: 0px;
}
#mw-navigation > div#mw-head {
  background-color: var(--bg-2);
  background-image: linear-gradient(0deg, var(--bg-2) 10%, var(--bg) 90%, var(--bg));
  position: absolute !important;
  margin-top:-45px !important;
  box-shadow: 0 0 10px rgba(0,0,0,0.5);
  transition: margin-top 0.3s ease-in-out;
  z-index: 1000 }
#content {
  margin-top: 25px }
#p-personal {
    display: none }

div#mw-panel {
  background-image: linear-gradient(270deg, var(--bg-2) 10%, var(--bg) 90%, var(--bg));
  left: -9.5em;
  transition: left 0.3s ease-in-out;
  z-index: 999;
}
div#mw-panel:hover {
  left: 0px;
}

.mw-body {
  text-shadow: 0.3px 0.3px rgba(0,0,0,0.5);
  margin-left: 1em;
  margin-right: 1em;
}

.metadata.stub {
  margin-top: 5px;
  background-color: var(--bg) !important;
}

::selection {
  background-color: var(--fg-link);
  color: var(--bg);
  text-shadow: none;
}

::-moz-selection {
  background-color: var(--fg-link);
  color: var(--bg);
  text-shadow: none;
}

#frb-inline, #frb-nag, #Inspire_Community_Health {
  display: none }

.mwe-popups .mwe-popups-extract[dir="ltr"]::after, .ext-related-articles-card-list h3::after {
  background-image: linear-gradient(to right,#0000,var(--bg-2) 50%) }
.mwe-popups.flipped-x-y::after, .mwe-popups.flipped-x-y::before {
  border-top-color: var(--bg-2) }
.mwe-popups-settings-icon:hover {
  background-color: var(--bg-input-hover) }

.mwe-popups-overlay, .oo-ui-windowManager-modal > .oo-ui-dialog {
  background-color: rgba(0, 0, 0, 0.2) }

td[style="background:lightblue;"] {
  background-color: #4af3 !important }
td[style="background:#cff;"] {
  background-color: #4af5 !important }

kbd[style] {
  background-color: var(--syntax-bg) !important; color: var(--syntax-fg) !important; background-image: none !important; border-color: #fff1 !important }

.mf-section-0 div[style*="background:"] {
  background-color: transparent !important }

}
