css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hn.premii.com") {
  /*[[theme]]*/
  
  ::-moz-selection {
    color: var(--bg) !important;
    background: var(--fg-link) !important;
  }
  ::selection {
    color: var(--bg) !important;
    background: var(--fg-link) !important;
  }

body, .theme-bg-lighter, .theme-bg-lighter-dark {
  background-color: var(--bg) }
.header-container, .bubble-icon, .page-stories .bd .points, .page-article-content .article-content th {
  background-color: var(--bg-2) }
oddrow, .theme-bg, .page-stories .active, .page-stories .bd .active a.theme-bg-lighter-dark, .page-article-content .article-content tr:nth-child(2n) {
  background-color: var(--bg-odd) }
input[type="text"], input[type="password"], select, textarea, .form-control {
  background-color: var(--bg-input);
  color: var(--fg-input) }
input[type="text"]:focus, input[type="password"]:focus, select:focus, textarea:focus, .form-control:focus {
  background-color: var(--bg-input-hover);
  color: var(--fg-input-hover) }
body, .navbar, input[type="text"], select, textarea, .form-control {
  background-image: none }

body, .header .icon, .header a:link, .page-stories .bd a, .story h3 {
  color: var(--fg) }
.brand, h1, h2, h3, h4, h5, .article-comments .author {
  color: var(--fg-2) }
.muted, .article-comments .comment-meta, .article-comments time {
  color: var(--fg-muted) }

a, a:link, a, .theme-color, .alt-color, .story:hover h3 {
  color: var(--fg-link) }
a:hover, a:visited:hover {
  color: var(--fg-link-hover) }
a:visited {
  color: var(--fg-link-visited) }

noborder, .navbar {
  border: none }
custom-border {
  border: var(--border) }
input, select, textarea, .theme-border, .page-options .options-action .selected, .page-article-content .article-content table {
  border-color: var(--border-color) }
.bubble-icon::after {
  border-top-color: var(--bg-2) }
.article-comments .kids .kids {
  border-left-color: var(--bg-odd) }
.comment-meta, .list-line li, .hairlines .page-stories .bd li {
  border-bottom: solid 0.5px rgba(255, 255, 255, 0.1) }

.article-comments .selected > .comment, .page-stories .active {
  box-shadow: 3px 0px 0px var(--border-color) inset }

success, success a {
  background-color: var(--bg-success);
  color: var(--fg-success) }
info, info a {
  background-color: var(--bg-info);
  color: var(--fg-info) }
warning, warning a {
  background-color: var(--bg-warning);
  color: var(--fg-warning) }
error, error a {
  background-color: var(--bg-error);
  color: var(--fg-error) }
disabled {
  background-color: var(--bg-disabled);
  color: var(--fg-disabled) }

pre {
  border-radius: 5px;
  padding: 5px;
  background-color: var(--syntax-bg);
  border: none }
pre {
  color: var(--syntax-fg) }

body {
  text-shadow: 0.8px 0.8px 0.4px rgba(0, 0, 0, 0.5) }

.article-content {
  margin: 0% 10% }

.article-content img {
  border-radius: 20px;
  opacity: 0.5;
  transition: opacity 0.3s ease-in-out; }
.article-content img:hover {
  opacity: 1 }

}
