css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tampermonkey.net") {

/*[[theme]]*/

::-moz-selection {
  color: var(--bg) !important;
  background: var(--fg-link) !important;
}
::selection {
  color: var(--bg) !important;
  background: var(--fg-link) !important;
}

html, body, .tv_contents, .tv_content, .section, .tv_tab.tv_selected, .tv_tab.tv_selected:hover, .curbg, .shariff .info a, .curwaitmsg {
  background-color: var(--bg) !important }
header, .head_container, .footer, .navbar .inner, .dropdown li, .panel, .download .tile, div[style*="background-color"], .bg1, .dropdown .dropdown-contents {
  background-color: var(--bg-2) !important }
oddrow, .tr_tabs_alt, .subsection > h3, .nice_table tbody > tr:nth-child(2n+1) > td, .bg2, .jumpbox-sub-link, ul.navlinks {
  background-color: var(--bg-odd) !important }
input, textarea, select, .tv_tab, .searchbox_mv, .page-header-metro .navbar .nav-main-metro-tabs li.responsive-menu a.responsive-menu-link, ul.forums, .stat-block p, .codebox {
  background-color: var(--bg-input) !important;
  color: var(--fg-input) !important }
input:focus, select:focus, textarea:focus, select:focus, .tv_tab:hover,.tv_tab:focus, .page-header-metro .navbar .nav-main-metro-tabs li.responsive-menu a.responsive-menu-link:hover, li.row:hover {
  background-color: var(--bg-input-hover) !important;
  color: var(--fg-input-hover) !important }
blockquote {
  background-color: rgba(0, 0, 0, 0.2) !important }
#container2, .navbar, .navbar-metro .inner {
  background-color: transparent !important }
nobg {
  background-image: none !important }

html, body, table, .content, .panel, dd label, .social-icons a:link, .social-icons h2, li.row strong, .download div, .curtext, .postprofile strong, .pagination li.ellipsis span {
  color: var(--fg) !important }
#header, h1, h2, h3, h4, h5, .codetitle, i[style="color:green;"], span[style*="color:"], fieldset dl:hover dt label {
  color: var(--fg-2) !important }
.muted, .panel dl, .copyright, ul.topiclist li, .download .hint, .postbody, .postprofile {
  color: var(--fg-muted) !important }
.shariff a:link {
  color: #fff !important }

a, a:link, .icon.icon-blue {
  color: var(--fg-link) !important }
a:hover, a:visited:hover, a:hover .icon.icon-blue {
  color: var(--fg-link-hover) !important }
a:visited {
  color: var(--fg-link-visited) !important }

noborder, pre code, select, blockquote, .codetitle, .cookie_hint, .panel, li.row, .stat-block p, .shariff .info, .post, .codebox, .postlink:hover {
  border: none !important }
button, custom-border, input, textarea, pre, code {
  border: var(--border) !important }
bordercolor, h1, h2, h3, h4, h5, hr, .navbar ul#nav-footer.linklist, .download ul li + li, .postprofile, .postbody .content, .codebox p, .signature {
  border-color: var(--border-color) !important }
.postlink {
  border-color: var(--fg-link) !important }
.page-header-metro .navbar .nav-main-metro-tabs, .page-header-metro .navbar .nav-main-metro-tabs > li {
  border-color: transparent !important }
.dropdown-menu .divider, hr, .pagination li a {
  background-color: var(--border-color) !important }

noshadow {
  box-shadow: none !important }
baseshadow, .panel, li.row, .curcontainer {
  box-shadow: 0 0 5px #000 !important }

body {
  text-shadow: 0.3px 0.3px 0.5px rgba(0, 0, 0, 0.5) !important }

success, success a {
  background-color: var(--bg-success) !important;
  color: var(--fg-success) !important }
info, info a, .alert-info, .cookie_hint {
  background-color: var(--bg-info) !important;
  color: var(--fg-info) !important }
warning, warning a, .alert-warning, .posthilit {
  background-color: var(--bg-warning) !important;
  color: var(--fg-warning) !important }
error, error a, .alert-danger {
  background-color: var(--bg-error) !important;
  color: var(--fg-error) !important }
disabled, input[readonly] {
  background-color: var(--bg-disabled) !important;
  color: var(--fg-disabled) !important }
option:checked {
  background-color: var(--fg-link) !important;
  color: var(--bg) !important; }

.button, button, .btn, input[type="submit"], .header, div.back-to-top, .cookie_hint a, .dropdown li, a.button2, .jumpbox-cat-link, .pagination li.active span {
  color: var(--fg-input) !important;
  background-image: linear-gradient(var(--bg-input-hover), var(--bg-input)) !important;
  border-color: var(--border-color) !important;
  text-shadow: 0.3px 0.3px 0.5px #000 !important }

.button:active, .button:focus, button:active, .btn.active,  input[type="submit"]:active, div.back-to-top:hover, .cookie_hint a:hover,.responsive-menu .dropdown .dropdown-contents > li > a:hover, a.button2:active, .jumpbox-cat-link:hover {
  background-image: linear-gradient(var(--bg-input), var(--bg-input-hover)) !important }

.btn:hover, .btn:focus,  input[type="submit"]:focus, input[type="submit"]:hover {
  background-color: var(--bg-input) !important ;
  border-color: var(--border-color) !important;
  color: var(--fg-input-hover) !important }

pre, code {
  background-color: var(--syntax-bg) !important }
pre, code, .prettyprint, .nocode, .pln { color: var(--syntax-fg) !important }
.kwd, .tag, .pun, .linenums .tag { color: var(--syntax-builtin) !important }
.atn { color: var(--syntax-keyword) !important }
.dec, .typ { color: var(--syntax-function) !important }
.atv, .str { color: var(--syntax-string) !important }
.num, .lit { color: var(--syntax-number) !important }
.com { color: var(--syntax-comment) !important }

.adventing, .ad-changelog-block { display: none }

img.avatar[src*="avatar=2_1346226864.gif"] {
  display: none !important }

#user_chart_div, #reviewchart_div, #betachart_div, .site_logo, .sprite-images-icon48 {
  filter: invert(100%) }

.curbg {
  opacity: 0.5 !important }

#nav-breadcrumbs *, .hint {
  opacity: 1.0 !important }

}
