css:
@-moz-document domain('juick.com') {
    html {
        background: #eaeadf;
    }
    body > header,
    .header--background {
        background: #f2f2ec;
    }
    #column li > a:hover {
        background-color: #f2f2ec;
    }
    #column .inp {
        background: #F5F5E9;
    }
    #column .tags {
        background: #eaeadf;
    }
    #column .tags h4 {
        background: #f2f2ec;
        border-width: 0;
    }
    #global a,
    .page a
    {
        color: #069;
    }
    .msg-tags > a,
    article .tags > a {
        background: #eee;
        padding: 0 10px 3px 10px;
    }
    span.msg-tags {
        display: block;
        margin-top: unset;
        margin-bottom: 8px;
    }
    .title2 {
        background: #f2f2ec;
    }
    #content .title2 h2 {
        font-size: unset;
    }
    .msg-ts {
        float: right;
    }
    .msg-media img {
        max-width: 100%;
    }
}
