css:
/* ez pz 2 code */

body {
    -webkit-animation: fadein /*[[BTime]]*/s !important;
    -moz-animation: fadein /*[[BTime]]*/s;
    -ms-animation: fadein /*[[BTime]]*/s;
    -o-animation: fadein /*[[BTime]]*/s;
    animation: fadein /*[[BTime]]*/s;
}
body {
    -webkit-animation: blur /*[[BTime]]*/s !important;
    -moz-animation: blur /*[[BTime]]*/s !important;
    -ms-animation: blur /*[[BTime]]*/s !important;
    -o-animation: blur /*[[BTime]]*/s !important;
    animation: blur /*[[BTime]]*/s !important;
}
@keyframes blur {
    from {
        filter: blur(/*[[BSize]]*/px);
    }
    to {
        filter: blur(0px)
    }
}
