css:
@-moz-document url-prefix("https://www.roblox.com") {
.btn-primary-lg,
.btn-control-md,
.btn-secondary-md,
.btn-secondary-xs,
.btn-buy-md
{
    border-radius: 0px;
    transition: 200ms ease-in-out;
}

.btn-buy-md {
    border-color: #FFFFFF;
    border: 0px solid transparent;
    transition: 200ms ease-in-out;
}

.btn-buy-md:hover {
    border-color: #FFFFFF;
    border: 0px solid transparent;
    transition: 200ms ease-in-out;
}

.store-card .store-card-caption {
    border-top: 0px solid #B8B8B8;
    padding: 0 0px 0px;
}

.list-item .real-game-pass,
.store-card-footer h5 {
    border: 0px solid transparent;
    border-radius: 0px;
}

.store-card-footer h5 {
    background-color: rgb(63, 198, 121);
    color: #FFFFFF !important;
    padding: 9px 18px 9px;
}

.input-field,
.input-field:focus {
    transition: 200ms ease-in-out;
    border-radius: 0px;
}
}
