@-moz-document domain("discord.com")
{
:root {
    --header-primary: #fff;
    --header-secondary: #212121;
    --text-normal: #dcddde;
    --text-muted: #72767d;
    --text-link: #00b0f4;
    --channels-default: #8e9297;
    --interactive-normal: #b9bbbe;
    --interactive-hover: #dcddde;
    --interactive-active: #fff;
    --interactive-muted: #4f545c;
    --background-primary: rgba(0,0,0,0);
    --background-secondary: rgba(0,0,0,0);
    --background-secondary-alt: rgba(0,0,0,0.5);
    --background-tertiary: rgba(0,0,0,0);
    --background-accent: #4f545c;
    --background-floating: #212121;
    --background-mobile-primary: #212121;
    --background-mobile-secondary: #212121;
    --background-modifier-hover: rgba(79, 84, 92, 0.16);
    --background-modifier-active: rgba(79, 84, 92, 0.24);
    --background-modifier-selected: rgba(79, 84, 92, 0.32);
    --background-modifier-accent: hsla(0, 0%, 100%, 0.06);
    --background-mentioned: rgba(250, 166, 26, 0.05);
    --background-mentioned-hover: rgba(250, 166, 26, 0.08);
    --background-message-hover: rgba(4, 4, 5, 0.07);
    --background-help-warning: rgba(250, 166, 26, 0.1);
    --background-help-info: rgba(0, 176, 244, 0.1);
    --scrollbar-thin-thumb: #202225;
    --scrollbar-thin-track: transparent;
    --scrollbar-auto-thumb: #202225;
    --scrollbar-auto-track: #2e3338;
    --scrollbar-auto-scrollbar-color-thumb: #202225;
    --scrollbar-auto-scrollbar-color-track: #2f3136;
    --elevation-stroke: 0 0 0 1px rgba(4, 4, 5, 0.15);
    --elevation-low: 0 1px 0 rgba(4, 4, 5, 0.2), 0 1.5px 0 rgba(6, 6, 7, 0.05), 0 2px 0 rgba(4, 4, 5, 0.05);
    --elevation-medium: 0 4px 4px rgba(0, 0, 0, 0.16);
    --elevation-high: 0 8px 16px rgba(0, 0, 0, 0.24);
    --logo-primary: #fff;
    --focus-primary: #00b0f4;
    --guild-header-text-shadow: 0 1px 1px rgba(0, 0, 0, 0.4);
    -channeltextarea-background: rgba(0,0,0,0);
    --activity-card-background: #212121;
    --textbox-markdown-syntax: #8e9297;
    --deprecated-card-bg: rgba(32, 34, 37, 0.6);
    --deprecated-card-editable-bg: rgba(32, 34, 37, 0.3);
    --deprecated-store-bg: #36393f;
    --deprecated-quickswitcher-input-background: #72767d;
    --deprecated-quickswitcher-input-placeholder: hsla(0, 0%, 100%, 0.3);
    --deprecated-text-input-bg: rgba(0, 0, 0, 0.1);
    --deprecated-text-input-border: rgba(0, 0, 0, 0.3);
    --deprecated-text-input-border-hover: #040405;
    --deprecated-text-input-border-disabled: #202225;
    --deprecated-text-input-prefix: #dcddde;
}
/* Scrollbars */
body {
    overflow: overlay;
}
::-webkit-scrollbar-track-piece{
    background: rgba(0,0,0,0) !important;
    border:0 !important;
}
::-webkit-scrollbar-thumb {
    background:#212121 !important;
}

body {
    font-family:"Fira Sans",sans-serif;
}
body{
    background-color: rgba(0,0,0,0.85) !important;
    background-repeat:no-repeat;
    background-position:center center;
    background-image: url(/*[[image]]*/) !important;
    background-size: cover !important;
}
#app-mount {
    background-color:rgba(0,0,0,0.6);
}


.da-titleBar
{
height: 32px;
}

.da-wordmark
{
margin-top: 5px;
}
.titleBar-3_fDwJ .wordmark-2L03Wr
{
display: none;
}
.titleBar-3_fDwJ
{
height: 32px !important;
margin-top: 0 !important;
}
.da-winButtonClose:hover,
.da-winButtonMinMax:hover
{
background-color: transparent;
}

.typeWindows-1za-n7 {
    margin-top:0;
    padding: 4px;
}

.da-winButtonMinMax, .da-winButtonClose
{
margin: 8px;
}
@font-face {
    font-family: 'Fira Sans';
    src: local('Fira Sans Regular'), local('FiraSans-Regular'),
        url(data:application/font-woff2;charset=utf-8;base64,) format('woff2');
    font-weight: normal;
    font-style: normal;
}


}
