css:
@-moz-document domain("www.youtube.com") {
ytd-multi-page-menu-renderer[menu-style="multi-page-menu-style-type-notifications"] {height: 95em;}
#contentWrapper ytd-multi-page-menu-renderer {max-height: none!important}


/* Scrollbar Chrome*/
::-webkit-scrollbar{width:10px!important;height:10px!important;background:#1e1e1e!important;border:1px solid #252525!important}::-webkit-scrollbar-button{display:none!important}::-webkit-scrollbar-thumb,::-webkit-scrollbar-track{border:0!important;box-shadow:none!important}::-webkit-scrollbar-thumb{min-height:28px!important;background:#333!important}::-webkit-scrollbar-corner,::-webkit-scrollbar-track{background:#1e1e1e!important}
}
