css:
@-moz-document domain('fast.com') {

/*[[color]]*/

/*[[hideclutter]]*/


html,
body {
    background-color: /*[[background]]*/;
}
.speed-results-container {
    color: /*[[resultcolor]]*/;
}
.speed-units-container.succeeded {
    color: /*[[resultcolor]]*/;
}
.speed-units-container {
    color: /*[[resultcolor]]*/;
}
.speed-progress-indicator.succeeded {
    color: /*[[resultcolor]]*/;
    border-color: /*[[maintext]]*/;
}
.your-speed-message {
    color: /*[[resultcolor]]*/;
}
.speed-progress-indicator.in-progress > .spinner {
    box-shadow: 0 .4vh 0 0 /*[[maintext]]*/;
}
.speed-progress-indicator.stopped {
    border-color: white;
}
.speed-progress-indicator.in-progress {
    border-color: /*[[background]]*/;
}
a:hover {
    color: /*[[maintext]]*/;
}
a:hover .oc-icon-fb {
    color: /*[[maintext]]*/;
}
a:hover .oc-icon-tw {
    color: /*[[maintext]]*/;
}
.help-content-container {
    color: /*[[resultcolor]]*/;
}
.dropbtn {
    color: #4a4a4a;
    background-color: /*[[background]]*/;
    border: 1px solid /*[[maintext]]*/;
    border-radius: 4px;
}
.usage-info-container {
    border: none;
}
.measurement-label {
border-bottom: none;
}
.extra-details-container.succeeded {
    color: /*[[resultcolor]]*/;
}
.extra-measurement-container.succeeded {
    color: /*[[resultcolor]]*/;
}
.test-config-container {
    color: /*[[maintext]]*/;
}
.measurement-details-label.succeeded {
    color: /*[[resultcolor]]*/;
}
.extra-measurement-value-container.succeeded {
   color: /*[[maintext]]*/;
}
.dropbtn {
    border: none;
}
.test-config-btn:hover {
color: /*[[maintext]]*/ !important;
border-color: /*[[maintext]]*/;
}
.more-info-link:hover {
color: /*[[maintext]]*/ !important;
border-color: /*[[maintext]]*/;
}
.speed-results-container.succeeded {
color: /*[[resultcolor]]*/;
}
}
