css:
@-moz-document domain("jeuxvideo.com") {
/****************
     Principal
****************/  

/* Suppression de la marge en haut du site */
#content {
   margin-top: 54px !important;
}

/* Suppression du bloc machine */
.jv-header-bottom, .jv-header-sticky {
   height: 0px !important;
}
   
/* Largeur du menu */
.jv-header-top .jv-header-container {
    width: 980px !important;
} 
   
/* Logo qui passe par dessus le reste pour qu'il reste cliquable */
.logo-link {
    position: relative !important;
    z-index: 100000 !important;
}

/****************
     MENU
****************/

/* Suppression du menu machine */
.nav-container.nav-platform {
   display:none !important;
}
   
/* Décalement du menu vers la gauche */
.jv-nav-lvl1{
    margin-left: 90px !important;
}
   
/* Ajustement de la taille de police dans le menu */
.nav-primary .nav-lvl1-item > .nav-link {
    font-size: 16px  !important;
}
   
/* Suppresion du bouton store */
.jv-nav-primary .jv-nav-lvl1-item:last-child {
   display: none !important;
}
   
/* Ajustement de la hauteur de ligne dans le menu */
.nav-primary .nav-lvl1-item > .nav-link {
    line-height: 47px !important;
}
  


/*[[0]]*/


.panel-jv-forum > .panel-heading 
  {
    background-color: #C00000;
  }

.bloc-rech-forum
{
    margin-bottom: -100px;
    background-color: #F2F2F2;
}

.form-rech-forum
{
    position:relative; top:-48px;
}

.btn-actu-new-list-forum.btn, .btn-actualiser-forum.btn-actu-new-list-forum.btn {
    position: relative !important;
    z-index: 100000 !important;
}

.jv-nav-lvl1-item-more, .jv-nav-platform.jv-nav-container {
    display : none !important
}
}

@-moz-document url("http://www.jeuxvideo.com/"), url-prefix("http://www.jeuxvideo.com/profil/") {
/* Marge haute sur la page des profils */
#content {
   margin-top: 54px !important;
}
}

@-moz-document url-prefix("http://www.jeuxvideo.com/profil/") {
.container-content {
    opacity: 0.85 !important;
  }
  
#header-profil {
  background: rgba(82, 82, 82, 0.65) !important;
  z-index: 1 !important;
}
}
