css:
@-moz-document regexp("^https?://news\\.google(\\.com?)?(\\.[a-z]{2,3})?.*$"), regexp("^https://notifications.google.com(\\.com?)?(\\.[a-z]{2,3})?/.*$") {
/*[[theme]]*/

/* Selection */
::-moz-selection { color: var(--bg) !important; background: var(--fg-link) !important; text-shadow: none }
::selection { color: var(--bg) !important; background: var(--fg-link) !important; text-shadow: none }
/* Scrollbars */
::-moz-scrollbar{background-color:var(--bg-2) !important;width:10px !important}
::-moz-scrollbar-track{background-color:var(--bg-2) !important}
::-moz-scrollbar-button{display:none !important}
::-moz-scrollbar-thumb{background-color:var(--fg-link) !important;border-radius:4px !important;border:none !important }
::-moz-scrollbar-thumb:hover{background-color:var(--fg-link-hover) !important}
::-webkit-scrollbar{background-color:var(--bg-2) !important;width:10px !important}
::-webkit-scrollbar-track{background-color:var(--bg-2) !important}
::-webkit-scrollbar-button{display:none !important}
::-webkit-scrollbar-thumb{background-color:var(--fg-link) !important;border-radius:4px !important;border:none !important }
::-webkit-scrollbar-thumb:hover{background-color:var(--fg-link-hover) !important}

/* Notification Bar */
.gV6pJf, .gb_fa, .gb_ia, .gb_Z:hover .gb_3 {
  background-color: var(--bg-odd) !important }
.fEjTrb {
  background-color: var(--bg-input) !important }
.gMG7Fd, .wh73ab, .Vz68Kb, .gb_3, .gb_fa {
  color: var(--fg) !important }
.XSVuue, .iWO9fc .mUbCce svg, .RFF2xb svg, .ajgU6, .gb_Ib {
  fill: var(--fg-2) !important }
[aria-checked="true"] > .hh4xKf, .gb_Z:hover a, .gb_ka {
  border-color: var(--fg-link) !important }
[aria-checked="true"] .E7QdY {
  border-color: var(--fg-link-hover) !important }

/* News */
body, .zQTmif {
  background-color: var(--bg) }
.gb_de, .YDRMcc {
  background-color: var(--bg-2) }
.JPdR6b, .VOEIyf .jBmls, .g3VIld {
  background-color: var(--bg-odd) }
.zWHNCf .SFllF.KKjvXb, .RF3lAd, .WwvKTc, .w7qIhd, .dIehj::before, .TRHLAc, .z80M1.FwR7Pc {
  background-color: var(--fg-link) }
.uUmIDd, .oKubKe[aria-selected="true"] {
  background-color: rgba(255, 255, 255, 0.1) }
.gb_Qe {
  background-color: var(--bg-input) }
.gb_Qe.gb_8e {
  background-color: var(--bg-input-hover) }

body, .gb_Xa, .dSva6b, .zWHNCf, .MQsxIb, .bDw0ac, .LNrHIf .CHM9mc, .giB63b .NwrYVd, .GPmEYb.KKjvXb, .GPmEYb.KKjvXb:hover, .i9ZCec .GFO5Jd, .xwumSd, .REXN1d, .lZERNc, .VOEIyf .VOEIyf, .VOEIyf .jBmls, .oKubKe, .z80M1 {
  color: var(--fg) }
.YDRMcc .JQQdOc, .OJMBqe {
  color: var(--fg-2) }
.Ikgmhe, .gb_oc, .Xcw0p.ztUP4e, .naBZYc .xfudi, .giB63b .oWh3Ld, .GPmEYb, .wO2hid .kF22y, .NqPCi, .xsLqxd {
  color: var(--fg-muted) }
.zWHNCf .SFllF.KKjvXb, .zWHNCf .SFllF.KKjvXb .hGhvff, .zWHNCf .SFllF.KKjvXb:hover, .zWHNCf .SFllF.KKjvXb:hover .hGhvff, .RF3lAd .Xd067b, .RF3lAd, .z80M1.FwR7Pc {
  color: var(--bg) }
.VOEIyf {
  color: var(--fg-input-hover) }
a, .ipQwMb, .i9ZCec .wBtOlc, .vXfEse, .xsLqxd:hover {
  color: var(--fg-link) }
a:hover, .zWHNCf .SFllF:hover, .zWHNCf .SFllF:hover .hGhvff, .Xcw0p.ztUP4e:hover, .naBZYc:hover .xfudi, .GPmEYb:hover, .vXfEse:hover {
  color: var(--fg-link-hover) }

.gb_Xa svg, .fKz7Od {
  fill: var(--fg) }

.Qo7o0c, .NiLAwe, .YDRMcc .JQQdOc, .wi7gLd, .EOlQu .JQQdOc, .QuqGMc, .lZERNc .JQQdOc, .Pt3ZH, .u9F81b.keNKEd.JQQdOc, .i9ZCec {
  border-color: rgba(255, 255, 255, 0.2) }

div[style="background-color: #ffffff; color: #000000;"] {
  background-color: transparent !important }

trans-layer > div[style*="background"] {
  background-color: rgba(0, 0, 0, 0.3) !important }

}
