css:
@-moz-document url("https://finance.yahoo.com/") {
#YDC-UH, #YDC-Col1, #UH-0-UH-0-Header, td p, #data-util-col div, #Col2-5-LinkOut-Proxy, #Col2-1-TradeItLinkBrokerPromo-Proxy,
div[id*="Ads"], div[id*="Ad"], section:nth-of-type(1n+9), nav li:nth-child(4), .nr-applet-main-nav-right, #mrt-node-Col2-6-LinkOut, #mrt-node-Col2-7-Footer
{
    display:none
}

.W\(300px\), #atomic .Maw\(1260px\)
{
    width:100% !important;
    position: relative;
    max-width: 1500px;
}

.Mb\(20px\)
{
    width:290px;
    display:inline-block;
    margin-right:30px;
}

.data-util
{
    max-width:100%;
    width:100%;
}

.US
{
    margin-top:36px !important;
}

#atomic .H\(44px\)
{
    height:25px;
}
#atomic .H\(60px\)
{
    height:0px;
}
#atomic .Py\(6px\)
{
    padding-top:4px;
    padding-bottom:0px !important;
}
#atomic .Maw\(\$newGridWidth\), #atomic .Maw\(1260px\)
{
    max-width: 1500px !important;
}

#mrt-node-Col2-0-SymbolLookup
{
    position:fixed;
    top:50px;
    right:0px;
    width:200px;
}

.Carousel-Mask
{
    width:1400px !important;
}

.Carousel-Mask ul li
{
    width:150px !important;
}

section[data-yaft-module="tdv2-applet-recently_viewed"]
{
	background-color:lightyellow
}

section[data-yaft-module="tdv2-applet-non-stock"]
{
	background-color:#d0ddf6
}
}
