css:
@-moz-document url-prefix("https://www.youtube.com") {
#masthead{background:transparent}
#guide-icon.ytd-masthead,
#masthead yt-icon,
#logo-icon-container svg path,
#logo-icon-container.ytd-topbar-logo-renderer #youtube-paths.ytd-topbar-logo-renderer path.ytd-topbar-logo-renderer {
  fill: #fff;
}
#guide-content polygon {
  fill: #fff;
}
yt-img-shadow.ytd-topbar-menu-button-renderer {
  border: 1.5px solid #fff;
}
#country-code.ytd-topbar-logo-renderer {
  color: #fff;
}
#search-form #container {
  background: rgba(255, 255, 255, 0.2);
  border: 0;
  box-shadow: none;
  border-radius: 6px 0 0 6px;
}
#search-form #container #search {
  color: #fff;
}
#search-form #container #search::-webkit-input-placeholder {
  color: rgba(255, 255, 255, 0.7);
}
ytd-searchbox[mode="legacy"] #search-icon-legacy.ytd-searchbox {
  background: rgba(255, 255, 255, 0.2);
  border: 0;
  border-radius: 0 6px 6px 0;
  box-shadow: none;
  transition: 0.3s;
}
ytd-searchbox[mode="legacy"] #search-icon-legacy.ytd-searchbox:hover,
ytd-searchbox[mode="legacy"] #search-icon-legacy.ytd-searchbox:focus {
  background: rgba(255, 255, 255, 0.4);
  box-shadow: none;
}
ytd-searchbox[mode="legacy"] #search-icon-legacy.ytd-searchbox yt-icon {
  transition: 0.3s;
}
/*[[themeColor]]*/
}
