/* ==UserStyle==
@name        AppVeyor CI Dark
@namespace   StylishThemes
@version     1.2.1
@homepageURL https://github.com/xt0rted/AppVeyor-Dark
@updateURL   https://raw.githubusercontent.com/xt0rted/AppVeyor-Dark/master/appveyor-dark.css
@license     CC-BY-SA-4.0
@author      xt0rted
==/UserStyle== */
/* Background images are from https://github.com/StylishThemes/GitHub-Dark */
@-moz-document domain("ci.appveyor.com") {
  body {
    color: #c0c0c0 !important;
    background-color: #222 !important;
    /*[[bg-choice]]*/
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-attachment: /*[[bg-attachment]]*/ !important;
    /*[[bg-options]]*/
  }

  h1,
  h2,
  h3,
  .label-active {
    color: #c0c0c0 !important;
  }

  a {
    color: #e2dddd !important;
  }

  a:hover {
    color: #00c7f4 !important;
  }

  label {
    color: #949494 !important;
  }

  input[type="email"],
  input[type="text"],
  input[type="number"],
  input[type="password"],
  input[type="url"],
  select,
  textarea,
  .checkbox-group {
    background-color: #181818 !important;
    border-color: #343434 !important;
    color: #ccc !important;
  }

  input[type="email"]:focus,
  input[type="text"]:focus,
  input[type="number"]:focus,
  input[type="password"]:focus,
  input[type="url"]:focus,
  select:focus,
  textarea:focus {
    background-color: #181818 !important;
    border-color: #4183C4 !important;
  }

  select,
  select:hover {
    /* Background image is from https://github.com/StylishThemes/GitHub-Dark */
    background: #181818 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAUCAQAAAAua3X8AAAARklEQVR4AWNABWcazjQg8zGl/wNhA15piBL80gglmNKYSjClMZUgpDGVwKXxKBl4QNiRBLxJIKAIBzXhyCIc3YQTDMEkBwBplPJRxEMa4wAAAABJRU5ErkJggg==") no-repeat right 8px center !important;
    background-size: 8px 10px !important;
    border-color: #343434 !important;
  }

  form.show-required input.ng-invalid,
  form.show-required input:focus.ng-invalid,
  form.show-required select.ng-invalid,
  form.show-required select:focus.ng-invalid,
  form.show-required textarea:focus.ng-invalid,
  form.show-required textarea.ng-invalid {
    border-color: #f44 !important;
  }

  .button,
  .button:focus,
  .button:hover {
    color: #222 !important;
  }

  .button.secondary {
    background-color: #e7e7e7 !important;
    color: #333 !important;
  }

  .button.secondary:hover,
  .button.secondary:focus {
    background-color: #b9b9b9 !important;
  }

  .switch-buttons li a {
    color: #222 !important;
    transition: background-color 300ms ease-out, border-color 300ms ease-out !important;
  }

  .switch-buttons li a:hover {
    background-color: #c0c0c0 !important;
  }

  .switch-buttons li.active a:hover {
    background-color: #2795b6 !important;
  }

  pre {
    background-color: #181818 !important;
    border-color: #343434 !important;
    color: #ccc !important;
  }

  .lighter {
    color: #7b7b7b !important;
  }

  .responsive-table pre {
    background-color: #202020 !important;
  }

  code {
    background-color: #182030 !important;
    border: 1px solid #246 !important;
    color: #9daccc !important;
  }

  .pricing-table {
    border-color: #303030 !important;
  }

  .pricing-table .title {
    background-color: #303030 !important;
    border-bottom: solid 1px #343434 !important;
  }

  .pricing-table .price,
  .pricing-table .bullet-item,
  .pricing-table .cta-button {
    background-color: #181818 !important;
    border-color: #343434 !important;
    color: #c0c0c0 !important;
  }

  .pricing-table .bullet-item,
  .pricing-table .description {
    border-bottom-style: solid !important;
  }

  .contain-to-grid,
  .top-bar,
  .top-bar.expanded .title-area,
  .top-bar-section ul,
  .top-bar-section li a:not(.button),
  .top-bar-section li a:not(.button):hover,
  .top-bar-section .dropdown li label,
  .responsive-table,
  .project-builds,
  .project-build {
    background-color: #181818 !important;
  }

  .top-bar-section .dropdown li label {
    color: #606060 !important;
  }

  .top-bar-section li a:not(.button):hover {
    color: #00b3e0 !important;
  }

  .contain-to-grid {
    border-bottom: solid 1px #343434 !important;
  }

  .project-moniker,
  .project-moniker:hover {
    color: #000 !important;
    font-size: .7rem !important;
    line-height: .7rem !important;
    padding: .25rem !important;
    text-transform: none !important;
    font-weight: 400 !important;
    vertical-align: baseline !important;
    margin-bottom: 0 !important;
  }

  .project-moniker.private {
    text-transform: capitalize !important;
  }

  .project-name .project-moniker {
    margin: 0 !important;
    vertical-align: baseline !important;
  }

  .project-group {
    background-color: #181818 !important;
    color: #949494 !important;
  }

  .project-group:hover,
  .project-group:hover .project-build {
    background-color: #242424 !important;
  }

  .project-build .project-build-version,
  .project-build .project-build-state,
  .project-build .project-deployment-environment {
    margin-right: 1rem !important;
  }

  .project-build + .project-build {
    border-top: solid 1px #343434 !important;
  }

  .project-build:last-of-type {
    border-bottom: none !important;
  }

  .project-build-status.running {
    border-bottom: none !important;
  }

  .project-build-message,
  .project-build-message a,
  .project-build-version {
    color: #e2e2e2 !important;
  }

  .project-build-status a:hover {
    color: #00b3e0 !important;
  }

  .console-log-truncated,
  .top-bar-section .divider,
  .responsive-table .row.empty-list .columns,
  .project-builds-more,
  .deployment-scenario {
    border-color: #343434 !important;
  }

  .top-bar-section li.hover > a:not(.button),
  .project-builds-more:hover,
  .responsive-table .row.body:hover,
  .responsive-table .row.body .columns.navigate a:hover {
    background-color: rgba(255,255,255,.1);
  }

  .project-group-header:hover {
    background-color: transparent !important;
  }

  .project-group-header:hover .project-name a {
    color: #00b3e0 !important;
  }

  .project-toolbox li a:hover {
    background-color: transparent !important;
  }

  .project-build-state .cancel-build,
  .project-build-state .delete-build {
    color: #f44 !important;
  }

  .project-build-state .cancel-build:hover,
  .project-build-state .delete-build:hover {
    color: #b00 !important;
  }

  /* changes the branch icon & text to a purple color */
  /*.branch {
    filter: sepia() saturate(10000%) hue-rotate(280deg) !important;
  }*/

  .horiz-nav li a.active {
    border-bottom: solid 2px #00b3e0 !important;
  }

  .horiz-tabs li a {
    margin-top: 1rem !important;
  }

  .horiz-tabs li:not(.active) a:hover {
    background-color: #444 !important;
    color: #e2dddd !important;
  }

  .horiz-tabs li.active a,
  .horiz-tabs li.active a span {
    color: #222 !important;
  }

  .vertical-tabs-container h2 {
    border-color: #343434 !important;
  }

  .vertical-tabs > li > a {
    color: #e2e2e2 !important;
  }

  .vertical-tabs > li > a:hover,
  .vertical-tabs > li.active > a {
    background-color: #333 !important;
  }

  @media only screen and (max-width: 40em) {
    .responsive-table .row.body {
      border-color: #343434 !important;
    }
  }

  .responsive-table .row.header .columns {
    border-color: #343434 !important;
  }

  .responsive-table .row.body .columns {
    color: #c0c0c0 !important;
  }

  .responsive-table.editable-table {
    background-color: transparent !important;
  }

  .job-console {
    background-color: #182030 !important;
    border-color: #246 !important;
    color: #e2e2e2 !important;
  }

  .job-console .bg-1 {
    background-color: #182030 !important;
  }
  .job-console > div > a {
    color: #888 !important;
  }
  .job-console > div > a:hover {
    color: #00c7f4 !important;
  }

  .trial-warning {
    background-color: #181818 !important;
    color: #aaa !important;
  }

  .in-page-warning {
    background: #182030 !important;
    border-color: #264c72 !important;
    color: #c8d0e1 !important;
  }

  .in-page-warning a {
    color: #4183c4 !important;
  }

  .in-page-warning a:hover {
    text-decoration: underline;
  }

  .has-tip {
    color: #b5b5b5 !important;
    background-color: #444 !important;
    border-color: #444 !important;
  }

  @-webkit-keyframes build-running-status {
    0%,40%,to {
      border-left-color: #fce40f !important;
    }

    75% {
      border-left-color: #f7f5f4 !important;
    }
  }

  @keyframes build-running-status {
    0%,40%,to {
      border-left-color: #fce40f !important;
    }

    75% {
      border-left-color: #f7f5f4 !important;
    }
  }

  @-webkit-keyframes build-queued-status {
    0%,40%,to {
      border-left-color: #66ddf8 !important;
    }

    75% {
      border-left-color: #f7f5f4 !important;
    }
  }

  @keyframes build-queued-status {
    0%,40%,to {
      border-left-color: #66ddf8 !important;
    }

    75% {
      border-left-color: #f7f5f4 !important;
    }
  }

  @-webkit-keyframes build-cancelling-status {
    0%,40%,to {
      border-left-color: #d9d5d1 !important;
    }

    75% {
      border-left-color: #f7f5f4 !important;
    }
  }

  @keyframes build-cancelling-status {
    0%,40%,to {
      border-left-color: #d9d5d1 !important;
    }

    75% {
      border-left-color: #f7f5f4 !important;
    }
  }
}
