css:
@-moz-document domain("anilist.co") {
/* Created by Bicker (https://anilist.co/user/bicker/)
 * Last updated 16-02-2019
 */

/*[[theme]]*/

/*[[color]]*/

/* Navbar */
/*[[nav]]*/

/* Home */
/*[[feedoptions]]*/

/* Activity */
.activity-feed .activity-entry {
    margin-bottom: 15px;
}
.activity-text .text {
	border-left: solid 7px rgba(var(--color-blue));
}
.activity-message .text {
	border-left: solid 7px rgba(var(--color-green));
}
.reply-wrap .activity-replies {
	margin: 1px 20px 0 20px;
}
.reply-wrap .activity-replies .reply {
	background: rgba(var(--color-foreground-alt));
	border-left: 4px solid;
	border-radius: 0;
	margin-bottom: 1px;
	padding: 9px;
	padding-bottom: 1px;
}
.reply-wrap .activity-replies .reply:nth-child(2n) {
    border-color: rgba(var(--color-blue));
}
.reply-wrap .activity-replies .reply p {
	margin-top: 8px;
	margin-bottom: 8px;
}
.reply-wrap .activity-replies .activity-edit .markdown-editor {
	border-radius: 0;
	margin: 0 0 1px 0;
}
.reply-wrap .activity-replies .activity-edit .input {
	border-radius: 0;
	margin-bottom: 0;
}
.reply-wrap .activity-replies .activity-edit h2 {
	margin-top: 10px;
}
.reply-wrap .activity-replies .activity-edit .preview {
	border-radius: 5px;
}
.reply-wrap .activity-replies .activity-edit .actions {
	margin: 15px 0 0 0;
}

/* Banner */
.header-wrap .banner {
	margin-top: 0px;
}
.header-wrap .banner .shadow {
	display: none;
}
.header-wrap .banner .name {
	background: rgba(var(--color-blue));
	border-radius: 5px;
	padding: 6px 15px;
	margin: 16px 15px;
}

/*[[profilecolor]]*/

/* Drop down menus */
.quick-search .el-select .el-input .el-input__inner,
.quick-search .el-select .el-input.is-focus .el-input__inner,
.el-select-dropdown,
.el-dropdown-menu,
.el-dropdown-menu__item--divided::before {
	background: rgba(var(--color-foreground-blue));
}
.el-select-dropdown__item.hover,
.el-select-dropdown__item:hover {
	background: rgba(var(--color-blue));
}
.el-dropdown-menu__item--divided {
	border-color: rgba(var(--color-foreground));
}
.el-select-group__wrap:not(:last-of-type)::after {
	background: rgba(var(--color-background));
}
.el-popper[x-placement^="bottom"] .popper__arrow,
.el-popper[x-placement^="bottom"] .popper__arrow::after {
	border-bottom-color: rgba(var(--color-foreground-blue));
}
.el-popper[x-placement^="top"] .popper__arrow,
.el-popper[x-placement^="top"] .popper__arrow::after {
	border-top-color: rgba(var(--color-foreground-blue));
}

/* Forum */
.comment-wrap {
    border-left: 7px solid rgba(var(--color-blue));
}
.comment-wrap .child.odd {
    border-left: 7px solid;
}

/* Other */
.categories .category {
	background: rgba(var(--color-background)) !important;
	color: rgba(var(--color-blue));
	border-radius: 5px;
}
.categories .category:hover {
	color: rgba(var(--color-blue));
}
.media .media-card {
	box-shadow: none;
}
.user-page-unscoped.blue .progress .bar,
.user-page-unscoped.purple .progress .bar,
.user-page-unscoped.green .progress .bar,
.user-page-unscoped.orange .progress .bar,
.user-page-unscoped.red .progress .bar,
.user-page-unscoped.pink .progress .bar,
.user-page-unscoped.gray .progress .bar,
.user-page-unscoped.custom-color .progress .bar::after {
    background: rgba(var(--color-blue));
}
}
