css:
@-moz-document url-prefix("http://www.eldarya.fr/mall"), url-prefix("http://fa1.eldarya.com/mall"), url-prefix("http://www.eldarya.ru/mall"), url-prefix("http://www.eldarya.com/mall"), url-prefix("http://www.eldarya.br/mall"), url-prefix("http://www.eldarya.de/mall"), url-prefix("http://www.eldarya.es/mall"), url-prefix("http://www.eldarya.fi/mall"), url-prefix("http://www.eldarya.hu/mall"), url-prefix("http://www.eldarya.it/mall"), url-prefix("http://www.eldarya.pl/mall") {

/*прячет лица продавцов и римерочную*/
#mall-alchemy-npc, #mall-pets-npc, #mall-clothes-npc, #mall-preview, #malls-list > li.ad, #mall-purroshop-npc{
    display: none;
}

/*отступ и ширина*/
#mall-categories,#mall-products, #mall-productDetail, #mall-productDetail-variations, #container > div, #mCSB_1_container{
    padding-left: 0px;
    margin-right: 0px;
    width: /*[[css1]]*/px;
}
/*Ширина блока*/
#content{
    width: /*[[css1]]*/px;
    padding-left: 20px;
    padding-right: 20px;
}

/*настройка высоты*/
#mall-products-list, #mall-container, #mall-productDetail-variations{
    max-height: none!important;
    height: auto!important;
    max-width: none!important;
}

#mall-clothes, #mall-pets, #mall-alchemy, #mall-purroshop{
    margin-left: 30px!important;
    margin-right: 30px!important;
    width: 350px!important;
}   
.mall-category, .mall-product, .mall-variation {
    width: 104px;
}#mCSB_29_container{
    
    margin-right: 0px!important;
}
}
