css:
@-moz-document url("https://pikabu.ru/"), domain("pikabu.ru") {
/*[[font]]*/
/*[[ad]]*/
/*Если правое меню поменяло цвет на белый*/
div.hzojDoFHpqpeSzziGPQ {
    box-shadow: 0 1px rgba(255,255,255,.1) inset,0 1px 3px rgba(0,0,0,.5)!important;
    background: linear-gradient(#323232,#282828)!important;
    color: #a6a6a6!important;
    border: 0!important;
}
.hzojDoFHpqpeSzziGPcPBgEikFK4s h4 {
    color: #ffffff;
}
div.hzojDoFHpqpeSzziGPcPBgEikFK4s {
    border-bottom: 1px solid rgba(233, 233, 233, 0)!important;
    background-color: rgba(249, 249, 251, 0)!important;
}
::selection {
    background-color: rgba(68, 68, 68, 0.91)!important;
}
/*иконки*/

:root {
   --color--icon: #e5e5e5;
}

body {
    background-color: #1d1d1d;
}

.body ,.app,.comment-reply,.profile__section,.section_padding_none,.popup__container {
    background: #1d1d1d url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAIAAAAmkwkpAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAClJREFUeNpiFBQU5OXlZQADJmFhYQYYYPr16xeQ+vz5M4gDEYPIAwQYAJUPBptp0pKhAAAAAElFTkSuQmCC) repeat!important;
}
/*цвет значков*/
.bell,.bell_profile-menu ,.story-editor-block__adding-before{
    background-color: #757575!important;
    }
.story-editor-block__adding-before:hover{
    background-color: #454545!important;
    }
.header-right-menu__notification>.icon,.header-right-menu__search button>.icon{
    border-color: #353535!important;
    color: #ffffff!important;
}
    /*цвет значков(наведение выбор)*/
  .header-right-menu__search_focus>.icon, .header-right-menu__search_focus button>.icon, .header-right-menu__search:hover>.icon, .header-right-menu__search:hover button>.icon, .header-right-menu__notification_focus>.icon, .header-right-menu__notification_focus button>.icon, .header-right-menu__notification:hover>.icon, .header-right-menu__notification:hover button>.icon .story__save_active svg {
    fill: #757575!important;
}
.radio_checked,.checkbox_checked {
    border-color: #aaaaaa;
}
/*Блоки*/

.table_standard thead,.table_standard,.static-page__container>h1+div, .static-page__container>h1, section,.header__main,.story__main,.sidebar-block_border ,.section, .community-info-block__rules,.c7V3j_vPF1i74YSs1Eg ,.community-header__details {
    box-shadow: 0 1px rgba(255,255,255,.1) inset,0 1px 3px rgba(0,0,0,.5)!important;
    background: linear-gradient(#323232,#282828)!important;
    color: #a6a6a6!important;
    border: 0!important;
}

.community-header__details {
    border-top: 1px solid #e9e9e9!important;
    padding: 10px 20px!important;
    margin: 0 0px!important;
}

/*делаем прозрачным*/

div.relap-pikabu_ru__test__top-container,.dropdown-item_current ,.story__footer , .sidebar-block__header, .sidebar-block__content_hr,.story__read-more, .story .b-story__show-all,.comment-reply__controls ,.comment-reply,.input,.story-editor__blocks,.user_right-sidebar ,.avatar img,.user_right-sidebar ,.avatar,.story-image img ,.settings-main__sub-header{
    border: 1px solid rgba(233, 233, 233, 0)!important;
    background: rgba(249, 249, 251, 0)!important;
}
.dropdown-item_current,.table_standard tbody tr:before,.table_standard tbody tr:after{
    background-color: rgba(249, 249, 251, 0)
!important;
}

.table_standard tbody tr:not(:first-child) td,.pagination__page_more,.stories-search-group+.stories-search-group , .table_standard thead td, .table_standard thead th, .table_standard thead tr:before, .table_standard thead tr:before, .table_standard thead tr:after, .table_standard thead tr:after {
    border: 1px solid rgba(233, 233, 233, 0)!important;
}

/*Коментарии*/

div.comment__children.comment__children_next-floor {
    background: #2d2d2d!important;
}

.comment__children_next-floor::before {
    border: rgb(56, 56, 56)!important;
}

.comment__children_next-floor::after {
    border: rgb(56, 56, 56)!important;
}

.comment__children {
    background-color: rgba(255, 255, 255, 0)!important;
    border-left: solid 1px rgb(56, 56, 56)!important;
}

.comment__children::before {
    width: 1.2px!important;
    background-color: #313131!important;
}

.comment__body_highlight_yellow:before {
    border-left: 2px solid #8a8a8a!important;
    background-color: rgba(173, 173, 173, 0.1)!important;
}

.comments-navigator__count::after {
    border-left-color: #424242!important;
}

blockquote {
    color: #cccbcb!important;
    background-color: rgba(190, 190, 190, 0.18)!important;
}

.comments-navigator__count {
    background-color: #424242!important;
}

.icon--ui__pagination-left, .icon--ui__pagination-right {
    fill: #707070!important;
}

.page-answers .comments+.comments, .page-comments .comments+.comments, .notification {
    border-top: 1px solid rgba(0, 0, 0, 0.15)!important;
    margin-top: 5px!important;
    padding-top: 5px!important;
}

.page-answers .comments__answers:before, .page-comments .comments__answers:before {
    background-color: #313131!important;
}

.page-comments .comments__answers {
    border-left: solid 1px rgba(112, 112, 112, 0.14)!important;
}

/* убираем плаваюший блок*/

.sidebar__inner_fixed {
    margin-bottom: 200px!important;
}

/*рамка картинок*/

.story-image__stretch rect {
    fill: rgba(58, 58, 58, 0.4)!important;
}

/*кнопки*/

.collapse-button,.comments-navigator__refresh,.button_add,.comments__more-button,.button,.stories-search-group__button a,.stories-search-item_selected ,.button-group_green>button:last-of-type,.button-group_green>button,.button_green,.comment__user[data-own-story=true] .user__nick,.slider__handle,.slider__quantity,.checkbox_switch.checkbox_checked::after,.checkbox_switch::after ,.pagination__page_current,.pagination__page,.notifications-hint__more,.stories-search-item,.stories-search-item__more,.checkbox_checked{
    box-shadow: 0 1px rgba(255,255,255,.1) inset,0 1px 3px rgba(0,0,0,.5)!important;
    background: #282828!important;
    color: #a6a6a6!important;
    transition: 700ms ease-out
!important;
}

/*кнопки выбор*/

.pagination__page_current {
    border: 1px solid rgba(197, 197, 197, 0.18)!important;
    color: #ffffff!important;
    transition: 700ms ease-out
!important;
}

/*кнопки наведение*/

.stories-search-item__close:hover,.stories-search-item__more:hover,.collapse-button:hover,.comments-navigator__refresh:hover,.button_add:hover ,.comments__more-button:hover,.button:hover,.stories-search-group__button a:hover,.stories-search-item_selected:hover,.stories-search-item:hover,.comment__tool_active, .comment__tool:hover,.button-group_green>button:last-of-type:hover,.button-group_green>button:hover,.button_green:hover,.comment-reply__attach-files:hover ,.comment__user[data-own-story=true]:hover .user__nick:hover ,.slider__handle:hover,.pagination__page:hover,.notifications-hint__more:hover ,.radio_checked {
    box-shadow: 0 1px rgba(255,255,255,.1) inset,0 1px 3px rgba(0,0,0,.5)!important;
    background: #323232!important;
    color: #ffffff!important;
    transition: 700ms ease-out
!important;
}
.story-save__cat:hover {
    background-color: rgba(45, 45, 45, 0.9);
}
/*скролл*/

.story__scroll-next-btn:before,.scroll-up-button:hover {
    background: linear-gradient(to bottom,#282828 15%,#2a2a2a 100%)!important;
    box-shadow: 0 1px rgba(255,255,255,.1) inset,0 1px 3px rgba(0,0,0,.5)!important;
    transition: 700ms ease-out!important;
}

/*удаляем ненужное*/

.story__read-more:before, .story .b-story__show-all:before{
    background-image: none
!important;
}

.comments-navigator__refresh,.collapse-button,.notifications-hint__more {
    border: 1px solid rgba(233, 233, 233, 0.0)!important;
   }
 /*поиск*/
.dropdown-item:hover {
    background-color: #343434!important;
}

/*бардюр section*/

.section-hr__title:after, .section-hr__title:before {
    height: 5px!important;
    background: #282828!important;
}

.communities-list__line {
    background-color: #343434!important;
}

/*Текст*/

.sidebar-block__header h4 ,.sidebar-toggle__title,.menu,.user a ,.story__title-link_visited,.page-story__comments h4, element.style,.user__nick,.page-story__comments .section__right[data-role=order], .link,.page-story__compare-title[data-level="0"],body,input,.stories-search__feed-panel>span,.profile__digital b,.profile-info__label,input, textarea, .input-buffer,.story-save__name {
    color: #cecece!important;
    transition: 700ms ease-out
!important;
}

.awards-feed__caption span,.tags__tag_highlight, .tag_highlight ,.tags__tag, .tag,.story__comments-count,.story__tools,.social__count ,.button_link{
    color: #6e6e6e!important;
    transition: 700ms ease-out!important;
}
.caption,.sidebar-toggle__action,.community__line-up{
    color: #6e6e6e;
}
/*Текст при наведении и выборе */

.table thead td, .table thead th,.menu__item:before,.menu__item:hover ,.menu__item_current,.feed-panel__calendar:hover,.user__nick:hover,.page-story__comments:hover .section__right[data-role=order]:hover,a:hover, .link:hover,.story__save:hover,.social__count:hover,.stories-search-group__button:hover span,.button_link:hover ,.page-settings__value,.select select 
{
    color: #ffffff!important;
    transition: 700ms ease-out
!important;
}

.feed-panel__calendar:hover svg {
    fill: #ffffff!important;
}

/*подчеркивание Текста */

.menu__item:before {
    background-color: #8c8c8c!important;
}

/*панель снизу*/

.footer {
    border-top: 1px solid rgba(233, 233, 233, 0)!important;
    background-color: rgba(240, 240, 240, 0.07)!important;
    color: rgba(255, 255, 255, 0.81)!important;
}

/*поиск*/

.header-right-menu__search_focus, .header-right-menu__search:hover, .header-right-menu__notification_focus, .header-right-menu__notification:hover {
    background-color: rgba(56, 56, 56, 0.6)!important;
}

/*подсказки*/

.profile_short {
    background-color: rgba(0, 0, 0, 0.00)!important;
}

.popup__arrow_bottom:after {
    border-bottom-color: #1d1d1d!important;
}

.popup__arrow_top:after {
    border-top-color: #1d1d1d!important;
}

.popup_theme_gray .popup__container {
    background-color: rgba(85, 85, 85, 0.43)!important;
}

/*аватар*/

.background {
    mix-blend-mode: multiply!important;
    background-image: url(https://preview.ibb.co/k8T0H7/sprite_192dpi.png)!important;
}

.avatar__default {
    background-image: url(https://preview.ibb.co/k8T0H7/sprite_192dpi.png)!important;
}

.profile__header>.avatar {
    position: absolute!important;
    left: 25px!important;
    top: 50px!important;
    border: 3px solid rgba(0, 0, 0, 0)!important;
    box-sizing: content-box!important;
    background-color: rgba(0, 0, 0, 0)!important;
}

.profile__section {
    border-top: 1px solid rgba(233, 233, 233, 0.0)!important;
    margin: 0 20px!important;
}

.select {
    background-color: rgba(105, 105, 105, 0.80)!important;
    border: 1px solid rgba(233, 233, 233, 0.15)!important;
}

.select select {
    background-color: rgba(105, 105, 105, 0.80)!important;
}

.select__icon {
    background-color: rgba(255, 255, 255, 0)!important;
    color: #ffffff!important;
}

.input_focus, .input:focus {
    border-color: #323232!important;
}

/*иконка сворачивания ветки*/

.comment-toggle-children__icon,.comment-toggle-children_collapse .comment-toggle-children__icon {
    border: 1px solid rgba(197, 197, 197, 0.18)!important;
    color: rgba(197, 197, 197, 0.18)
!important;
    background-color: rgba(255, 255, 255, 0)!important;
}

.comment_collapsing-area>.comment-toggle-children>.comment-toggle-children__icon,.comment-toggle-children:hover .comment-toggle-children__icon {
    border-color: rgba(239, 239, 239, 0.33)!important;
    color: rgba(239, 239, 239, 0.33)!important;
}

.comment__children:hover {
    border-left: solid 1px rgba(239, 239, 239, 0.33)!important;
}

.checkbox_switch::after {
    top: 0px!important;
    width: 18px!important;
    height: 8px!important;
}

.checkbox_switch {
    height: 8px!important;
    background-color: rgba(31, 31, 31, 0.78)!important;
    border-radius: 100px!important;
}

.input_small {
    border-color: #636363!important;
}
}
@-moz-document domain("pikabu.ru"){
::-webkit-scrollbar { width: 10px !important; height: 10px !important;}
::-webkit-scrollbar-thumb {
    background: #cccccc !important;
}

::-webkit-scrollbar-track {
    background: #444 !important;
}
::-webkit-scrollbar-track-piece {
    background: #191919 !important;
}
blockquote { background: #191919; }

}
