css:
@-moz-document domain(www.twitch.tv) {
    .side-nav.tw-relative {
        display: none;
    }
    .persistent-player.persistent-player--theatre {
        width: calc(100% - 65rem) !important;
    }
    .persistent-player.persistent-player--theatre .video-player__container {
        bottom: 0 !important;
    }
    .right-column--theatre .video-watch-page__right-column,
    .right-column--theatre .channel-page__right-column,
    .right-column--theatre .channel-root__right-column {
        width: 65rem !important;
    }
    .right-column--theatre .video-chat {
        flex-basis: 65rem !important;
    }
    .right-column--theatre .chat-line__message,
    .right-column--theatre .chat-line__moderation, 
    .right-column--theatre .chat-line__status {
        font-size: 20px;
        line-height: 24px;
    }
}
