css:
@-moz-document domain("developer.mozilla.org") {
/*Elements to hide*/

.newsletter-box,
.nav-footer,
.wiki-block.contributors,
#content .toc,
.document-actions,
#main-header {
	display: none;
}
.document-head {
	padding: 10px 0;
}
main > .center {
	padding-top: 10px;
}
.document-head>.center {
	border-bottom: 1px solid darkgray;
	padding-bottom: 10px;
}
.wiki-column {
	padding-bottom: 0;
}
.wiki-column > #wikiArticle {
	padding-bottom: 0;
	border-bottom: 0px solid #3f87a6;
}
.text-content dl,
.text-content ol,
.text-content ul,
.text-content p,
.text-content table {
	width: 100%;
	max-width: /*[[max-page-width]]*/;
}
.banner,
.blockIndicator,
.geckoVersionNote,
.inheritsbox,
.note,
.notice,
.overheadIndicator,
.standardNoteBlock,
.warning,
.reviews,
.text-content .cssprop,
.text-content .htmlelt,
.text-content .learn-box,
.text-content .properties,
.text-content .summary-box-events,
.text-content .summary-box-js,
.communitybox,
.moreinfo,
.prevnext,
.readable-line-length,
.summary,
.banner,
.blockIndicator,
.geckoVersionNote,
.inheritsbox,
.note,
.notice,
.overheadIndicator,
.standardNoteBlock,
.warning,
.draft,
.notice:not(.experimental),
.translationInProgress,
.warning#doc-rendering-in-progress,
.warning#doc-rendering-scheduled,
.warning#kserrors,
.warning.warning-review {
	max-width: /*[[max-page-width]]*/;
}
.text-content pre.line-numbers {
	padding: 10px;
	border: none;
	margin: 20px auto;
	max-width: 90%;
}
}
