    font-family: 'Roboto';
    font-style: normal;
    font-weight: 500;
    src: local('Roboto Medium'), local('Roboto-Medium'), url(https://fonts.gstatic.com/s/roboto/v15/RxZJdnzeo3R5zSexge8UUVtXRa8TVwTICgirnJhmVJw.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
@font-face {
    font-family: 'Roboto';
    font-style: normal;
    font-weight: 700;
    src: local('Roboto Bold'), local('Roboto-Bold'), url(https://fonts.gstatic.com/s/roboto/v15/d-6IYplOFocCacKzxwXSOFtXRa8TVwTICgirnJhmVJw.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}



* {
    font-family: 'Roboto' !important;
}
body, html {
    overflow-x: hidden;
}
.b2hzT, #prm {
    display: none;
}

#res {
    padding: 0;
}
.jhp input[type="submit"], .sbdd_a input, .gbqfba {
    background: #fff;
    border-color: #fff;
    border-radius: 3px;
    box-shadow: 0 3px 5px rgba(0,0,0,0.15);
    color: #666;
    cursor: pointer;
    transition: 0.1s ease;
    position: relative;
    top: 0;
}
.jhp input[type="submit"]:hover, .sbdd_a input:hover, .gbqfba:hover {
    background: #fff;
    border-color: #fff;
    color: #000;
    box-shadow: 0 5px 8px rgba(0,0,0,0.3);
    top: -2px;
}
#hplogo, body:not(.hp) #lga {
    display: none !important;
}
#lga {
    background: url("https://i.imgur.com/znTa8Hy.png") no-repeat center 109px !important;
    background-size: 272px 92px !important;
}
#logo, #logocont {
    background: url("https://i.imgur.com/znTa8Hy.png") no-repeat center !important;
    background-size: 110px 37px !important;
}
#logo > *, #logocont > * {
    opacity: 0 !important;
}
#hdtb {
    border: none;
}

.sfbg, #hdtb, .sfbgx {
    background: transparent;
    border-color: transparent;
}
.sfbgg, #hdtbSum, #viewport {
    background-size: cover;    
}
#fbar {
    background-size: cover;
    height: 120px;
    border: none;
    position: relative;
    margin-top: 2px;
}
#fbar span {
    visibility: hidden;
}
#hdtb .hdtb-mitem a, #hdtb-tls, #gb#gb a.gb_O, #gb#gb a.gb_P, #gb#gb span.gb_P {
    color: #fff !important;
    text-shadow: 0 0 2px rgba(0,0,0,1);
}
#hdtb .hdtb-mitem a:hover {
    color: #fff;
}
#hdtb-msb .hdtb-mitem.hdtb-msel, #hdtb-msb .hdtb-mitem.hdtb-msel-pre {
    color: #006eff;
    border-bottom: 3px solid #006eff;
}
#hdtb-tls:hover {
    background: transparent;
    border-color: transparent;
    box-shadow: none;
}
#hdtb-msb .hdtb-tl-sel, #hdtb-msb .hdtb-tl-sel:hover {
    background: #fff;
    box-shadow: none;
    border-color: #fff;
    color: #666 !important;
    text-shadow: none;
}
#foot {
    position: absolute;
    z-index: 1;
    margin-top: 20px;
}
#foot #navcnt td span, #foot #navcnt #pnnext {
    display: none !important;
    visibility: hidden !important;
}

#foot #navcnt a, #foot #navcnt .cur {
    background: #fff;
    width: 42px;
    height: 42px;
    display: flex;
    justify-content: center;
    align-items: center;
    margin-right: 10px;
    color: #666;
    font-weight: 700;
    border-radius: 3px;
}
#foot #navcnt .cur {
    background: #eee;
    color: #000;
}
#foot #navcnt a:hover, #foot #navcnt .cur:hover {
    background: #eee;
    text-decoration: none;
    color: #333;
}
.gb_hc {
    filter: invert(1);
    -webkit-filter: invert(1);
}
.gb_Z span {
    color: #000 !important;
    text-shadow: none;
}
#rso .g, #brs {
    border-radius: 3px;
    box-sizing: border-box;
    padding: 15px;
    box-shadow: 0 2px 2px 0 rgba(0,0,0,0.16),0 0 0 1px rgba(0,0,0,0.08);
}
div[data-hveid] > .g {
    padding: 0 !important;
    box-shadow: none !important;
}
#brs g-section-with-header {
    margin-top: 0;
}
div[data-hveid] > .g, div[data-hveid], div[data-md] {
    box-shadow: none !important;
    background: transparent !important;
}
.exp-outline {
    border: none;
}
#fbar.EvHmz.hRvfYe {
    display: none !important;
}
.knowledge-panel[data-hveid] {
    box-shadow: 0 2px 2px 0 rgba(0,0,0,0.16),0 0 0 1px rgba(0,0,0,0.08) !important;
}
.sbibod {
    overflow: hidden;
    border-radius: 5px;
}






/***** Settings *****/

/* Fade Search Banner and Footer */
/*[[search-banner-n-y]]*/

/* Background Image */
.sfbgg, #hdtbSum, #viewport, #fbar {
    background: url('https://source.unsplash.com//*[[bg-width]]*/x/*[[bg-height]]*///*[[imageDuration-d-w]]*/?/*[[bg-keyword]]*/') center fixed;
}

}
