css:
@-moz-document domain("resetera.com") {
.pageWidth
{
    width: /*[[PageWidth]]*/% !important;
    max-width:none;
}

#logo
{
    filter: grayscale(50%);
}

#meta-links a
{
    color: #636363;
    text-decoration: none;
}


/*navbar*/
.Responsive #navigation
{
    position: /*[[NavBarPos]]*/;
}
.navTabs .navLink,
.navTabs .SplitCtrl
{
    color: #636363;
}
.navTabs .publicTabs .navTab.group .navLink:last-child,.navTabs .publicTabs .navTab.group .navLink:first-child,.navTabs .publicTabs .navLink
{
    border:none;
    background:none;
}
.navLink:hover
{
    background-color: #282828 !important;
}

/* title bar*/
.titleBar
{
    background:none;
}
#pageDescription
{
    color:#636363;
    text-decoration: none;
}
.fa-envelope:before,.fa-bell:before,.fa-eye:before
{
    color:#564184;
}
.navTabs .publicTabs .navLink img,.fa-gamepad:before,.fa-asterisk:before
{
    opacity:.5;
}



/*Sticky threads*/
.discussionListItem .iconKey span
{
    display: none;
}

.discussionListItem.sticky
{
    background-color: #222;
}

.threadListSeparator,
.threadListSeparator sticky
{
    background-color: #564184;
}

.discussionList .sectionHeaders
{
    background: #564184;
    margin-left: 0px;
}

/*unread icon*/

/*[[unreadIcon]]*/

/*Pagenav*/
.discussionListItem .itemPageNav
{
    /*[[PageNv]]*/
}
.PageNav a
{
    color: #636363;
    border: #F000;
}

body .itemPageNav a
{
    background-color: #222;
    border: #F000;
    color:#636363 !important;
}

body .itemPageNav a:hover
{
    background-color: #222;
    border: #0000;
    color: #a791cf
}

::selection
{
    background-color: #a791cf;
}

.unreadLink
{
    color: #636363 !important;
    border: transparent;
}

.discussionListItems .lastPostInfo .username
{
    color: #564184;
}

a.dateTime
{
    color: #636363 !important;
}

.discussionListItem .stats dd
{
    color: #636363;
}

.discussionListItem .title a
{
    font-weight: normal !important;
    color: #dbdbdb !important;
}

body .faint,
body a.faint,
body .faint a
{
    color: #636363;
}

.discussionListItem .posterDate .username
{
    color: #636363;
}

.discussionList .sectionFooter
{
    display: none;
}

abbr[title],
acronym[title]
{
    text-decoration: none;
}

a.callToAction span
{
    background-color: #564184 !important;
}

a.callToAction:hover span
{
    background-color: #452981 !important;
}

a:link:hover
{
    text-decoration:none;
    color:#a791cf !important;
}
abbr[title], acronym[title],.message .postCount a,.message .messageMeta .control
{
    color: #636363;
    border:none;
}
.message .messageMeta .control:hover
{
    text-decoration: none;
}
.messageList .newMessagesNotice
{
	border: none;
	background: #564184;
}

.pagenav-actions
{
     /*[[NavButtons]]*/;
    background:#252525;
}

/*Quick reply*/
.quickReply,.quickReply:before, .quickReply:after
{
    border:none;
    background: none;
}
.button.primary
{
    background:#564184;
}
.button.primary:hover
{
    background:#564184;
}

/*Avatar*/
.messageUserBlock .avatar img
  {
    	background: none !important;
    	box-shadow: none !important;
  }

/*Quotes*/
.bbCodeQuote .attribution
{
    justify-content:left;
    color:#868686;
}
.bbCodeQuote .attribution
{
    background:none !important;
}

/*target/user posts*/
.messageList .message:target
{
    border: 3px solid #564184;
}

.messageList [data-author="/*[[Username]]*/"]
{
    border: 3px solid /*[[Userpostcolor]]*/ !important;
}

/*.Message Notices*/
/*[[MessageNotices]]*/
}
