css:
/* i really want this to be global */

/** table */
.xdebug-error {
	border-color: grey !important;
	font-family: "IBM Plex Mono", "Fira Code", Consolas, Monaco, "Lucida Console", "Liberation Mono", "DejaVu Sans Mono", "Bitstream Vera Sans Mono", "Courier New", monospace;
	color: black;
	font-size: 14px;
	width: 100%;
	border: .5px solid grey !important;
	border-top: 6px solid /*[[accentcolour]]*/ !important;
}

/** table header */
.xdebug-error > tbody > tr:nth-child(1) > th {
	background-color: white !important;
	color: black !important;
	font-weight: 500;
	padding: 8px;
	font-size: 14px;
}

/** table header sub */
.xdebug-error > tbody > tr:nth-child(2) > th {
	padding: 4px 8px;
	background-color: #f2f2f2;
}

/** exclamation mark */
.xdebug-error > tbody > tr:nth-child(1) > th > span {
	display: none;
}

/** table rows */
.xdebug-error th,
.xdebug-error td {
	padding: 4px;
	border: .5px solid grey !important;
	background-color: #f2f2f2;
	font-size: 12px;
}

/** green text */
.xdebug-error font[color="#00bb00"] {
	color: green;
}
