css:
@-moz-document url-prefix("https://mangadex.org/") {
      
    /*[[theme]]*/


    /* -------------- layout changes -------------- */
    
    .navbar-default,
    .footer,
    .breadcrumb {
        background-image: none;
    }
    
    .navbar-right > .dropdown > .dropdown-toggle > .hidden-md {
        color: white !important;
    }

    thead {
        height: 33px;
        font-weight: 500;
    }

    .table > tbody > tr > th,
    .table > tfoot > tr > th,
    .table > thead > tr > td,
    .table > tbody > tr > td,
    .table > tfoot > tr > td {
        border: none;
    }

    .manga_title > td > tr {
        margin-top: 15px;
    }

    .panel-default,
    panel {
        border: none;
    }

    .panel-default > .panel-heading {
        background-image: none;    
        height: 33px;
        padding: 5px 15px;
        border-top-right-radius: 0px;
        border-top-left-radius: 0px;
    }

    .btn {
        padding: 9px 12px;
        background-image: none;
        border: none;
        border-radius: 0;
    }

    .panel {
        border-radius: 0;
    }

    .nav-tabs.nav-justified > li > a {
        padding-top: 4px;
        padding-bottom: 5px;
    }

    .nav-tabs > li > a,
    .nav-tabs.nav-justified > li > a {
        border: none;
        border-radius: 0;
    }

    .nav-tabs.nav-justified > .active > a,
    .nav-tabs.nav-justified > .active > a:hover,
    .nav-tabs.nav-justified > .active > a:focus {
        border: none;
    }

    .form-control,
    #quick_search_button,
    .form-control > .btn-default {
        background-image: none;
        border: none;
        border-radius: 0;
    }

    #quick_search_button {
        height: 38px;
    }

    #quick_search_input {
        box-shadow: none !important;
    }
    
    #content > .nav-tabs {
        margin-bottom: 3px !important;
    }

    .nav-tabs {
        border: none;
    }

    .nav-tabs > li {
        margin-bottom: 0;
    }

    .nav-tabs > li > a {
        margin: 0;
    }

    .nav-tabs > li.active > a,
    .nav-tabs > li.active > a:hover,
    .nav-tabs > li.active > a:focus {
        border: none;
        margin: 0;
    }

    .row > .col-sm-6 {
        margin: 10px 7px !important;
        width: 49%;
        padding-top: 10px;
        padding-right: 10px;
        height: 235px !important;
    }

    .row > .col-sm-6 > .large_logo {
        max-height: 213px;
    }

    .row > .col-sm-6 > p:nth-child(4) {
        overflow: auto;
        height: 155px !important;
        padding: 5px;
    }

    .edit > .col-sm-9 > .table {
        margin-top: 5px;
        background-color: unset;
    }

    img.border-radius {
        border-radius: 0;
    }

    #user_search_form {
        margin-top: 0 !important;
    }

    #search_button {
        background: none;
    }


    /* reading view */
    #content > .toggle > .panel:not(.panel-body) {
        position: fixed;
        left: 15px;
        right: 80%;
        max-width: 280px;
    }

    #content > .toggle > .panel > .panel-heading {
        height: auto;
    }

    #content > .toggle > .panel > .panel-heading > .panel-title {
        width: 92% !important;
    }

    #content > .toggle > .panel > .panel-heading > .panel-title > .minimise {
        float: right;
        position: absolute;
        right: 8px;
        top: 8px;
    }

    #content > .toggle > .panel > .panel-body > .row > .col-md-3,
    #content > .toggle > .panel > .panel-body > .row > .col-md-2,
    #content > .toggle > .panel > .panel-body > .row > .col-md-4 {
        width: 100%;
    }

    #content > .toggle > .panel > .panel-body > .row > .col-md-4 {
        padding-top: 15px;
    }

    #content > .toggle > .panel > .panel-body > .row > .col-md-3:nth-child(2) {
        position: absolute;
    }

    #content > .toggle > .panel > .panel-body > .row > .col-md-3:nth-child(1) {
        padding-top: 55px;
    }

    .bootstrap-select .dropdown-toggle:focus {
        outline: none;
    }

    .images {
        margin-top: -10px;
    }

    .settings {
        margin-left: 20%;
    }

    @media only screen and (max-width: 1440px) and (min-width: 1150px) {
        .container {
            width: 100%;
            padding-left: 20px;
            padding-right: 20px;
        }

        .hidden-md {
            display: inline-block !important;
        }
    }

    @media only screen and (max-width: 1040px) {
        #content > .toggle > .panel:not(.panel-body) {
            position:relative;
            width: 100%;
            left: 0;
            right: 0;
            max-width: 100%;
        }

        #content > .toggle > .panel > .panel-body > .row > .col-md-3,
        #content > .toggle > .panel > .panel-body > .row > .col-md-2,
        #content > .toggle > .panel > .panel-body > .row > .col-md-4 {
            display: inline-block;
            width: 24%;
            margin: 0;
        }

        #content > .toggle > .panel > .panel-body > .row > .col-md-3:nth-child(2) {
            position: relative;
        }

        #content > .toggle > .panel > .panel-body > .row > .col-md-4 {
            padding-top: 0;
        }

        #content > .toggle > .panel > .panel-body > .row > .col-md-3:nth-child(1) {
            padding-top: 0px;
        }

        .settings {
            margin-left: 0;
        }

        #quick_search_input {
            width: 100px;
        }
    }

    @media only screen and (max-width: 720px) {
    
        #content > .toggle > .panel > .panel-body > .row > .col-md-3,
        #content > .toggle > .panel > .panel-body > .row > .col-md-2,
        #content > .toggle > .panel > .panel-body > .row > .col-md-4 {

            max-width: unset;
            margin: 0;
        }
    
        .navbar-brand {
            padding: 15px 5px 15px 15px;
    }
    
}
}
