css:
@-moz-document domain("proboards.com"), domain("freeforums.net"), domain("boards.net") {
:root, body, .container1_color .email, .container2_color .email, #wrapper, .popup_html ul, nav ul li.currentpage, input[type="text"], input[type="email"], input[type="password"], .ui-dialog .ui-dialog-titlebar, .container>.content, .ui-dialog .ui-dialog-content, .ui-dialog .ui-dialog-buttonpane, .container.copy .clone, .content-box {
    background-color: /*[[0]]*/;
}
.panel, .btn-default, #content, .dropdown-menu, #nav-tree>li:hover, #nav-tree .ui-menu li:hover, .popup_html li:hover, .popup_html .ui-menu li:hover, #my-forum .account div.preview ul li:hover, #account-settings-forum #tree-container>.tree li.forum>a, #account-settings-forum #tree-container>.tree li.forum>a.clicked, #jstree-dragged li.forum>a, #jstree-dragged li.forum>a.clicked, #create_forum .forum_domain .dropdown, #create_forum, .sub_nav_wrapper, .recent-threads-button:hover {
    background-color: /*[[1]]*/;
}
.well, .button input, .dropdown-menu>li>a:focus, .dropdown-menu>li>a:hover, .dropdown-menu .divider, .tree-boards>ul>li .last, #account-settings-forum #tree-container>.tree li.account>a, #account-settings-forum #tree-container>.tree li.account>a.clicked, #jstree-dragged li.account>a, #jstree-dragged li.account>a.clicked {
    background-color: /*[[2]]*/;
}
.search .disabled {
    background-color: #555;
}
table.list abbr.time, table.list .last-edited, .container abbr.time, .sub_nav_wrapper .sub_nav a:hover, #settings-wrapper .setting-container .collapsed .description, .o-single-post__title {
    color: /*[[d]]*/;
}
select, input, #nav-tree>li a, #nav-tree>li:hover>div>a, .popup_html li a, .popup_html .ui-menu li:hover>a, nav ul li.currentpage a, table.list, .sub_nav_wrapper .sub_nav a, .tree li a, .tree li span, .recent-threads-button:hover, .container, .stats .content, .list .item.state-hover, .list .board.item:hover, #calendar-list .item:hover, .explanation, .btn-default, .content-box {
    color: /*[[f]]*/;
}
.arrow.right>span {
    border-left-color: /*[[f]]*/;
}
#nav-tree>li:hover .menu_arrow span, .stats .content>table>tbody>tr>td {
    border-color: transparent transparent transparent /*[[1]]*/;
}
table.list>thead>tr>th, table.list>tbody>tr>td {
    border-color: /*[[1]]*/;
}
body, header.navbar.navbar-default .nav li .dropdown-menu a, .signup-checkbox {
    color: /*[[a]]*/;
}
select, input {
    background-color: /*[[1]]*/;
    color: /*[[f]]*/;
}
.well, .btn-default {
    border: 1px solid /*[[2]]*/;
}
.btn-default.active, .btn-default:active, .btn-default:focus, .btn-default:hover, .open .dropdown-toggle.btn-default {
    color: /*[[3]]*/;
    background-color: /*[[08]]*/;
    border-color: /*[[3]]*/;
}
.list .item.state-hover, .list .board.item:hover, #calendar-list .item:hover {
    background-color: /*[[08]]*/;
}
#create_forum .forum_domain .dropdown {
    border-color: /*[[08]]*/;
}
#nav-tree .menu_arrow {
    border-color: transparent transparent transparent /*[[f]]*/;
}
#nav-tree .menu_arrow span {
    border-color: transparent transparent transparent /*[[0]]*/;
}
.recent-threads-button, #navigation-tree, #create_forum input[type=password], #create_forum input[type=text], #create_forum select {
    background-color: /*[[0]]*/;
    color: /*[[f]]*/;
}
nav ul li.currentpage {
    border-bottom: 3px solid /*[[0]]*/;
    border-right: 1px solid /*[[0]]*/;
}
#create_forum, #create_forum input[type=password], #create_forum input[type=text], #create_forum select {
    border: none;
    box-shadow: none;
}
#captcha-container {
    background-color: transparent;
}
.ui-form-error {
    background-color: #a94442;
    color: #f2dede;
}
#news {
    color: #888;
}
/*Optional with Style Settings when released*/
img {
    display: /*[[n]]*/;
}
}
