css:
@-moz-document domain("downdetector.com"), regexp("^.*disqus.com/embed/.*downdetector.com.*$") {

/*[[theme]]*/

/* Selection */
::-moz-selection { color: var(--bg) !important; background: var(--fg-link) !important; text-shadow: none }
::selection { color: var(--bg) !important; background: var(--fg-link) !important; text-shadow: none }
/* Scrollbars */
::-moz-scrollbar{background-color:var(--bg-2) !important;width:10px !important}
::-moz-scrollbar-track{background-color:var(--bg-2) !important}
::-moz-scrollbar-button{display:none !important}
::-moz-scrollbar-thumb{background-color:var(--fg-link) !important;border-radius:4px !important;border:none !important }
::-moz-scrollbar-thumb:hover{background-color:var(--fg-link-hover) !important}
::-webkit-scrollbar{background-color:var(--bg-2) !important;width:10px !important}
::-webkit-scrollbar-track{background-color:var(--bg-2) !important}
::-webkit-scrollbar-button{display:none !important}
::-webkit-scrollbar-thumb{background-color:var(--fg-link) !important;border-radius:4px !important;border:none !important }
::-webkit-scrollbar-thumb:hover{background-color:var(--fg-link-hover) !important}


.navbar-inverse .navbar-inner {
  background-image: none }

html, body {
  background-color: var(--bg) }
.navbar-inverse .navbar-inner, .breadcrumb, .plan, .well, .modal {
  background-color: var(--bg-2) }
.thumbnail {
  background-color: #000 }
.table-striped tbody > tr:nth-child(2n+1) > td {
  background-color: rgba(255, 255, 255, 0.1) }
.alert-warning {
  background-color: rgba(255, 255, 0, 0.3) }
#overlay {
    background-color: rgba(0, 0, 0, 0.5) }

body, h1.logo .black, .breadcrumb > li, .postbox section label, .comment__footer a:hover {
  color: var(--fg) }
h1, h2, h3, h4, h5, .navbar-inverse .nav > li > a {
  color: var(--fg-2) }
h1.logo .grey, .footer a, .footer a:link, .footer, .comment__footer a {
  color: var(--fg-muted) }

a, a:link, #top10 span.counter, .rating > span:hover::before, .rating > span:hover ~ span::before {
  color: var(--fg-link) }
a:hover, a:focus, .navbar-inverse .nav > li > a:hover, .navbar-inverse .nav > li > a:focus {
  color: var(--fg-link-hover) }

img[src="https://cdn.downdetector.com/static/images/mark.svg"] {
  filter: saturate(0) }

img, img.lazy {
  filter: invert(1) hue-rotate(180deg) }
img.lazy[src*="sparkline.png"], img[src*="chart.png"], [class^="icon-"] {
  filter: invert(1) saturate(0) }
#map {
  filter: invert(1) hue-rotate(90deg) }

.thumbnail, input[type="search"], .footer, .table td, .alert-warning, .well, .disqus-footer__wrapper, .page-header, .nav-primary, .vote-down, .modal-header {
  border-color: rgba(255, 255, 255, 0.1) }
a.thumbnail:hover, a.thumbnail:focus, input[type="search"]:focus, #top10 span.counter, .input--text:focus {
  border-color: var(--fg-link) }

input[type="search"], .textarea-wrapper, .post-actions, .input--text {
  border-color: rgba(255, 255, 255, 0.1);
  background-color: var(--bg-input) !important;
  color: var(--fg-input) }

.breadcrumb > li {
  text-shadow: none }

a[style="color:black"], a[style="color:#428bca"] {
  color: var(--fg-link) !important }
a[style*="color: black"], .btn-danger, .load-more .btn, .tab-conversation.active > a {
  color: #fff !important }
.btn {
  color: #000 !important }

.alert-success {
  background-color: rgba(0, 255, 0, 0.3) }

.thumb-map {
  border-color: #fff !important;
  filter: invert(1) hue-rotate(90deg) }
.thumb-map .btn {
  filter: brightness(200);
  border-color: transparent !important }

.footer {
  display: none }

}
