css:
@-moz-document url-prefix("https://blog.csdn.net/") {
#csdn-toolbar {
      background-color: #FFF;
      position: fixed;
      top: 0px;
      z-index: 999;
      opacity: 0.9
  }
  body {
      padding-top: 50px!important
  }
  .grade-detail-box {
      display: none!important;
  }
  #mainBox {
      padding: 0!important;
  }
  .jump_article {
      display: none!important
  }
  .bdshare-button-style0-16 {
      display: none;
  }
  #article_content {
      height: auto!important
  }
  .hide-article-box {
      display: none
  }
  #adContent {
      visibility: hidden;
  }
  #csdn-toolbar {
      background-color: #FFF;
      position: fixed!important;
      top: 0px;
      z-index: 999;
      opacity: 0.9
  }
  body {
      padding-top: 50px!important
  }
  .hide-article-box {
      display:none!important;
  }
  #article_content {
      display:block!important;
      height:auto!important;
      overflow:visible!important;
  }
  .container#mainBox {
      width: calc(100% - 200px)!important;
      margin-left: 100px;
      height: auto!important;
      padding: 0!important;
  }
  .container#mainBox main {
      float: right!important;
      width: 100%!important;
  }
  aside {
      z-index:9999!important;
      position:fixed!important;
      width:200px!important;
      box-shadow:1px 1px 1px #DDD;
      height:70px!important;
      left:40px!important;
      top:60px!important;
      background:rgba(200, 200, 200, 0.4);
      overflow:hidden;
      border-radius:5px;
  }
  aside:hover {
      padding:40px!important;
      overflow-y:scroll;
      display:block;
      position:fixed;
      width:400px!important;
      height:500px!important;
      left:40px;
      top:60px;
  }
  .recommend-box, .article-list {
      margin-top: 30px;
  }
  .comment-list {
      padding:15px;
  }
  .login-mark {
      display:none;
  }
  #passportbox {
      display:none;
  }
  #dmp_ad_58{
      display:none;
  }
  .recommend-recommend-box{
      display:none;
  }
  #footerRightAds{
      display:none;
  }
  .mb8{
      display:none;
  }
td:hover{
	cursor:pointer;
	padding:20px!important;
}
.aside-box:hover{
	margin-left:-5px;
	width:calc(100% + 10px);
	padding-top:5px;
	padding-bottom:5px;
}
}
