css:
@-moz-document domain("reddit.com") {
html, body, h1, h2, h3, h4, h5, li, li span, p, a,
.sitetable,
.entry .buttons li a,
.md p,
.morelink a, .morelink a:hover{
    font-family: 'Fira Sans',sans-serif;
}
.s1r1zaa-6,
.s1c2oq5o-6, .eePGjB{
    background-color: rgba(0,0,0,0.8);
}
.s1r1zaa-2:after,
.bkQNFA,
#overlayScrollContainer:after,
.s1c2oq5o-2:after,
.s3n19jy-0:after,
.s1tblowa-2:after,
.kaaRvo:after,
.kujlWd::after,
.eePGjB::after{
    background: rgba(0,0,0,0) !important;
}
.eePGjB {
        -webkit-animation: fadein 1s;
        -moz-animation: fadein 1s;
        -ms-animation: fadein 1s;
        -o-animation: fadein 1s;
        animation: fadein 1s;
    }
@supports (-webkit-backdrop-filter: none) or (backdrop-filter: none) {
    .eePGjB {
        -webkit-backdrop-filter: blur(3px);
        backdrop-filter: blur(3px);
    }
}
@keyframes fadein {
    from { opacity: 0; }
    to   { opacity: 1; }
}

/* Firefox < 16 */
@-moz-keyframes fadein {
    from { opacity: 0; }
    to   { opacity: 1; }
}

/* Safari, Chrome and Opera > 12.1 */
@-webkit-keyframes fadein {
    from { opacity: 0; }
    to   { opacity: 1; }
}

/* Internet Explorer */
@-ms-keyframes fadein {
    from { opacity: 0; }
    to   { opacity: 1; }
}

/* Opera < 12.1 */
@-o-keyframes fadein {
    from { opacity: 0; }
    to   { opacity: 1; }
}
/*[[font]]*/
}
