css:
@-moz-document domain("forums.fusionfalluniverse.com")
{
  .post img
  {
    max-width: 100%;
  }


/*dark theme*/


/* use dark grey for the text, leaving #000 for headers etc */
body
{
	color: #e6e6e6;
}

a:link, a:visited
{
	color: #09f;
}

td, th, tr
{
	color: #e6e6e6;
}

/* for any search boxes
input, button, select, textarea
{
	color: #fb00ff;
	background: #39ff00;
	border: 1px solid #8400ff;
}
 */

.windowbg, #preview_body
{
	color: #e6e6e6;
	background-color: #001b30;
}

.windowbg2
{
	color: #e6e6e6;
	background-color: #000b14;
}

/* Sticky topics get a different background */
.stickybg
{
	background: #002440;
}
.stickybg2
{
	background: #003259;
}

/* Locked posts get a different shade, too! */
.lockedbg
{
	background: #303030;
	font-style: italic;
}
.lockedbg2
{
	background: #474747;
	font-style: italic;
}

table.table_grid td
{
	border-bottom: 1px solid #001b30;
	border-right: 1px solid #001b30;
}

/* Styles for the board index.
------------------------------------------------- */

/* the board title! */
.table_list tbody.content td.info a.subject
{
	color: #09f;
}

.table_list tbody.content td.children
{
	color: #b0b0b0;
	font-size: 85%;
}
}
