css:
@-moz-document url-prefix("https://packagist.org/") {
body,
section.wrapper {
    background-color: #212121;
}

.form-control,
.nav-tabs li a,
#search-form input[type="search"] {
    background: #777777;
    color: #fff;
}

text.nv-legend-text,
.form-control::placeholder,
.form-control::-webkit-input-placeholder {
    color: #fff;
}

#search-container em {
    background: rgba(0, 0, 0, 0.16);
    box-shadow: 0px 0px 2px rgb(179, 78, 34);
}

.search-facets-tags .sbx-sffv__wrapper svg {
    fill: #f28d1a;
}

.search-facets-tags .sbx-sffv__wrapper input::placeholder,
.search-facets-tags .sbx-sffv__wrapper input::-webkit-input-placeholder,
.search-facets-tags .sbx-sffv__wrapper input {
    color: black;
}

g.tick text,
g.nv-axisMaxMin,
g.nv-axisMaxMin-x,
g.nv-axisMin-x,
text.nv-legend-text {
    fill: #fff;
}

.markdown-body .highlight pre,
.markdown-body pre,
pre,
code {
    background-color: black;
    color: gray;
}

.wrapper .content h2 a,
.wrapper .content h2.title,
.wrapper .content h3.title,
.wrapper .container {
    color: #aaaaaa;
}

.wrapper-search a {
    color: #B34E22;
}

.packages .package-item {
    background: #2f2f2f;
    color: #777777;
}

a:hover,
.package .package-aside a:hover {
    color: #f1682c;
}

.package .package-aside {
    background: transparent;
    border: 1px solid #f28d1a;
    border-radius: 5px;
}

.package i,
.package .version-details .version-number {
    color: inherit;
}

.markdown-body .pl-s,
.markdown-body .pl-pds,
.markdown-body .pl-s .pl-pse .pl-s1,
.markdown-body .pl-sr,
.markdown-body .pl-sr .pl-cce,
.markdown-body .pl-sr .pl-sre,
.markdown-body .pl-sr .pl-sra {
    color: #2057ff;
}

.markdown-body .pl-smi,
.markdown-body .pl-s .pl-s1 {
    color: #bdbdbd;
}
}
