css:
@-moz-document regexp("chrome-extension://.*"), regexp("moz-extension://.*") {
  #stylus body,
  #notes,
  #stylus input,
  #stylus select,
  #stylus-popup > #installed
  {
    background-color: #111 !important
  }

  #header,
  #help-popup,
  .CodeMirror-hints,
  .blocked-info,
  #stylus > #stylus-popup,
  #hotkey-info div,
  #installed div:hover,
  #message-box > div
  {
    background-color: #222 !important
  }

  #hotkey-info div:last-child,
  #confirm > div
  {
    background-color: #333 !important
  }

  #confirm > div
  {
    border-color: black
  }

  #message-box-title
  {
    background-color: #1B4E4E
  }

  #help-popup .title
  {
    background-color: rgba(255, 255, 255, .1)
  }

  #message-box-buttons
  {
    background-color: rgba(255, 255, 255, .05)
  }

  #stylus a:hover,
  #stylus .style-name:hover .style-name-link,
  #confirm > div
  {
    color: #FFF !important
  }

  #stylus .style-name .style-name-link
  {
    transition: initial !important
  }

  #stylus,
  #stylus body,
  #stylus input,
  #stylus select,
  #stylus a
  {
    color: #CCC !important
  }

  .Codemirror-hint
  {
    color: #CCC
  }

  #confirm
  {
    color: #000
  }

  #header, .header
  {
    border-color: #555
  }

  #hotkey-info div
  {
    border: none
  }

  #stylus input, #stylus select
  {
    border: 1px inset #666 !important;
  }

/*[[corners]]*/

  #stylus select
  {
    padding-top: 1px
  }

/*[[buttons]]*/

  .svg-icon:hover,
  a:hover .svg-icon
  {
    fill: #FFF !important
  }

  .newUI .entry:hover .svg-icon
  {
    fill: #999
  }

  .newUI .entry .svg-icon,
  .svg-icon.select-arrow,
  .svg-icon.settings,
  .svg-icon.info,
  .svg-icon,
  #update-history > .svg-icon,
  #sections-help
  {
    fill: #888
  }

  #stylus #usercss-wiki > .svg-icon
  {
    fill: #666
  }

  #stylus body
  {
    max-width: unset
  }

  .CodeMirror
  {
    border: inset #555 1px;
    outline: none;
  }

  .resize-grip
  {
    border-color: #AAA
  }
}
