@-moz-document domain("play.google.com") {

/*[[theme]]*/

/* Selection */
::-moz-selection { color: var(--bg) !important; background: var(--fg-link) !important; text-shadow: none }
::selection { color: var(--bg) !important; background: var(--fg-link) !important; text-shadow: none }
/* Scrollbars */
::-moz-scrollbar{background-color:var(--bg-2) !important;width:10px !important}
::-moz-scrollbar-track{background-color:var(--bg-2) !important}
::-moz-scrollbar-button{display:none !important}
::-moz-scrollbar-thumb{background-color:var(--fg-link) !important;border-radius:4px !important;border:none !important }
::-moz-scrollbar-thumb:hover{background-color:var(--fg-link-hover) !important}
::-webkit-scrollbar{background-color:var(--bg-2) !important;width:10px !important}
::-webkit-scrollbar-track{background-color:var(--bg-2) !important}
::-webkit-scrollbar-button{display:none !important}
::-webkit-scrollbar-thumb{background-color:var(--fg-link) !important;border-radius:4px !important;border:none !important }
::-webkit-scrollbar-thumb:hover{background-color:var(--fg-link-hover) !important}

body, paper-toolbar, iron-overlay-backdrop {
  background-color: var(--bg) !important }
header, #mainPanel, #player, .fEjTrb, .paper-drawer-panel-1 iron-selector.paper-drawer-panel > #drawer.paper-drawer-panel, .gb_Of, .action-bar, .card.apps .cover, .card .cover, .card-content, .card .reason-set, .nav-container, .nav-container .shared-sub-nav, .t5eBue, .LXrl4c, .k8Lt0 .CoKTif, .W4P4ne, .uMConb, .buPxGf, .N9c7d, .main-content, .details-section, .gb_Uf, .my-account-fop-row {
  background-color: var(--bg-2) !important }
oddrow, #suggestionList, .material-card, .goog-menu, .goog-menuitem, sj-mini-queue, .top-tracks, .songlist-container, .gb_ia, .gV6pJf, paper-dialog, sj-search-box, sj-search-box[opened], .gb_fa, .gb_Fb, .gpm-vertical-list-0 #items.gpm-vertical-list, .sj-card-0[card-aspect-ratio="wide"], .k8Lt0 .Knqxbd, .menu-link[style*="background-color: rgb(255, 255, 255)"], .websky-modal-dialog, .LNKfBf, .LVQB0b, .dropdown-submenu, .ac-renderer {
  background-color: var(--bg-odd) !important }
lighterbg, #loading-overlay[data-type="full-loading-overlay"], .websky-modal-dialog-overlay, .JvjCjf, .action-bar-spacer {
  background-color: rgba(255, 255, 255, 0.1) !important }
div, tr, transparent, .entity-card.material-card .image-wrapper, body.qp .fade-out.gray::after, .song-row td, .entity-card.material-card, .gb_Z:hover .gb_3, .card .price-container .price, .sIIDsc, .TAviAb, .footer, #footer-content {
  background-color: transparent !important }
input, select, textarea, sj-play-button, sj-search-box #input.sj-search-box::-moz-placeholder, .song-row:hover td, paper-progress #progressContainer.paper-progress, .nav-item-container:hover, .nav-item-container:focus, .gpm-vertical-list-0 #items.gpm-vertical-list > [focused], .gbqfqw, .KZnDLd:hover, .GL7Kod:hover, .child-submenu-link-wrapper:hover, .parent-submenu-link-wrapper:hover, .ac-active, .track-list-row:hover td {
  background-color: var(--bg-input) !important;
  color: var(--fg-input) !important }
input:focus, select:focus, textarea:focus, .song-row.selected-song-row td, #player:hover #material-player-progress:not([disabled]) #progressContainer, .song-row.currently-playing td, .nav-item-container.selected,.gpm-vertical-list-0 #items.gpm-vertical-list > :hover, .gpm-detail-page-header-0[description-overflows] #descriptionWrapper.gpm-detail-page-header:hover, .gbqfqw:focus-inner {
  background-color: var(--bg-input-hover) !important;
  color: var(--fg-input-hover) !important }
highlighted, sj-entity-suggestion:hover, sj-search-suggestion:hover, .goog-menuitem-highlight, paper-fab, .paper-button-2, .paper-button-2, sj-play-button #buttonContent.sj-play-button, paper-progress #primaryProgress.paper-progress, paper-slider .slider-knob-inner.paper-slider, #playlist-drawer .playlist-drawer-item .playlist-wrapper:focus, #playlist-drawer .playlist-drawer-item sj-play-button:hover ~ .playlist-wrapper, .show-all-hover-zone, .k8Lt0 .znPNx {
  background-color: var(--fg-link) !important; color: var(--bg) !important; text-shadow: none }
.paper-button-2:hover, sj-play-button #buttonContent.sj-play-button:hover, #playlist-drawer .playlist-drawer-item .playlist-wrapper:hover {
  background-color: var(--fg-link-hover) !important; color: var(--bg) !important; text-shadow: none }
nobg {
  background-image: none !important }
.song-row.selected-song-row td, .song-row.currently-playing td {
  background: var(--bg-input-hover) !important;
  color: var(--fg-input-hover) !important }

.sj-entity-suggestion-0:hover div, .sj-entity-suggestion-0:hover #subtitle, .sj-search-suggestion-0:hover div, .goog-menuitem-highlight div.goog-menuitem-content, #playlist-drawer .playlist-drawer-item .playlist-wrapper:hover .playlist-title, #playlist-drawer .playlist-drawer-item .playlist-wrapper:focus .playlist-title {
  color: var(--bg) !important }
body, paper-toolbar, paper-toolbar div, paper-toolbar span, .goog-menuitem-content, .material-card .title, .material-empty .empty-message, #details.sj-card a[slot="title"], .song-row td, .song-table td a, gpm-detail-page-header div.gpm-detail-page-header > [slot="buttons"], .gMG7Fd, .wh73ab, paper-dialog, .playlist-name, .song-table.mini [data-col="song-details"] .song-title, a.nav-item-container:link, a.nav-item-container:visited, .Vz68Kb, span[style*="color: rgb(85, 85, 85);"], .BgcNfc, .iZhiic .pvVVcb, .EymY4b, .gb_Ig, .CodeMirror {
  color: var(--fg) !important }
#header, h1, h2, h3, h4, h5, .sj-search-suggestion-0 #queryText.sj-search-suggestion .sub-match.sj-search-suggestion, .goog-menuheader, .material-card .sub-title, sj-home[selected="0"] .home-palette-id-1, h2.gpm-headline-header > .home-palette-id-1, .gpm-headline-header-0 h2.gpm-headline-header, #details.sj-card a[slot="subtitle"], .top-charts-view:not([style-scope]):not(.style-scope) .song-row:not([style-scope]):not(.style-scope) [data-col="index"]:not([style-scope]):not(.style-scope) .column-content:not([style-scope]):not(.style-scope), .song-table td a.song-artist, .BHMmbe, .redeem-title {
  color: var(--fg-2) !important }
.muted, paper-toolbar #subtitle, .goog-menu .goog-menuitem .goog-menuheader-disabled, gpm-quick-nav-item a.gpm-quick-nav-item, .material-empty .empty-submessage, #details.sj-card span[slot="description"], .song-table th, .song-table td[data-col="duration"], gpm-detail-page-header div.gpm-detail-page-header > [slot="metadata"], .player-album, #player-artist, #material-player-right-wrapper .player-top-right-items .time-label, #time_container_duration, #time_container_current, .gpm-detail-page-header-0 div.gpm-detail-page-header > [slot="subtitle"], .gpm-detail-page-header-0 div.gpm-detail-page-header > [slot="description"], .song-table td a.song-album, .sIIDsc, .footer, .card .subtitle, .jdjqLd, .dNLKff, .info-container .document-subtitle, .secondary-sub-nav-option .sub-nav-link {
  color: var(--fg-muted) !important }

.song-table td[data-col="rating"] li, .my-account-credit-card-image {
  filter: invert(100%) }

a, a:link, paper-tab.iron-selected div, paper-dialog:not([style-scope]):not(.style-scope) .buttons:not([style-scope]):not(.style-scope) paper-button:not([style-scope]):not(.style-scope):not([disabled]), a:visited > .playlist-title, .primary:not([style-scope]):not(.style-scope), .drrice .n9lfJ .snByac {
  color: var(--fg-link) !important }
a:hover, a:visited:hover, paper-tab:hover div, paper-button:hover, gpm-quick-nav-item a.gpm-quick-nav-item:hover, #details.sj-card a:hover, gpm-detail-page-header div.gpm-detail-page-header > [slot="buttons"]:hover, paper-dialog:not([style-scope]):not(.style-scope) .buttons:not([style-scope]):not(.style-scope) paper-button:not([style-scope]):not(.style-scope):not([disabled]):hover, .primary:not([style-scope]):not(.style-scope):hover {
  color: var(--fg-link-hover) !important }
a:visited {
  color: var(--fg-link-visited) !important }

#player iron-icon svg, .play-button-container iron-icon svg, iron-icon[icon="av:play-arrow"] svg, .RFF2xb svg, .ajgU6, .sj-page-controls[aria-selected="true"] > iron-icon {
  fill: var(--fg-link) !important }
#player iron-icon:hover svg, .RFF2xb:hover svg, .nav-item-container:hover iron-icon, .sj-page-controls[aria-selected="false"]:hover > iron-icon {
  fill: var(--fg-link-hover) !important }
.menu-container iron-icon svg, .iWO9fc .mUbCce svg {
  fill: var(--fg) !important }
.XSVuue {
  fill: var(--fg-2) !important }
gpm-quick-nav iron-icon, .nav-item-container iron-icon, .sj-page-controls[aria-selected="false"] > iron-icon {
  fill: var(--fg-muted) !important }
#player #player-bar-play-pause iron-icon svg path.iron-icon:nth-child(2) {
  fill: var(--bg) !important }

noborder, paper-header-panel#content-container:not([style-scope]):not(.style-scope):not(.transparent)[at-top] paper-toolbar#material-app-bar:not([style-scope]):not(.style-scope), sj-search-box #input, .more-songs-container, .gb_fa, #gbqfq {
  border: none !important }
noborder {
  border-color: transparent !important }
custom-border, input, select, textarea, pre, code {
  border: var(--border) !important }
bordercolor, h1, h2, h3, h4, h5, hr, #playlist-drawer:not([style-scope]):not(.style-scope) paper-header-panel[at-top]:not([style-scope]):not(.style-scope) paper-toolbar:not([style-scope]):not(.style-scope) {
  border-color: var(--border-color) !important }
lighterborder, sj-search-box sj-entity-suggestion.sj-search-box:last-of-type, .now-playing-menu.goog-menu, #playlist-drawer .autoplaylist-section, #playlist-drawer #recent-playlists-container, .new-playlist-drawer .nav-section-divider, .nav-section-divider, .nav-container .shared-sub-nav, .k8Lt0 .CoKTif, .Njo8s, .YcEAid, .LNKfBf, .submenu-divider, .dropdown-submenu, .ac-renderer, .details-section-divider {
  border-color: rgba(255, 255, 255, 0.2) !important }
paper-slider .slider-knob-inner.paper-slider, .gb_Z:hover a, [aria-checked="true"] > .hh4xKf, .action-bar-link.active {
  border-color: var(--fg-link) !important }
[aria-checked="true"] .E7QdY {
  border-color: var(--fg-link-hover) !important }
#queue-overlay::after {
  border-color: var(--bg-odd) !important }
.gb_qb {
  border-bottom-color: var(--bg-odd) !important }
paper-tabs #selectionBar.paper-tabs {
  border-bottom-color: var(--fg-link) !important }
.song-table.tight .song-row td {
  border-bottom-color: #fff1 !important }
.dropdown-menu .divider, hr, .goog-menu .goog-menuseparator {
  background-color: var(--border-color) !important }

noshadow, sj-search-box sj-entity-suggestion.sj-search-box:last-of-type {
  box-shadow: none !important }
baseshadow {
  box-shadow: 0 0 5px #000 !important }
.material-card.material-shadow-z1:hover, .material-card.material-shadow-z1.focused, .entity-card.material-card:hover .material-shadow-z1, .entity-card.material-card.focused .material-shadow-z1 {
  box-shadow: 0 0 5px 2px var(--fg-link) !important }

body {
  text-shadow: 0.3px 0.3px 0.5px rgba(0, 0, 0, 0.5) !important }

success, success a {
  background-color: var(--bg-success) !important;
  color: var(--fg-success) !important }
info, info a, .alert-info {
  background-color: var(--bg-info) !important;
  color: var(--fg-info) !important }
warning, warning a, .alert-warning {
  background-color: var(--bg-warning) !important;
  color: var(--fg-warning) !important }
error, error a, .alert-danger {
  background-color: var(--bg-error) !important;
  color: var(--fg-error) !important }
disabled, input[readonly] {
  background-color: var(--bg-disabled) !important;
  color: var(--fg-disabled) !important }

.btn, .buttons > paper-icon-button, paper-button[data-id="more-songs"], .gb_ja, .gb_Ea, #nav_collections paper-button.nav-item-container.sub, .play-button, .LkLjZd {
  color: var(--fg-input) !important;
  background-image: linear-gradient(var(--bg-input-hover), var(--bg-input)) !important;
  border: solid 0.5px var(--border-color) !important;
  text-shadow: 0.3px 0.3px 0.5px #000 !important }

.buttons > paper-icon-button:hover, paper-button[data-id="more-songs"]:hover, .gb_ja:hover, .gb_Ea:hover, #nav_collections paper-button.nav-item-container.sub:hover {
  border-color: rgba(255, 255, 255, 0.2) !important }

.btn.active, .buttons > paper-icon-button:active, paper-button[data-id="more-songs"]:focus, .gb_ja:focus, .gb_Ea:focus, #nav_collections paper-button.nav-item-container.sub:focus {
  background-image: linear-gradient(var(--bg-input), var(--bg-input-hover)) !important }

.btn:focus {
  background-color: var(--bg-input) !important;
  border-color: var(--border-color) !important;
  color: var(--fg-input-hover) !important }

pre, code {
  background-color: var(--syntax-bg) !important }
pre, code, .prettyprint, .nocode, .pln { color: var(--syntax-fg) !important }
.kwd, .tag, .pun, .linenums .tag { color: var(--syntax-builtin) !important }
.atn { color: var(--syntax-keyword) !important }
.dec, .typ { color: var(--syntax-function) !important }
.atv, .str { color: var(--syntax-string) !important }
.num, .lit { color: var(--syntax-number) !important }
.com { color: var(--syntax-comment) !important }

.fade-out::after, .fade-out.gray::after {
  background-image: linear-gradient(to right,#fff0,var(--bg-odd)) }
.paragraph-end, .cqtbn {
  background-image: linear-gradient(to right,#fff0,var(--bg-2)) }
.uwAgLc {
  background-image: linear-gradient(#fff0, var(--bg-2)) }

.music-logo-link, .gb_dc, img[src="https://play-music.gstatic.com/fe/0063f8c589bba123938b7beb46002059/default_album.svg"], .gb_Va {
  filter: invert(100%) hue-rotate(180deg) !important }
#searchIcon {
  filter: brightness(150%) }

.song-indicator {
  filter: invert(100%);
  background-color: transparent !important;
  z-index: 5 !important }
.currently-playing .aria-play-button:after {
  background-color: var(--bg-input-hover) !important;
  content: "";
  width: 25px;
  height: 25px;
  top: 5px;
  position: absolute;
  z-index: 1 !important;
}

paper-toolbar.medium-tall {
  max-height: 64px !important }

#bottomBar {
  top: 64px !important;
  background-image: linear-gradient(var(--bg), 80%, transparent) !important }
paper-toolbar, paper-toolbar > #topBar.toolbar-tools.paper-toolbar {
  height: 64px !important }
#bottomBar > .material-header-bar, #header-tabs-container, paper-toolbar .toolbar-tools.paper-toolbar, paper-tabs #tabsContent.paper-tabs, paper-tabs.tab-container {
  height: 32px !important }
#material-app-bar #countSummary {
  line-height: 32px !important }

/* Hide ad */
#header-upsell-container, #dropShadow {
  display: none !important;
  height: 0 !important;
  max-height: 0 !important }

/* Header buttons hidden except on hover */
#material-one-left, #material-one-right {
  opacity: 0; transition: opacity 0.2s ease-in-out }
#material-one-left:hover, #material-one-right:hover {
  opacity: 1 }

.play-button.apps, .play-button.music, .play-button.movies {
  color: #fff !important }
.my-account-add-fop {
  color: #3f3 !important }

}
