css:
@-moz-document regexp(".*anilist.co.*") {
/*General Adjustments*/
::-webkit-scrollbar {
	width: 10px!important;
	height: 7px!important;
}
::-webkit-scrollbar-thumb {
	cursor: pointer!important;
	background: grey!important;
}
.site-theme-dark::-webkit-scrollbar-thumb{
	background: rgba(var(--color-foreground-grey-dark))!important;
}
.text ::-webkit-scrollbar, .about ::-webkit-scrollbar, .activity-text ::-webkit-scrollbar, .input.el-textarea ::-webkit-scrollbar{
	width: 0px!important;
}
.container {
	padding-left: 0px;
	padding-right: 0px;
}
#app > div.wrap > div {
	background: rgba(var(--color-foreground-grey-dark));
}
.emoji-spinner {
	display: none!important;
}
.list .details {
	padding: 10px 100px 10px 10px!important;
}
body {
	font-family: Segoe UI !important;
}
div,
.el-input.is-focus, .el-input__inner:focus, .el-textarea__inner:focus, .input-shadow .el-input__inner{
	box-shadow: none!important;
}
.markdown .markdown-spoiler.spoiler-visible i.hide-spoiler{
	color: rgba(var(--color-blue), .9)!important;
	border-radius: 3px 0 3px 0;
	top: -5px;
}
.markdown a, .markdown a:visited{
	transition: .2s;
}
.markdown a:hover, .markdown a:visited:hover{
	text-shadow: 0px 0px 2px;
	transition: .2s;
}
.reply{
	line-height: 18px!important;
}
.shadow{
	display: none!important;
}
.status .title:hover, .name:hover {
	text-shadow: 0px 0px 2px;
}
.status .title, .name{
	font-weight: 500!important;
}
.like-wrap .users {
    overflow-y: hidden!important;
}
.markdown .markdown-spoiler.spoiler-visible .hide-spoiler, .markdown .markdown-spoiler.spoiler-visible span {
    width: 10%!important;
}
.rules-notice{
	color: rgba(var(--color-blue-dim));
	font-size: 1rem!important;
}

/*Dropdown menus are site theme based*/
.el-select-dropdown,
.el-dropdown-menu,
.el-dropdown-menu__item--divided::before {
	background: rgba(var(--color-foreground-blue-dark))!important;
}
.el-select-dropdown__item.hover,
.el-select-dropdown__item:hover {
	background: rgba(var(--color-foreground-grey-light));
}
.el-dropdown-menu__item--divided {
	border-color: rgba(var(--color-background));
}
.el-select-group__wrap:not(:last-of-type)::after {
	background: rgba(var(--color-foreground));
}
.el-popper[x-placement^="bottom"] .popper__arrow,
.el-popper[x-placement^="bottom"] .popper__arrow::after {
	border-bottom-color: transparent;
}
.el-popper[x-placement^="top"] .popper__arrow,
.el-popper[x-placement^="top"] .popper__arrow::after {
	border-top-color: transparent;
}
.el-select-dropdown.is-multiple .el-select-dropdown__item.selected,
.el-select-dropdown.is-multiple .el-select-dropdown__item.selected.hover{
	background: rgba(var(--color-foreground-grey-light));
}
#nav > div.wrap > div.user > div > div.dropdown,
#nav > div.wrap > div.links > span > div{
	box-shadow: none!important;
}
/*User dropdown*/
#nav > div.wrap > div.user > div > div.dropdown,
#nav > div.wrap > div.links > span > div{
	background: rgba(var(--color-foreground-grey-dark));
}
#nav > div.wrap > div.user > div > div.dropdown:before,
.browse-wrap .dropdown:before{
	color: rgba(var(--color-foreground-grey-dark));
}
#nav > div.wrap > div.user > div > div.dropdown > div.footer,
#nav > div.wrap > div.links > span > div > div.footer{
	background: rgba(var(--color-foreground-grey));
}

/*Banners don't need shadows*/
.header-wrap .banner {
	margin-top: 0px;
}
.shadow {
	display: none;
}

/*Texteditor*/
.markdown-editor,
.el-textarea__inner {
	border-radius: 0;
}
.markdown-editor {
	margin-bottom: 2px;
}
.markdown-editor > div > svg {
	min-width: 1em!important;
}
.markdown-editor > [title="Image"],
.markdown-editor > [title="Youtube Video"],
.markdown-editor > [title="WebM Video"]  {
	color: rgba(var(--color-blue), .6);
}
.markdown-editor > [title="Youtube Video"]:hover {
	color: rgb(255, 0, 0);
}
.activity-entry .details .status:first-letter{
	text-transform: lowercase;
}

/*Donator Badge alignment*/
.details > .donator-badge {
	left: 105px!important;
	padding:3px 5px 3px 5px!important;
	top: 100%!important;
    background: rgba(var(--color-foreground-blue));
	-ms-transform: translate(0px, -34px);
	/* IE 9 */
	-webkit-transform: translate(0px, -34px);
	/* Safari */
	transform: translate(0px, -34px);
	font-size: 1rem!important;
}
.activity-text > div > div > div > .donator-badge {
	position: relative!important;
	display: inline-block!important;
	left: 0px!important;
	top: 2px!important;
	-ms-transform: translate(0px, 0px);
	/* IE 9 */
	-webkit-transform: translate(0px, 0px);
	/* Safari */
	transform: translate(0px, 0px);
}
.site-theme-dark .details > .donator-badge {
	color: #fff;
}

/*Keeps long custom list names in the anime/manga editor in container(only works with hoh's script -> https://greasyfork.org/en/scripts/370473-aniscripts)*/
.list-editor-wrap .list-editor .body .checkbox .el-checkbox__label{
	word-break: normal;
}

/*Buttons*/
div.button.save {
	color: rgba(var(--color-text));
	background: rgba(var(--color-foreground));
	transition: .2s;
	margin: -5px 0 0 0;
	border-style: solid;
	border-radius: 3px;
	border-width: 1px!important;
	border-color: rgba(var(--color-text),.3);
}
div.save.button:hover {
	background: rgba(var(--color-foreground-blue));
	transition: .2s;
	margin-top: -5px;
	box-shadow: none;
}
.home .button.cancel,
.user .button.cancel,
.activity-entry .button.cancel,
.forum-thread .button.cancel{
	color: rgba(var(--color-text))!important;
	transition: .2s;
	margin: -5px 10px 0 0;
	border-style: solid;
	border-radius: 3px;
	border-width: 1px!important;
	border-color: rgba(var(--color-text),.3);
}
#app > div.page-content > div > div.content.container > div > div:nth-child(2) > div.activity-feed-wrap > div.activity-edit > div.actions > div.el-tooltip.button.save{
	margin-left: 10px;
}
.home .button.cancel:hover,
.user .button.cancel:hover,
.activity-entry .button.cancel:hover,
.forum-thread .button.cancel:hover{
	background: rgba(var(--color-foreground-blue));
	transition: .2s;
	margin: -5px 10px 0 0;
	box-shadow: none;
}
div.like-wrap:hover {
	color: rgb(232, 93, 117)!important;
	transition: .2s;
}
.replies > .count {
	color: rgb(61, 180, 242)!important;
}
.replies > *:nth-child(2) {
	color: rgb(61, 180, 242)!important;
}

/*Anime/manga editor*/
.list-editor-wrap .list-editor{
	margin-top: 5vh!important;
}
#app > div.el-dialog__wrapper.list-editor-wrap > div > div.el-dialog__body > div.body{
	padding-top: 40px;
}
#app > div.el-dialog__wrapper.list-editor-wrap > div > div.el-dialog__body > div.header{
	height: 140px!important;
}
.list-editor-wrap .list-editor .body .input-wrap{
	row-gap: 0px;
}
.list-editor-wrap .list-editor .body .checkbox{
	margin-bottom: 4px;
}
.list-editor-wrap .list-editor .body .custom-lists{
	margin-bottom: 4px;
}
body > div.el-picker-panel.el-date-picker.el-popper,
.el-date-picker__header-label{
	background: rgba(var(--color-background));
	border: none;
	color: rgba(var(--color-text));
}
body > div:nth-child(n) th{
	color: rgba(var(--color-text));
}
[type=reset], [type=submit], button, html [type=button]{
	color: rgba(var(--color-text));
}
body > div:nth-child(n) > div.popper__arrow{
	display: none;
}
.el-date-table td.next-month, .el-date-table td.prev-month {
    color: #606266;
}
}

@-moz-document regexp(".*anilist.co.*") {
/*Color Customize Setting*/

/*[[colortheme]]*/
}

@-moz-document regexp(".*anilist.co.*") {
/*Global Customize Setting*/

/*[[navbarmod]]*/

/*[[navbaricons]]*/

/*[[hohcorrect]]*/
}

@-moz-document regexp(".*anilist.co/home"), regexp(".*anilist.co/social") {
/*Home Adjustments*/
.list-preview {
	grid-gap: 10px!important;
	padding: 0px!important;
	background: rgba(0, 0, 0, 0)!important;
}
.small .cover{
	border-radius: 3px!important;
}
.home {
	grid-column-gap: 30px!important;
	grid-template-columns: auto 465px!important;
}
.search {
	margin-top: 0px!important;
}
.icon {
	padding: 0px !important;
}
.wrap {
	overflow: visible!important;
}
#app > div.page-content > div > div > div.activity-feed-wrap > div.activity-feed > div:nth-child(n) > div > div.list > a{
	border-radius: 3px 0 0 3px;
}
.media-preview {
	grid-gap: 10px!important;
	padding: 0px!important;
	background: rgba(0, 0, 0, 0)!important;
}
.media-preview-card {
	display: inline-grid!important;
}
.home .activity-feed-wrap h2.section-header {
	transition: .2s;
}
.section-header {
    padding-left: 0px!important;
}
#app > div.page-content > div > div > div.activity-feed-wrap > div.activity-edit > div.input.el-textarea{
	margin-bottom: 10px!important;
}
.input.el-textarea,
.activity-entry.full-width.activity-anime_list,
.activity-entry.full-width.activity-manga_list,
.activity-entry.full-width.activity-text{
	margin-bottom: 0px!important;
}
.list-preview .media-preview-card .content,
.media-preview .media-preview-card .content{
	background: rgba(var(--color-foreground), .9)!important;
}
#app > div.page-content > div > div > div:nth-child(2) > div.recent-reviews > div > a:nth-child(n) > div.content{
	padding-bottom: 20px;
}

/*Feed type Flollowing/Global*/
.feed-type-toggle{
	margin-left: 10px;
	background: transparent!important;
}
#app > div.page-content > div > div > div.activity-feed-wrap > h2 > div > div.feed-type-toggle > div:nth-child(n){
	background: rgba(var(--color-foreground));
	margin-left: -3px;
}
#app > div.page-content > div > div > div.activity-feed-wrap > h2 > div > div.feed-type-toggle > div.active{
	background-color: rgba(var(--color-foreground-grey-dark));
}

/*Like and comment buttons*/
div.like-wrap:hover {
	color: rgb(232, 93, 117);
	transition: .2s;
}
.replies > .count {
	color: rgb(61, 180, 242);
}
.replies > *:nth-child(2) {
	color: rgb(61, 180, 242);
}

/*All links are blue*/
#app > div.page-content > div > div > div:nth-child(2) > div.list-previews > div:nth-child(1) > div.section-header > h2 {
	color: rgba(var(--color-blue));
}
#app > div.page-content > div > div > div:nth-child(n) > div.list-previews > div:nth-child(n) > div.section-header > h2:hover {
	text-shadow: 0px 0px 2px!important;
}
h2.section-header.link {
	color: rgba(var(--color-blue));
}
h2.section-header.link:hover {
	text-shadow: 0px 0px 2px!important;
}
.home .activity-feed-wrap h2.section-header{
	padding-top: 10px;
}
#app > div.page-content > div > div > div.activity-feed-wrap > h2{
	padding-top: 0px;
}
#app > div.page-content > div > div > div:nth-child(2) > div.list-previews > div:nth-child(2) > div.section-header > h2:hover,
#app > div.page-content > div > div > div:nth-child(2) > div.list-previews > div:nth-child(3) > div.section-header > h2:hover{
	text-shadow: none!important;
}

/*Activities are seperated by color*/
.activity-text .text {
	border-left: solid 5px rgb(61, 180, 242);
	border-radius: 3px;
}
.activity-manga_list > div > div > div > div > .title {
	color: rgb(232, 93, 117)!important;
}

/*Activity distribution*/
.activity-entry .status {
	display: inline-block!important;
	line-height: 1.6rem;
	padding-right: 115px;
}
.avatar{
	display: block!important;
}
.reply {
	margin-bottom: 2px!important;
	border-radius: 0px!important;
}
.header a:nth-child(1) {
	display: inline-block!important
}
.list {
	min-height: 80px!important;
	grid-template-columns: 60px auto!important;
}
.list .name{
	padding-bottom: 0!important;
}
.activity-replies {
	margin-top: 2px!important;
}
.reply-wrap .activity-replies .activity-edit .markdown-editor {
	border-radius: 0;
	margin: 0px 0px 2px 0px;
}
.activity-text .markdown {
	overflow-wrap: break-word;
	word-break: normal;
}
.reply-wrap .activity-replies .activity-edit .actions {
	margin: 10px 0 -5px 0;
}
.input.el-textarea{
	margin-bottom: 10px;
}
.activity-entry{
	margin-bottom:10px!important;
}
.text {
    padding: 15px 10px 20px 10px!important;
}
}

@-moz-document regexp(".*anilist.co/home") {
/*Home Customize Settings*/

/*[[feedfilter]]*/
}

@-moz-document regexp(".*anilist.co/user/.*") {
/*User Adjustments*/
.header-wrap .banner .name {
	background: rgba(0,0,0,.5);
	border-radius: 16px;
	padding: 6px 15px;
	margin: 15px 5px 15px 25px;
	height: 29px;
	text-shadow: none;
}
.user .overview {
	grid-template-columns: 465px auto!important;
}
.section-header {
    padding-left: 0px!important;
}
.user-page-unscoped.custom-color .progress .bar:after{
	background: rgba(var(--color-blue))!important;
}
.el-slider__button-wrapper{
	z-index: 0;
}

/*Nav bar on user profiles*/
#app > div.page-content > div > div.header-wrap{
	position: sticky!important;
	top: -330px!important;
	z-index: 100;
}

/*Mod badge*/
#app > div.page-content > div > div.header-wrap > div.banner > div.container > div > div.mod-badge {
	margin-bottom: 19px!important;
	cursor: default;
	height: 20px;
	border-radius: 10px;
}

/*Follow/Donator*/
.nav-btn{
	height: 35px;
}

/*Genre overview from Kuwabara*/
.genre .name{
    background: rgba(var(--color-foreground-grey))!important;
    border-radius: 2px;
    font-size: 1.3rem;
}
.genre .amount{
	font-size: 1.2rem;
	font-weight: 500;
}
.status:nth-of-type(1)  .name {
    color: rgb(104, 214, 57);
}
.status:nth-of-type(2)  .name {
    color: rgb(2, 169, 255);
}
.status:nth-of-type(3)  .name {
    color: rgb(146, 86, 243);
}
.status:nth-of-type(4)  .name {
    color: rgb(247, 121, 164);
}
.status:nth-of-type(5)  .name {
    color: rgb(232, 93, 117);
}
.genre:nth-of-type(1)  .name {
    color: rgb(104, 214, 57);
}
.genre:nth-of-type(2)  .name {
    color: rgb(2, 169, 255);
}
.genre:nth-of-type(3)  .name {
    color: rgb(146, 86, 243);
}
.genre:nth-of-type(4)  .name {
    color: rgb(247, 121, 164);
}
.genre:nth-of-type(5)  .name {
    color: rgb(232, 93, 117);
}

/*wae's additions (affects genre display on profiles)*/
.genres,
.statuses{
	grid-template-columns: repeat(auto-fill, auto)!important;
	grid-gap: 3px;
}
.genre .name,
.status .name{
	padding: 6px 8px!important;
}
.overview .genre-overview .genre:last-child,
.overview .statuses .status:last-child{
	display: unset!important;
}
.percentage-bar{
	height: 3px;
}

/*Small stats overview*/
.stats-wrap .milestones {
    font-size: 1rem;
    padding: 5px 0;
}
.stats-wrap .progress {
    height: 3px;
	border-radius: 0; 
}
.stats-wrap .milestones .milestone:after {
    padding-top: 2px;
}
.stat:not(:first-of-type) {
    border-left: 1px solid rgba(var(--color-foreground-grey));
}
#app > div.page-content > div > div.content.container > div > div:nth-child(n) > div.stats-wrap > div:nth-child(n) > div.stats-wrap{
	background: rgba(var(--color-foreground-grey-dark));
	padding-top: 5px;
	padding-bottom: 5px;
}
#app > div.page-content > div > div.content.container > div > div:nth-child(2) > div.stats-wrap > div:nth-child(n){
	border: none!important;
}

/*Activities look slimmer*/
.activity-entry:nth-child(1){
	padding-top: 10px;
} 
.activity-message .text {
	border-left: solid 5px rgba(61, 180, 242);
}
.activity-text .text {
	border-left: solid 5px rgba(var(--color-blue));
}
.activity-text .status {
	position: absolute;
	display: inline-block!important;
	margin-left: 4px;
	margin-right: 90px;
	overflow: hidden;
	line-height: 1.3em;
}
.reply {
	padding: 10px!important;
	margin-bottom: 2px!important;
	border-radius: 0px!important;
	line-height: 1.3em;
}
.list {
	min-height: 80px!important;
	grid-template-columns: 60px auto!important;
}
.activity-replies {
	margin-top: 2px!important;
	margin-left: 30px!important;
	margin-right: 30px!important;
}
.activity-entry {
	margin-bottom: 10px!important;
}
.reply-wrap .activity-replies {
	margin: 4px 20px 15px 20px;
}
.reply-wrap .activity-replies .reply {
	border-radius: 0;
	margin-bottom: 2px;
	padding: 9px;
	padding-bottom: 1px;
}
.reply-wrap .activity-replies .reply p {
	margin-top: 10px;
	margin-bottom: 8px;
}
.reply-wrap .activity-replies .activity-edit .markdown-editor {
	border-radius: 0;
	margin: 0 0 2px 0;
}
.reply-wrap .activity-replies .activity-edit .input {
	border-radius: 0;
	margin-bottom: 0;
}
.reply-wrap .activity-replies .activity-edit h2 {
	margin-top: 10px;
}
.reply-wrap .activity-replies .activity-edit .preview {
	border-radius: 5px;
}
.reply-wrap .activity-replies .activity-edit .actions {
	margin: 10px 0 -5px 0!important;
}
.activity-entry {
	margin-bottom: 10px!important;
}
.input.el-textarea{
	margin-bottom: 10px;
}
#app > div.page-content > div > div.content.container > div.overview > div:nth-child(2) > div.activity-feed-wrap > div.activity-edit > div.markdown-editor{
	margin-top: 20px!important;
}
.body-preview{
    line-height: 18px;
}
#app > div.wrap > div{
	margin-top: -60px!important;
}
#app > div.page-content > div > div.content.container{
	padding-bottom: 60px!important;
}

/*Nav bar on user pages*/
.site-theme-dark #app > div.page-content > div > div.header-wrap > div.nav-wrap{
	background: rgba(var(--color-foreground-grey-dark));
}
.nav.container a.link{
	font-size: 1.2rem;
	padding: 9.5px;
	height: 30px;
	margin: 0 20px;
}
#app{
	overflow:unset!important;
}

/*Favorites 5-width*/
.favourites-wrap{
	padding: 0px!important;
	display: grid!important;
    grid-gap: 10px!important;
    column-gap: 10px!important;
    grid-template-columns: repeat(auto-fill,85px)!important;
    grid-template-rows: repeat(auto-fill,115px)!important;
    background: transparent!important;
	margin-left: -5px!important;
}
.favourite.studio{
	display: grid!important;
    grid-template-columns: repeat(auto-fill,85px)!important;
    grid-template-rows: repeat(auto-fill,115px)!important;
	text-align: center!important;
	align-items: center!important;
}
.overview .favourites > .favourites-wrap > div, .overview .favourites > .favourites-wrap > a {
	margin-bottom: 0px!important;
	margin-right: 0px!important;
}

/*Favorite Studios site theme based*/
.favourite.studio {
	background: rgba(var(--color-foreground))!important;
	color: rgba(var(--color-text))!important;
}
.favourite.studio:hover {
	background: rgba(var(--color-blue))!important;
	color: rgba(var(--color-text))!important;
}
.site-theme-dark .favourite.studio:hover {
	background: rgba(var(--color-blue))!important;
	color: rgba(0, 0, 0)!important;
}
.site-theme-dark .favourite.studio {
	background: rgba(var(--color-foreground))!important;
}
}

@-moz-document regexp(".*anilist.co/user/.*") {
/*User Customize Settings*/

/*[[feedfilterup]]*/
}

@-moz-document regexp(".*anilist.co/user/.*/(animelist|mangalist).*") {
/*Anime/Manga list modification*/
.content.container {
	max-width: 1240px;
}
.section-header {
	margin-left: 0px;
}
.filters-wrap {
	width: 200px!important;
}
.draggable > span,
.filter-group > span {
	background: rgba(var(--color-foreground));
}
.filters.mobile-nav-hidden .active,
.filters.mobile-nav-hidden .active:hover {
	color: rgb(255, 255, 255)!important;
	font-weight: 500!important;
	background: rgba(var(--color-blue))!important;
}
.filter-group .count {
	position: absolute!important;
	font-size: 1.1rem !important;
    padding: 0 0 0 3px !important;
    transition: none !important;
}
.medialist .filters .filter-group:first-child > span:hover {
	background: rgba(var(--color-blue), .3);
	color: rgba(250, 250, 250);
	transition: .2s;
}
.draggable > span, .filter-group > span{
	padding: 5px 25px 5px 10px!important;
}

/*Mediacards*/
.medialist.cards .section-name,
#app > div.page-content > div > div.content.container > div > div.lists > div:nth-child(n) > h3 {
	padding-left: 0;
}
#app > div.page-content > div > div.content.container > div.medialist.compact.table.POINT_100 > div.filters-wrap > div.filters.mobile-nav-hidden > div:nth-child(n) > span:nth-child(n){
	word-break: break-word!important;
}
