	border-radius: 3px;
	z-index: 1;
}
.hohComments.link {
	margin-top: -25px;
}
.hohComments.link:hover {
	color: rgba(var(--color-blue));
}
.hohUnread {
	border-right: 5px;
	border-color: rgba(var(--color-blue));
	border-right-style: solid;
}
.hohCommentsArea {
	width: 860px!important;
	padding-top: 10px;
	padding-bottom: 0px;
}
.hohTime {
	color: rgba(var(--color-text-lighter));
	font-size: 1.1rem!important;
	margin-top: 3px!important;
}
.hohQuickCom{
	word-break: break-all!important;
}
.hohQuickComContent {
	font-size: 1.4rem!important;
}
.hohQuickComLikes {
	font-size: 1.3rem!important;
}
#app > div.page-content > div > div.filters > span{
	-webkit-touch-callout: none;
	/*iOS Safari*/
	-webkit-user-select: none;
	/*Safari*/
	-khtml-user-select: none;
	/*Konqueror HTML*/
	-moz-user-select: none;
	/*Firefox*/
	-ms-user-select: none;
	/*Internet Explorer/Edge*/
	user-select: none;
	/*Non-prefixed version, currently supported by Chrome and Opera*/
}

/*hohtagLess sign*/
.tagLessManga::after,
.tagLessAnime::after,
a.tagLess.hohDouble::after{
	padding: 1px;
	font-size: 1.15rem;
	line-height: 12px;
}

/*hohStaffRoles*/
#app > div.page-content > div > div:nth-child(n) > select,
#app > div.page-content > div > div.media-roles.container > div:nth-child(n) > input,
#app > div.page-content > div > div:nth-child(n) > input{
	background: rgba(var(--color-foreground))!important;
	color: rgba(var(--color-text));
	padding: 6px;
	border: none;
	border-radius: 3px!important;
	outline-width: 0;
	cursor: default;
}
#app > div.page-content > div > div.container.hohFilterBar > span:nth-child(6){
	background: rgba(var(--color-foreground));
	float: right;
	padding: 5px;
	border-radius: 3px;
	font-size: 1.3rem;
}
#statusList{
	position: relative!important;
}
.hohStatusDot.hohStatusDotRight{
	height: 15px;
	width: 15px;
	border-radius: 3px;
	top: 0;
	right: 0;
}

/*hohTags on anime/mangalists*/
#hohDraw3x3{
	background: rgba(var(--color-blue));
	padding: 10px 28.5px;
	border-radius: 4px;
	color: white;
	transition: .2s;
	font-size: 1.3rem;
}
#app > div.page-content > div > div.content.container > div > div.filters-wrap > div.hohExtraFilters > div, #app > div.page-content > div > div.content.container > div.medialist.cards.POINT_100 > div.filters-wrap > div.hohExtraFilters > div{
	margin-top: 20px;
}
#app > div.page-content > div > div.content.container > div > div.filters-wrap > div.hohExtraFilters > div:before, #app > div.page-content > div > div.content.container > div.medialist.cards.POINT_100 > div.filters-wrap > div.hohExtraFilters > div:before{
	content: "Alphabetical";
	font-size: 1.3rem;
	color: rgba(var(--color-text-lighter));
}
#Erwin > div.filters-wrap > div.hohExtraFilters > div > span:nth-child(2):before{
	content: "Most entries";
	font-size: 1.3rem;
	color: rgba(var(--color-text-lighter));
}
#app > div.page-content > div > div.content.container > div > div.filters-wrap > div.hohExtraFilters > div > p:nth-child(n), #app > div.page-content > div > div.content.container > div.medialist.cards.POINT_100 > div.filters-wrap > div.hohExtraFilters > div > p:nth-child(n){
	background: rgba(var(--color-foreground))!important;
	padding: 5px 15px 5px 10px;
	font-size: 1.4rem;
	border-radius: 4px;
	margin-bottom: -5px!important;
	color: rgba(var(--color-text-lighter));
	transition: .2s;
}
#app > div.page-content > div > div.content.container > div > div.filters-wrap > div.hohExtraFilters > div > p:nth-child(n):hover, #app > div.page-content > div > div.content.container > div.medialist.cards.POINT_100 > div.filters-wrap > div.hohExtraFilters > div > p:nth-child(n):hover{
	background: rgba(var(--color-blue), .3)!important;
	transition: .2s;
}

/*hohActivityTimeline*/
#activityTimeline > div:nth-child(n){
	margin: 0 0 5px 0 !important;
	background: rgba(var(--color-foreground));
	padding: 10px!important;
	font-size: 1.5rem;
	border-radius: 3px;
}
#activityTimeline > div:nth-child(n) > span{
	right: 10px!important;
}
#app > div.page-content > div > div.content.container > div.social > div:nth-child(n) > div:nth-child(n) > div:nth-child(n) > input{
		background: rgba(var(--color-foreground))!important;
	color: rgba(var(--color-text));
	border: none;
	outline-width: 0;
	cursor: default;
	padding: 6px;
	border-radius: 4px 0 0  4px ;
}
#app > div.page-content > div > div.content.container > div.social > div:nth-child(n) > div:nth-child(n) > div:nth-child(n) > button{
	align-items: center!important;
    background: rgba(var(--color-foreground));
    border-radius: 0 4px 4px 0;
	font-size: 15px;
    padding: 7px 10px;
	border-left: solid 2px rgba(var(--color-background))!important;
	outline-width: 0;
}
#app > div.page-content > div > div.content.container > div.social > div:nth-child(n) > div:nth-child(n) > div:nth-child(n) > button:hover{
	background: #11161d;
}

/*hohRecommendations from MAL*/
#app > div.page-content > div > div.content.container > div.overview > div:nth-child(n) > div.grid-section-wrap > div:nth-child(n){
	background: rgba(var(--color-foreground));
	padding: 10px;
	margin-bottom: 10px;
	border-radius: 3px;
}
#app > div.page-content > div > div.content.container > div.overview > div:nth-child(n) > div.grid-section-wrap > div:nth-child(n) .title{
	left: 70px!important;
}

.hohChangeScore{
	font-family: Segoe UI !important;
}

.hohThemeSwitch > span{
	cursor: pointer;
}
.hohThemeSwitch .active{
	background: transparent!important;
    color: rgba(var(--color-blue))!important;
}
.hohThemeSwitch .active:hover{
	background: transparent!important;
    color: rgba(var(--color-blue))!important;
}

div.hohMediaScore, span.hohMediaScore{
	background: rgba(var(--color-foreground))!important;
	border-radius: 3px;
	font-weight: 500;
	padding:2px 2px 2px 15px!important;
	color: inherit;
}
#hohEntryScore > div > span:nth-child(1){
	padding: 4px 2px 2px 6px!important;
}
#hohEntryScore > span > span.hohChangeScore{
	position: absolute;
	top: 0px!important;
}
span.hohMediaScore{
	padding-right: 15px!important;
}
div.hohMediaScore{
	margin-left: 0px;
	width: 100%;
}

/*hohExtraTitleInfo*/
.hohExtraBox{
	padding: 0!important;
	font-size: 1.1rem;
	font-weight:600;
	color: rgba(129,140,153);
}
#app > div.page-content > div > div.header-wrap > div.header > div > div.content > div.hohExtraBox > p{
	margin-left: 0!important;
}
}

@-moz-document regexp(".*anilist.co/user/.*/stats.*") {
/*hohStats*/
#personalStats > h1, #personalStatsManga > h1{
	color: rgba(var(--color-blue))!important;
}
#personalStats, #personalStatsManga, #miscQueries{
	padding: 10px;
	margin-bottom: 10px;
	border-radius: 4px;
	background-color: rgba(var(--color-foreground));
}
hr{
	display: none;
}
#miscQueries > div:nth-child(3) > select{
	background: rgba(var(--color-background));
}
div.header.row{
	margin-top: 5px;
	margin-left: -10px;
	margin-right: -10px;
}
div.row{
	margin-left: -10px;
	margin-right: -10px;
}
#hohStats{
	font-size: 1.4rem;
}
#customTagsAnimeTable > p:nth-child(1),
#customTagsMangaTable > p:nth-child(1){
	background: rgba(var(--color-foreground-grey-dark));
	font-size: 1.7rem;
	font-weight: 500;
	margin-left: -10px;
	margin-right: -10px;
	padding: 10px;
}
#customTagsAnimeTable > div > div.header.row,
#customTagsMangaTable > div > div.header.row{
	margin-top: -25px;
	background: rgba(var(--color-foreground-grey-dark))
}
#customTagsAnimeTable > p:nth-child(n) > input,
#customTagsMangaTable > p:nth-child(n) > input{
	background: rgba(var(--color-background))!important;
	color: rgba(var(--color-text));
	padding: 6px;
	border: none;
	border-radius: 3px 0 0 3px;
	transition: .2s;
	outline-width: 0;
	margin-right: 1px;
}
#customTagsAnimeTable > p:nth-child(n) > button,
#customTagsMangaTable > p:nth-child(n) > button{
	background: rgba(var(--color-background))!important;
	color: rgba(var(--color-text));
	padding: 6px;
	border: none;
	border-radius: 0 3px 3px 0;
	transition: .2s;
	outline-width: 0;
	cursor: pointer;
}
.hohTable{
	width: 900px!important;
	padding-left: 0px;
	padding-right: 0px;
}
#customTagsAnimeTable > div > div:nth-child(4n),
.hohSecondaryRow:nth-child(2n){
	background: rgba(var(--color-foreground-grey-dark))!important;
}

.hohStatsTrigger{
	color: rgba(var(--color-text-lighter))!important;
	transition: .2s;
	width: 210px;
	font-size: 1.3rem;
}
.hohStatsTrigger:hover{
	color: rgba(var(--color-blue))!important;
}
.hohStatsTrigger.hohActive{
	font-weight: 600!important;
}
#app > div.page-content > div > div.content.container > div > div.filters > div.filter-wrap.mobile-nav-hidden > span:nth-child(3){
	margin-top: 40px;
}
}

@-moz-document regexp(".*anilist.co/settings/apps.*") {
/*hohSettings*/
#hohSettings{
	font-size: 1.4rem;
}
input, select{
	background: rgba(var(--color-background))!important;
	color: rgba(var(--color-text));
	padding: 6px;
	border: none;
	border-radius: 3px;
	transition: .2s;
	outline-width: 0;
	min-width: 7em;
}
#blockInput > button{
	background: rgba(var(--color-background))!important;
	color: rgba(var(--color-text));
	transition: .2s;
	outline-width: 0;
	padding-top: 5px;
	padding-bottom: 3px;
}
#hohSettings > div:nth-child(n) > p:nth-child(n) > h3,
#hohSettings > h1{
	color: rgba(var(--color-blue));
	font-weight: bold;
}
textarea{
	background: rgba(var(--color-background))!important;
	color: rgba(var(--color-text));
	border: none;
	outline-width: 0;
	cursor: default;
	padding: 10px 10px;
}
}

@-moz-document regexp(".*anilist.co/user/.*/(animelist|mangalist)/compare.*") {
/*hohCompare*/
#app > div.page-content > div > div.content.container > div.hohCompare > select,
#app > div.page-content > div > div.content.container > div.hohCompare > input[type=number]:nth-child(n){
	background: rgba(var(--color-foreground))!important;	
}
.hohCompare > select, input, button{
	background: rgba(var(--color-background))!important;
	color: rgba(var(--color-text));
	padding: 6px;
	border: none;
	border-radius: 3px;
	transition: .2s;
	outline-width: 0;
	font-size: 1.3rem;
}
.hohUserRow select, .hohUserRow input{
	background: rgba(var(--color-background))!important;
	color: rgba(var(--color-text));
	border-radius: 3px;
	outline-width: 0;
	font-size: 1.3rem;
}
#app > div.page-content > div > div.content.container > span{
	z-index: 0!important;
    background: rgba(var(--color-blue));
    border-radius: 4px;
    color: #fff;
    font-size: 1.2rem;
    padding: 8px 14px;
	cursor: pointer!important;
}
#app > div.page-content > div > div.content.container > span:hover{
    background: rgba(var(--color-blue),.8);
}
}
